import React, { useState, useEffect } from "react"
import { withTranslation } from "react-i18next"
import {
  Row,
  Col,
  Button,
  Card,
  CardBody
} from "reactstrap"

//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb"

import FormFilter from "./firmaContrato/FormFilter"
import SearchResults from "./firmaContrato/SearchResults"
import ApiServicesBandejaFirmaContrato from "../../../services/FirmaContrato/ApiServicesBandejaFirmaContrato"

const InboxPage = (props) => {

  
  const [showResults, setShowResults] = useState(false);
  const [dataList, setDataList] = useState([]);

  //buscamos las Propuestas de Credito
  function searchData(values) {
    console.log(values);
    const api = new ApiServicesBandejaFirmaContrato()
    api.getResultadosBandejaFirmaContrato(values)
      .then((data) => {
       
        console.log(data);
        setDataList(data);
        setShowResults(true);
        
      })
      .catch((error) => {
    
        console.error('Error fetching documents', error);
      });
 

  }

  return (
    <React.Fragment>

      <div className="page-content">

        <Breadcrumbs title={ props.t("CommercialCredit") } breadcrumbItem={ props.t("SignContract") } />
        
        <Card>
      <CardBody>
             <FormFilter onSubmit={searchData} />
            {showResults && (
              <SearchResults results={dataList} />
            )}

      </CardBody>
    </Card>

      </div>

    </React.Fragment>
  );

}


export default (withTranslation()(InboxPage));
