import PropTypes from 'prop-types';
import { formatCurrency, translationHelpers } from '../../../../helpers';
import {
  Table,
  Card,
  CardBody,
  Col,
} from "reactstrap"
import { BackendServices, CoreServices, BpmServices, } from "../../../../services";
import React, { useEffect, useState } from "react"
import { useTranslation } from 'react-i18next';
import { useLocation, useHistory } from 'react-router-dom';
import * as url from "../../../../helpers/url_helper"
import Currency from '../../../../helpers/currency';

const ExposicionCorportativa = (props) => {
  const location = useLocation();
  const history = useHistory();
  const [locationData, setLocationData] = useState(null);
  const coreServices = new CoreServices();
  const { t, i18n } = useTranslation();
  const [dataRows, setdataRows] = React.useState(null);
  const [dataRows2, setdataRows2] = React.useState(null);
  const [dataRows3, setdataRows3] = React.useState(null);
  const [ExposicionCorportativa, setExposicionCorportativa] = useState(null);
  const [ExposicionCorportativaFacilidades, setExposicionCorportativaFacilidades] = useState(null);
  const [customerNumberT24, setcustomerNumberT24] = React.useState(props.customerNumberT24);
  const currencyData = new Currency();

  const backendServices = new BackendServices();

  useEffect(() => {

    let dataSession;
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        dataSession = location.data;
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        dataSession = result;
      }
    }


    getCoporateExhibition(dataSession);

    loadFacilities();
  }, [props.activeTab == 2]);
  useEffect(() => {
    console.log("ExposicionCorportativa", ExposicionCorportativa);
    console.log("ExposicionCorportativaFacilidades", ExposicionCorportativaFacilidades);
    setdataRows3(<tr>
      <th scope="row" width="30%">{t("Total")}</th>
      <th scope="row" className="table-right">{formatCurrency('$', ExposicionCorportativa?.reduce((acu, crr) => (acu + crr.approved), 0) || 0)}</th>
      <th scope="row" className="table-right">{formatCurrency('$', ExposicionCorportativa?.reduce((acu, crr) => (acu + crr.currentBalance), 0) || 0 + ExposicionCorportativaFacilidades?.reduce((acu, crr) => (acu + crr.currentBalance), 0) || 0)}</th>
      <th scope="row" className="table-right">{formatCurrency('$', ExposicionCorportativa?.reduce((acu, crr) => (acu + crr.proposed), 0) || 0 + ExposicionCorportativaFacilidades?.reduce((acu, crr) => (acu + crr.proposed), 0) || 0)}</th>
    </tr>)
  }, [ExposicionCorportativa, ExposicionCorportativaFacilidades]);


  async function getCoporateExhibition(dataSession) {
    
    await backendServices.consultPrincipalDebtor(dataSession.transactionId)
      .then(async (data) => {
        if (data !== undefined) {

          var dataexp = await coreServices.getCorporateExhibition(data.customerNumberT24);
          console.log("getCoporateExhibition",dataexp);

          await backendServices.getExposicionCorporativaBD(dataSession.transactionId).then(async resp => {
            if (resp.length > 0) {
              setExposicionCorportativa(resp.length > 0 ? resp : null);
              setdataRows(resp.map((item, index) => (
                <tr key={'row-' + index}>
                  <td scope="row" width="30%">{item.description}</td>
                  <td scope="row" className="table-right">{formatCurrency('$', item.approved, 0)}</td>
                  <td scope="row" className="table-right">{formatCurrency('$', item.currentBalance, 0)}</td>
                  <td scope="row" className="table-right">{formatCurrency('$', item.proposed, 0)}</td>
                  <td scope="row" className="table-right">{formatCurrency('$', (item.approved - item.proposed), 0)}</td>
                </tr>
              )
              ));
            } else {
              // Consultamos T24 para guardar
              await coreServices.getCorporateExhibition(data.customerNumberT24).then(async resp2 => {
                console.log(resp2);
                if (resp2 != undefined) {
                  for (let i = 0; i < resp2.length; i++) {
                    let datoSent = {
                      "transactId": Number(dataSession.transactionId),
                      "accountNumber": data.name,
                      "t24ClientId": data.customerNumberT24,
                      "description": resp2[i].name,
                      "approved": resp2[i].approved,
                      "currentBalance": resp2[i].balance,
                      "proposed": resp2[i].proposal,
                      "variation": resp2[i].variation,
                      "ltv": 0,
                      "difference": 0
                    }
                    backendServices.saveExposicionCorporativaBD(datoSent).then(saveExpo => {

                    })
                  }
                }
                await backendServices.getExposicionCorporativaBD(dataSession.transactionId).then(async exposision => {
                  setExposicionCorportativa(exposision.length > 0 ? exposision : null);
                  setdataRows(exposision.map((item, index) => (
                    <tr key={'row-' + index}>
                      <td scope="row" width="30%">{item.description}</td>
                      <td scope="row" className="table-right">{formatCurrency('$', item.approved, 0)}</td>
                      <td scope="row" className="table-right">{formatCurrency('$', item.currentBalance, 0)}</td>
                      <td scope="row" className="table-right">{formatCurrency('$', item.proposed, 0)}</td>
                      <td scope="row" className="table-right">{formatCurrency('$', (item.approved - item.proposed), 0)}</td>
                    </tr>
                  )
                  ));
                })
              });
            }
          })
          backendServices.retrieveProposalType()
            .then((propuesta) => {
              backendServices.retrieveFacilityType()
                .then((facilidad) => {
                  backendServices.consultarFacilidades(props.dataGlobal.requestId).then(resp => {
                    console.log(resp);
                    let acu = 0;
                    let acumuJson = [];
                    for (let i = 0; i < resp.length; i++) {
                      acumuJson.push({
                        "approved": 0,
                        "currentBalance": resp[i].amount,
                        "proposed": 0
                      })

                    }
                    setExposicionCorportativaFacilidades(acumuJson);
                    console.log("ExposicionCorportativa", acumuJson);
                    setdataRows2(resp.map((data, index) => {
                      acu++;
                      return (
                        <tr key={acu}>
                          {/* <td>{data.debtor}</td> */}
                          <td>{facilidad.find(x => x.id === data.facilityTypeId)?.description}</td>
                          <td>$0</td>
                          <td>${currencyData.format(data.amount ?? 0)}</td>
                          <td>$0</td>
                        </tr>)
                    }
                    ));
                  });
                })
            })
        }
      });

  }

  function loadFacilities() {
    backendServices.retrieveFacilityType()
      .then((facilidad) => {
        backendServices.consultarFacilidades(props.dataGlobal.requestId).then(resp => {
          console.log(resp);
          if (resp?.filter(data => data.debtor != "  ").length > 0) {
          } else {

          }
        });
      })
  }
  return (
    <React.Fragment>
      <Card>
        <CardBody>
          <h5 className="card-title">{props.title}</h5>
          <p className="card-title-desc">
          </p>
          <Col md="12" className="table-responsive styled-table-div">
            <Table className="table table-striped table-hover styled-table table" >
              <thead>
                <tr>
                  <th className="table-right">{t("Type")}</th>
                  <th className="table-right">{t("ApprovedRisk")}</th>
                  <th className="table-right">{t("ProposedRisk")}</th>
                  <th className="table-right">{t("Variation")}</th>
                </tr>
              </thead>
              <tbody>
                {dataRows}
                {dataRows2}
                {dataRows3}

              </tbody>
            </Table>
          </Col>
        </CardBody>
      </Card>
    </React.Fragment >
  );
};
ExposicionCorportativa.propTypes = {
  title: PropTypes.string,
  customerNumberT24: PropTypes.string,
};
export default ExposicionCorportativa;
