import { useState } from 'react';
import PropTypes from 'prop-types';
import { translationHelpers } from '../../../helpers/translation-helper';

import {
  Table,
  Card,
  CardBody,
  Button
} from "reactstrap"

import { Link } from 'react-router-dom'
import ModalNewDebt from './ModalNewDebt';


const Debts = (props) => {

  const [displayModalNewDebt, setDisplayModalNewDebt] = useState(false);

  const [t, c] = translationHelpers('commercial_credit', 'common');

  const { items } = props;

  if (!items) {
    return null;
  }

  function toggleModalNewDebt() {
    setDisplayModalNewDebt(!displayModalNewDebt);
  }


  const dataRows = items.map((item, index) => (
    <tr key={'row-' + index}>
      <td>{ item.bank }</td>
      <td>{ item.purpose }</td>
      <td className="text-end">{ item.approvedAmount }</td>
      <td className="text-end">{ item.balanceLastYear }</td>
      <td className="text-end">{ item.balanceLast6Months }</td>
      <td className="text-end">{ item.balanceForecast }</td>
      <td className="text-end">{ item.rate ? item.rate.toFixed(2) + '%' : '' }</td>
      <td>{ item.surety }</td>
      { props.editable && (
        <td>
          <Link to="edit">{ c("Edit") }</Link>&nbsp;
          <Link to="delete">{ c("Delete") }</Link>
        </td>
      )}
    </tr>)
  );

  return (
    <>
      <Card>
        <CardBody>
          <h4 className="card-title clearfix">{ props.title }
            { props.editable && (
              <Button className="float-end" color="success" style={{ margin: '5px' }}
                onClick={ toggleModalNewDebt }>
                { c("New") }
              </Button>
            )}
          </h4>
          <p className="card-title-desc"></p>
          <div className="table-responsive mt-2">
            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{ t("Banks") }</th>
                  <th>{ t("Purpose") }</th>
                  <th className="text-end">{ t("Approved Amount") }</th>
                  <th className="text-end">{ t("Month Balance", { month: 'dic-2019'}) }</th>
                  <th className="text-end">{ t("Month Balance", { month: 'jun-2020'}) }</th>
                  <th className="text-end">{ t("Forecast") }</th>
                  <th className="text-end">{ t("Rate") }</th>
                  <th>{ t("Surety") }</th>
                  { props.editable && (
                    <th></th>
                  )}
                </tr>
              </thead>
              <tbody>
                {dataRows}
              </tbody>
            </Table>
          </div>
        </CardBody>
      </Card>
      <ModalNewDebt isOpen={ displayModalNewDebt } toggle={ toggleModalNewDebt } onSave={ props.addDebt } />
    </>
  );
};

Debts.propTypes = {
  title: PropTypes.string,
  items: PropTypes.array.isRequired,
  editable: PropTypes.bool,
  addDebt: PropTypes.func
};

Debts.defaultProps = {
  editable: false
};


export default Debts;
