                    <LoadingOverlay active={isActiveLoading} spinner text={t("Processinginformation")}>
                      <TabContent activeTab={activeTab} className="body">
                        <TabPane tabId={1}>
                          <DatosGenerales datosGenerales={selectedData.datosGenerales} ref={datosGeneralesRef} onSubmit={submitData} onOpenClientSelection={() => { toogleShowClient() }} selectedClient={selectedClient} />
                        </TabPane>
                        <TabPane tabId={2}>
                          <GeneralesEmpresa datosEmpresa={selectedData.datosEmpresa} ref={generalesEmpresaRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={3}>
                          <HistoriaEmpresa locationData={locationData} historiaEmpresa={selectedData.historiaEmpresa} ref={historiaEmpresaRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={4}>
                          <InformacionAccionista jsonAccionistas={selectedData.dataAccionistas} ref={informacionAccionistaRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={5}>
                          <EstructuraOrganizacionalEmpresa locationData={locationData} dataEstructuraOrganizacional={selectedData.dataEstructuraOrganizacional} ref={estructuraOrganizacionalEmpresaRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={6}>
                          <GobiernoCorporativo ref={gobiernoCorporativoRef} jsonCorporativo={selectedData.dataGobiernoCorporativo} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={7}>
                          <RelevoGeneracional dataRelevoGenrencial={selectedData.dataRelevoGenrencial} ref={relevoGeneracionalRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={8}>
                          <FlujoOperativo locationData={locationData} dataFlujoOperativo={selectedData.dataFlujoOperativo} ref={flujoOperativoRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={9}>
                          <EmpresasRelacionadas dataEmpresaRelacionada={selectedData.dataEmpresaRelacionada} ref={empresaRelacionadaRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={10}>
                          <InformacionClientes dataInformacionClientes={selectedData.dataInformacionClientes} ref={informacionClientesRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={11}>
                          <InformacionProveedores dataInformacionProveedores={selectedData.dataInformacionProveedores} ref={InformacionProveedoresRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={12}>
                          <PrincipalesCompetidores dataPrincipalesCompetidores={selectedData.dataPrincipalesCompetidores} ref={PrincipalesCompetidoresRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={13}>
                          <Proyecciones locationData={locationData} dataProyecciones={selectedData.dataProyecciones} ref={ProyeccionesRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={14}>
                          <RelacionesBancarias dataRelacionesBancarias={selectedData.dataRelacionesBancarias} ref={RelacionesBancariasRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={15}>
                          <MoviemientosCuentas dataMoviemientosCuentas={selectedData.dataMoviemientosCuentas} ref={MoviemientosCuentasRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={16}>
                          <Reciprocidad dataReciprocidad={selectedData.dataReciprocidad} ref={ReciprocidadRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={17}>
                          <FacilidadActivosFijos locationData={locationData} dataFacilidadActivosFijos={selectedData.dataFacilidadActivosFijos} ref={FacilidadActivosFijosRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={18}>
                          <AspectosAmbientales datosGenerales={selectedData.datosGenerales} locationData={locationData} dataAspectosAmbientales={selectedData.dataAspectosAmbientales} ref={AspectosAmbientalesRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={19}>
                          <InformacionGarante dataInformacionGarante={selectedData.dataInformacionGarante} ref={InformacionGaranteRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={20}>
                          <SegurosActualesEmpresa dataSegurosActualesEmpresa={selectedData.dataSegurosActualesEmpresa} ref={SegurosActualesEmpresaRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={21}>
                          <ArquitecturaEmpresarial locationData={locationData} dataArquitecturaEmpresarial={selectedData.dataArquitecturaEmpresarial} ref={ArquitecturaEmpresarialRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={22}>
                          <CuentasCobrar locationData={locationData} dataCuentasCobrar={selectedData.dataCuentasCobrar} ref={CuentasCobrarRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={23}>
                          <Capex locationData={locationData} dataCapex={selectedData.dataCapex} ref={CapexRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={24}>
                          <FlujoCaja locationData={locationData} dataFlujoCaja={selectedData.dataFlujoCaja} ref={FlujoCajaRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={25}>
                          <NegociosObtener dataNegociosObtener={selectedData.dataNegociosObtener} ref={NegociosObtenerRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={26}>
                          <MatrizCompetitiva dataMatrizCompetitiva={selectedData.dataMatrizCompetitiva} ref={MatrizCompetitivaRef} onSubmit={submitData} />
                        </TabPane>
                        <TabPane tabId={27}>
                          <RecomendacionesOtros dataRecomendacionesOtros={selectedData.dataRecomendacionesOtros} ref={RecomendacionesOtrosRef} onSubmit={submitData} />
                        </TabPane>
                      </TabContent>
                      <Row>
                        <Col md={12} style={{ textAlign: "right", marginTop: "10px" }}>

                          <Button color="info" type="button" style={{ margin: '5px' }} onClick={() => { toogleModalPreview() }}><i className="mdi mdi-eye mdi-12px"></i> {t("Preview")}</Button>

                          {activeTab > 1 && (
                            <Button color="success" type="button" style={{ margin: '5px' }} onClick={() => { toggleTab(activeTab - 1); }}><i className="mdi mdi-arrow-left-bold mid-12px"></i> {t("Previous")}</Button>
                          )
                          }

                          {activeTab < 27 && (
                            <Button color="success" type="button" style={{ margin: '5px' }} onClick={() => {
                              saveDataTabs(); setIsActiveLoading(true); setTimeout(() => {
                                saveDataTabs();
                              }, 1000);
                            }}>{t("Next")} <i className="mdi mdi-arrow-right-bold mid-12px"></i></Button>
                          )
                          }
                        </Col>
                      </Row>

                    </LoadingOverlay>
                  </Col>
                </Row>
              </div>
            </CardBody>

            <CardFooter>

              <Row>
                <Col md={3} style={{ textAlign: "left", marginTop: "10px" }}>
                  <Button color="danger" type="button" style={{ margin: '5px' }} onClick={() => { showModalEndProcess() }}><i className="mdi mdi-cancel mid-12px"></i> {t("Cancel")}</Button>
                </Col>
                <Col md={9} style={{ textAlign: "right", marginTop: "10px" }}>
                  <Button color="primary" type="button" style={{ margin: '5px' }} onClick={() => { onSaveProcess(OPTs.PROCESS_INFORMEGESTION) }}><i className="mdi mdi-content-save-outline mid-12px"></i> {t("Exit")}</Button>
                  <Button color="success" type="button" style={{ margin: '5px' }}
                    onClick={() => {
                      saveDataTabs(); setIsActiveLoading(true); setTimeout(() => {
                        saveDataTabs();
                        setTimeout(() => {
                          onSaveProcess(OPTs.PROCESS_PROPUESTACREDITO)
                        }, 1000);
                      }, 1000);
                    }}><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i> {t("Credit Proposal")}</Button>
                </Col>
              </Row>
            </CardFooter>
          </Card >
          <LogProcess />
          <ModalCloseOptions onSaveProcess={onSaveProcess} isOpen={displayModalCloseOptions} toggle={() => { toggleModalCloseOptions() }} />
          <ModalEndProcess onSaveEndProcess={onSaveEndProcess} isOpen={displayModalEndProcess} toggle={() => { showModalEndProcess(false) }} />
          <ModalPrevicualizarIGR dataIGR={selectedData} isOpen={ModalPreviewData} toggle={() => { toogleModalPreview() }} />
          {
            error_dlg ? (
              <SweetAlert
                error
                title={t("ErrorDialog")}
                onConfirm={() => {
                  seterror_dlg(false);
                }}
              >
                {error_msg}
              </SweetAlert>
            ) : null
          }
        </div >
        : null}
    </React.Fragment >
  )
}

export default FormularioIgr;
