import React, { useState } from "react"
import PropTypes from 'prop-types';
import { translationHelpers } from '../../../helpers/translation-helper';
import { BackendServices } from '../../../services';
import { Link, useLocation } from "react-router-dom"
import SweetAlert from "react-bootstrap-sweetalert"
import {
  Table,
  Card,
  CardBody,
  Button
} from "reactstrap"
import ModalAnalisisCredito from "../../../pages/CommercialCredit/6_AnalisisCredito/ModalAnalisisCredito";
const Balance = (props) => {
  const location = useLocation();
  const [dataLocation, setData] = useState(location.data);

  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")

  const [dataSet, setdataSet] = useState({
    "transactId": 0,
    "itemActive": "",
    "year1": 0,
    "year2": 0,
    "year3": 0,
    "year4":0,
    "presentYear": 0
  });
  const [dataDelete, setDataDelete] = useState([]);
  const [Type, setType] = useState(null);
  const [items, setitems] = useState(null);
  const api = new BackendServices()
  const [t, c] = translationHelpers('commercial_credit', 'common');
  const [dataRows, setdataRows] = useState(null);
  const [showModalAnalisisCredito, setshowModalAnalisisCredito] = useState(false);
  const [botonValidation, setbotonValidation] = useState(true);

  function loadData() {
    if (props.type == "BalanceActivo") {
      api.checkActiveBalanceAC(dataLocation.transactionId).then(resp => {
        if (resp != undefined) {
          setdataRows(resp.map((item, index) => (
            item.estado?
            <tr key={'row-' + index}>
              <td className="text-end">{item.activoitem}</td>
              <td className="text-end">{item.anioactual}</td>
              <td className="text-end">{item.anio1}</td>
              <td className="text-end">{item.anio2}</td>
              <td className="text-end">{item.anio3}</td>
              <td className="text-end">{item.anio4}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => {
                  editData(item, "E" + props.type)
                }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => {
                  DeleteData(item, "D" + props.type)
                }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr>:null))
          );
        } else {
          setdataRows(
            <tr key={1}>
              <td colSpan="6" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
            </tr>);
        }
      })
    }
    if (props.type == "BalancePasivo") {
      api.checkBalancePassiveAC(dataLocation.transactionId).then(resp => {
        if (resp != undefined) {
          setdataRows(resp.map((item, index) => (
            item.status?
            <tr key={'row-' + index}>
              <td className="text-end">{item.itemPassive}</td>
              <td className="text-end">{item.presentYear}</td>
              <td className="text-end">{item.year1}</td>
              <td className="text-end">{item.year2}</td>
              <td className="text-end">{item.year3}</td>
              <td className="text-end">{item.year4}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => {
                  editData({
                    "itemPassiveId": item.itemPassiveId,
                    "itemPassive": item.itemPassive,
                    "year1": item.year1,
                    "year2": item.year2,
                    "year3": item.year3,
                    "year4": item.year4,
                    "presentYear": item.presentYear
                  }, "E" + props.type)
                }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => {
                  DeleteData({
                    "itemPassiveId": item.itemPassiveId,
                    "itemPassive": item.itemPassive,
                    "year1": item.year1,
                    "year2": item.year2,
                    "year3": item.year3,
                    "year4": item.year4,
                    "presentYear": item.presentYear
                  }, "D" + props.type)
                }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr>:null))
          );
        } else {
          setdataRows(
            <tr key={1}>
              <td colSpan="6" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
            </tr>);
        }
      })
    }
    if (props.type == "OrigenAplicacionn") {
      api.queryStateOriginApplicationsAC(dataLocation.transactionId).then(resp => {
        if (resp != undefined) {
          setdataRows(resp.map((item, index) => (
            item.status?
            <tr key={'row-' + index}>
              <td className="text-end">{item.sourceApplicationItem}</td>
              <td className="text-end">{item.presentYear}</td>
              <td className="text-end">{item.year1}</td>
              <td className="text-end">{item.year2}</td>
              <td className="text-end">{item.year3}</td>
              <td className="text-end">{item.year4}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => { editData(item, "E" + props.type) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { DeleteData(item, "D" + props.type) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr>:null))
          );
        } else {
          setdataRows(
            <tr key={1}>
              <td colSpan="6" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
            </tr>);
        }
      })
    }
    if (props.type == "indicadores") {
      api.queryACIndicator(dataLocation.transactionId).then(resp => {
        if (resp != undefined) {
          setdataRows(resp.map((item, index) => (
            item.status?
            <tr key={'row-' + index}>
              <td className="text-end">{item.itemIndicators}</td>
              <td className="text-end">{item.presentYear}</td>
              <td className="text-end">{item.year1}</td>
              <td className="text-end">{item.year2}</td>
              <td className="text-end">{item.year3}</td>
              <td className="text-end">{item.year4}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => { editData(item, "E" + props.type) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { DeleteData(item, "D" + props.type) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr>:null))
          );
        } else {
          setdataRows(
            <tr key={1}>
              <td colSpan="6" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
            </tr>);
        }
      })
    }
  }

  React.useEffect(() => {
    // Read Api Servic e data 
    loadData()
    setType(Type)
  }, [props]);

  function DeleteData(data, typeData) {
    setType(typeData)
    setDataDelete(data)
    setconfirm_alert(true);
  }
  function editData(data, typeData) {
    setType(typeData)
    setdataSet(data)
    setbotonValidation(true);
    toogleModalAnalisisCredito()
  }
  function dataManagement(data) {
    data.transactId = Number(dataLocation.transactionId);
    if (Type == "BalanceActivo") {
      api.newActiveBalanceAC(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toogleModalAnalisisCredito();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (Type == "BalancePasivo") {
      data.itemPassive = data.itemActive;
      api.nuevoBalancePasivoAC(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toogleModalAnalisisCredito();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (Type == "OrigenAplicacionn") {
      data.sourceApplicationItem = data.itemActive;
      api.nuevoEstadoOrigenAplicacionesAC(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toogleModalAnalisisCredito();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (Type == "indicadores") {
      data.itemIndicators = data.itemActive;
      api.newACIndicator(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toogleModalAnalisisCredito();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (Type == "EBalanceActivo") {
      data.itemActiveId = dataSet.idactivoitem;
      data.status = true;
      api.updateActiveBalanceAC(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toogleModalAnalisisCredito();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (Type == "EBalancePasivo") {
      data.itemPassive = data.itemActive;
      data.itemPassiveId = dataSet.itemPassiveId;
      data.status = true;
      api.actualizarBalancePasivoAC(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toogleModalAnalisisCredito();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (Type == "EOrigenAplicacionn") {
      data.sourceApplicationItem = data.itemActive;
      data.sourceApplicationItemId = dataSet.sourceApplicationItemId;
      data.status = true;
      api.actualizarEstadoOrigenAplicacionesAC(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toogleModalAnalisisCredito();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (Type == "Eindicadores") {
      data.itemIndicatorsId = dataSet.itemIndicatorsId;
      data.itemIndicators = data.itemActive;
      data.status = true;
      api.actualizarIndicadorAC(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toogleModalAnalisisCredito();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
  }
  function toogleModalAnalisisCredito() {
    setshowModalAnalisisCredito(!showModalAnalisisCredito);
    removeBodyCss()
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }
  return (
    <Card>
      <CardBody>
        <h4 className="card-title clearfix">{props.title}</h4>
        <p className="card-title-desc"></p>
        <div className="table-responsive mt-2">
          <Table className="table mb-0">
            <thead className="table-light">
              <tr>
                <th>{t("Description")}</th>
                <th>{t("CurrentYear")}</th>
                <th className="text-end">1 {t("YearAgo")} </th>
                <th className="text-end">2 {t("YearAgo")}</th>
                <th className="text-end">3 {t("YearAgo")}</th>
                <th className="text-end">4 {t("YearAgo")}</th>
                <th><Link className="float-end" onClick={() => {
                  setdataSet({
                    "transactId": 1,
                    "itemActive": "",
                    "year1": 1,
                    "year2": 2,
                    "year3": 3,
                    "year4": 4,
                    "presentYear": 1.1
                  }); setType(props.type); toogleModalAnalisisCredito()
                }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
              </tr>
            </thead>
            <tbody>
              {dataRows}
            </tbody>
          </Table>
        </div>
      </CardBody>
      <ModalAnalisisCredito titulo={props.title} dataSet={dataSet} tipo={Type} dataManagement={dataManagement} botones={botonValidation} isOpen={showModalAnalisisCredito} toggle={() => { toogleModalAnalisisCredito() }} />
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            loadData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            loadData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            if (Type == "DBalanceActivo") {
              apiBack.eliminarBalanceActivoAC(dataLocation.transactionId, dataDelete.idactivoitem).then(resp => {
                if (resp.statusCode == "500" || undefined) {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setsuccessSave_dlg(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }
            if (Type == "DBalancePasivo") {
              apiBack.eliminarBalancePasivoAC(dataLocation.transactionId, dataDelete.itemPassiveId).then(resp => {
                if (resp.statusCode == "500" || undefined) {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setsuccessSave_dlg(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }
            if (Type == "DOrigenAplicacionn") {
              apiBack.eliminarEstadoOrigenAplicacionesAC(dataLocation.transactionId, dataDelete.sourceApplicationItemId).then(resp => {
                if (resp.statusCode == "500" || undefined) {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setsuccessSave_dlg(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }
            if (Type == "Dindicadores") {
              apiBack.eliminarIndicadorAC(dataLocation.transactionId, dataDelete.itemIndicatorsId).then(resp => {
                if (resp.statusCode == "500" || undefined) {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setsuccessSave_dlg(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
    </Card>
  );
};

Balance.propTypes = {
  title: PropTypes.string,
  headers: PropTypes.array.isRequired,
};


export default Balance;
