import React, { useEffect, useState } from "react"

import PropTypes from 'prop-types';
import { translationHelpers } from '../../helpers/translation-helper';

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  CardTitle,
  InputGroup,
  Table,
  CardFooter
} from "reactstrap"

import AvField from 'availity-reactstrap-validation/lib/AvField';
import AvForm from 'availity-reactstrap-validation/lib/AvForm';
import { Link } from "react-router-dom";
import ModalPolicy from "../Attachments/ModalPolicy";
import SweetAlert from "react-bootstrap-sweetalert"
import { BackendServices, CoreServices, BpmServices, } from "../../services";
import { useLocation, useHistory } from 'react-router-dom'


const CreditPolicyReview = (props) => {

  React.useEffect(() => {
    // Read Api Service data     
    LoadData();
  }, []);


  const [displayModal, setDisplayModal] = useState(false);
  const [actions, setActions] = useState('');
  const [SusscessDialog, setSusscessDialog] = useState(false);
  const [dataRows, setdataRows] = useState(null);
  const [DataSend, setDataSend] = useState(null);
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [dataLocation, setData] = useState(props.data);
  const [dataDelete, setDataDelete] = useState([]);
  const [error_dlg, seterror_dlg] = useState(false);

  const [t, c] = translationHelpers('commercial_credit', 'common');
  const location = useLocation()

  if (!props.policies) {
    return null;
  }

  function LoadData() {
    const backendServices = new BackendServices();
    backendServices.consultarPoliticaCredito(dataLocation.transactionId).then(resp => {
      setdataRows(resp.map((policy, index) => {
        return (
          <tr key={'row-' + policy.id + '-' + index}>
            <th>{policy.parameter}</th>
            <td>{policy.condition}</td>
            <td>
              {policy.comply ? 'Si' : 'No'}
            </td>
            <td>{policy.observations}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { editarSowActual(policy) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { eliminarSowActual(policy) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>

        )
      }
      ));
    });

  }

  // setdataRows(props.policies.map((policy) => {
  //   return policy.conditions.map((condition, index) => (
  //     <tr key={'row-' + policy.id + '-' + index}>
  //       <th>{index === 0 ? policy.parameter : ''}</th>
  //       <td>{condition.title}</td>
  //       <td>
  //         <AvField
  //           className="form-control"
  //           name={"comply[" + index + "]"}
  //           type="select"
  //           id={"comply-" + index}
  //           value="N/A">
  //           <option>{c("Yes")}</option>
  //           <option>{c("No")}</option>
  //           <option>{c("N/A")}</option>
  //         </AvField>
  //       </td>
  //       <td>{condition.observations[0]}</td>
  //     </tr>
  //   )
  //   )
  // }
  // ));

  function toggleModal() {
    setDisplayModal(!displayModal);
  }

  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    // props.onSubmit(values);
  }

  function DataUpdate(values) {
    let datos = { ...values, transactId: dataLocation.transactionId };
    const backendServices = new BackendServices();

    backendServices.actualizarPoliticaCredito(datos).then(resp => {
      if (resp.statusCode == "200") {
        setSusscessDialog(true);
      }
    });
  }

  function DataInsert(values) {
    let datos = { ...values, transactId: +dataLocation.transactionId, debtorId: props.debtorId };
    const backendServices = new BackendServices();

    backendServices.nuevoPoliticaCredito(datos).then(resp => {
      if (resp.statusCode == "200") {
        setSusscessDialog(true);
      }
    });
  }

  function editarSowActual(data) {
    toggleModal();
    setActions('edit');
    setDataSend(data);
  }

  function eliminarSowActual(data) {
    setDataDelete(data)
    setconfirm_alert(true);
  }

  function InsertShowCurrent() {
    setActions('new');
    setDataSend(null);
  }

  return (
    <Card>
      <CardBody>
        <div className="d-flex flex-row justify-content-between">
          <h4 className="card-title">{t("Credit Policy Review")}</h4>
          <Link className="float-end" onClick={() => { toggleModal(); InsertShowCurrent() }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i>{t("")}</Link>
        </div>
        <p className="card-title-desc"></p>
        <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
          <Row>
            <Col lg="12">
              <div className="table-responsive">
                <Table className="table mb-0">
                  <thead className="table-light">
                    <tr>
                      <th>{t("Parameters")}</th>
                      <th>{t("Conditions")}</th>
                      <th>{t("Comply")}</th>
                      <th>{t("Observations")}</th>
                      <th></th>
                    </tr>
                  </thead>
                  <tbody>
                    {dataRows}
                  </tbody>
                </Table>
              </div>
            </Col>
          </Row>
        </AvForm>
      </CardBody>
      {displayModal &&
        <ModalPolicy
          isOpen={displayModal}
          toggle={toggleModal}
          DataUpdate={DataUpdate}
          DataInsert={DataInsert}
          actions={actions}
          DataSend={DataSend}
        />}

      {
        SusscessDialog ? (
          <SweetAlert
            success
            title={t("SuccessDialog")}
            onConfirm={() => {
              setSusscessDialog(false)
              LoadData();
            }}
          >
            {t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null
      }

      {
        confirm_alert ? (
          <SweetAlert
            title={t("Areyousure")}
            warning
            showCancel
            confirmButtonText={t("Yesdeleteit")}
            confirmBtnBsStyle="success"
            cancelBtnBsStyle="danger"
            onConfirm={() => {
              const backendServices = new BackendServices();
              backendServices.eliminarPoliticaCredit(dataLocation.transactionId, dataDelete.debtorId, dataDelete.itemId).then(resp => {
                if (resp.statusCode == "500") {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setSusscessDialog(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }}
            onCancel={() => setconfirm_alert(false)}
          >
            {t("Youwontbeabletorevertthis")}
          </SweetAlert>
        ) : null
      }
    </Card>
  );
};

CreditPolicyReview.propTypes = {
  policies: PropTypes.array
};

export default CreditPolicyReview;
