:dataDelete.cashFlowInvoiceId}).then(function(resp){if(resp.statusCode=="500"){setconfirm_alert(false);seterror_dlg(true);}else{setconfirm_alert(false);initializeData(locationData);}}).catch(function(){setconfirm_alert(false);seterror_dlg(true);});}if(tipo=="DCOBRANZA"){apiBack.eliminateCashFlowCollections({transactId:dataDelete.transactId,cashFlowCollectiond:dataDelete.cashFlowCollectiond}).then(function(resp){if(resp.statusCode=="500"){setconfirm_alert(false);seterror_dlg(true);}else{setconfirm_alert(false);initializeData(locationData);}}).catch(function(){setconfirm_alert(false);seterror_dlg(true);});}if(tipo=="DEGRESO"){apiBack.eliminateCashFlowExpenses({transactId:dataDelete.transactId,cashFlowOutcomeId:dataDelete.cashFlowOutcomeId}).then(function(resp){if(resp.statusCode=="500"){setconfirm_alert(false);seterror_dlg(true);}else{setconfirm_alert(false);initializeData(locationData);}}).catch(function(){setconfirm_alert(false);seterror_dlg(true);});}if(tipo=="DCARGATRABAJO"){apiBack.deleteCashFlowWorkLoad({transactId:dataDelete.transactId,cashFlowWorkloadId:dataDelete.cashFlowWorkloadId}).then(function(resp){if(resp.statusCode=="500"){setconfirm_alert(false);seterror_dlg(true);}else{setconfirm_alert(false);initializeData(locationData);}}).catch(function(){setconfirm_alert(false);seterror_dlg(true);});}},onCancel:function onCancel(){return setconfirm_alert(false);},children:t("Youwontbeabletorevertthis")}):null]});});export default FlujoCaja;

