/*ReinforcedManagementReport = Lista de Informe Reforzado*/
import React, { useState } from "react";
import PropTypes from "prop-types";
import { useLocation, useHistory } from "react-router-dom";
import { Link } from "react-router-dom";

import { Card, CardBody, Col, Button, CardFooter } from "reactstrap";

//Import Breadcrumb
import Breadcrumbs from "../../../../components/Common/Breadcrumb";

//i18n
import { withTranslation } from "react-i18next";

import DatosGenerales from "./DatosGenerales";
import CheckList from "./CheckList";
import Documentacion from "./Documentacion";
import ApiServicesChecklistCentroArchivo from "../../../../services/CentroArchivo/ApiServicesChecklistCentroArchivo";
import ApiServicesChecklistDocLegalCentroArchivo from "../../../../services/CentroArchivo/ApiServicesChecklistDocLegalCentroArchivo";
import ModalCloseOptions from "./ModalCloseOptions";
import ModalAdvanceOptions from "./ModalAdvanceOptions";

const PantallaBusqueda = (props) => {
  const history = useHistory();
  const location = useLocation();
  const { selectedData } = location;
  const [dataList, setDataList] = useState([]);
  const [dataList2, setDataList2] = useState([]);
  const [displayModalCloseOptions, setDisplayModalCloseOptions] = useState(false);
  const [displayModalAdvanceOptions, setDisplayModalAdvanceOptions] = useState(false);

  React.useEffect(() => {
    // Read Api Service data
    searchData(null);
    searchData2(null);
  }, []);

  //serviceapi1
  function searchData(values) {
    console.log(values);
    const api = new ApiServicesChecklistCentroArchivo();
    api
      .getResultadoChecklistCentroArchivoModel(values)
      .then((data) => {
        console.log(data);
        setDataList(data);
      })
      .catch((error) => {
        console.error("Error fetching documents", error);
      });
  }


  function searchData2(values) {
    console.log(values);
    const api = new ApiServicesChecklistDocLegalCentroArchivo();
    api
      .getResultadoDetalleDocLegalCentroArchivoModel(values)
      .then((data) => {
        console.log(data);
        setDataList2(data);
      })
      .catch((error) => {
        console.error("Error fetching documents", error);
      });
  }

  //Modal Opciones al Salir
  function toggleModalCloseOptions() {
    setDisplayModalCloseOptions(!displayModalCloseOptions);
  }

  //Modal Opciones al Salir
  function toggleModalModalAdvanceOptions() {
    setDisplayModalAdvanceOptions(!displayModalAdvanceOptions);
  }

  //Salvando el Proceso y le pasamos una opcion: 1-Finalizar Proceso, 2- Salvarlo, 3- A Cumplimmineto, 4- Avanzar a IGR
  function onSaveProcess(option){
    switch(option){
      case 1: {

        break;
      }
      case 2: {

        break;
      }
      case 3: {

        break;
      }
      case 4: {

        break;
      }
    }

    history.push("/dashboard");
  }




  return (
    <React.Fragment>
      <div className="page-content">

      <Breadcrumbs title={props.t("CommercialCredit")} breadcrumbItem={props.t("ArchiveCenter")} />

        <Card>
          <CardBody>
            <DatosGenerales />
            <CheckList results={dataList} />
            <Documentacion results={dataList2} />
          </CardBody>
          <CardFooter style={{ textAlign: "right" }}>

               <Button color="dark" type="button" style={{ margin: '5px' }} onClick={() => { onSaveProcess(2); }}><i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i> {props.t("Exit")}</Button>
                  <Button color="success" type="button" style={{ margin: '5px' }} onClick={() => { toggleModalModalAdvanceOptions() }}><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i> {props.t("Continue")}</Button>
            {/*  <Link
              className="btn btn-dark"
              style={{ margin: "5px" }}
              to={{
                pathname: "/creditocomercial/centroarchivo",
                selectedData: undefined,
              }}
            >
              {props.t("Close")}
            </Link>

            <Button color="negative" type="button" style={{ margin: "5px" }}>
              {props.t("Refuse")}
            </Button>

            <Button color="success" type="button" style={{ margin: "5px" }}>
              {props.t("Save")}
            </Button>
            */}
          </CardFooter>
        </Card>
        {/*  <ModalCloseOptions onSaveProcess={ onSaveProcess } isOpen={ displayModalCloseOptions } toggle={ () => { toggleModalCloseOptions() } } />*/}

        <ModalAdvanceOptions onSaveProcess={ onSaveProcess } isOpen={ displayModalAdvanceOptions } toggle={ () => { toggleModalModalAdvanceOptions() } } />
      </div>
    </React.Fragment>
  );
};

PantallaBusqueda.propTypes = {
  selectedId: PropTypes.any,
  onClose: PropTypes.func.isRequired,
};

export default withTranslation()(PantallaBusqueda);
