import React, { useState } from "react"
import PropTypes from 'prop-types';
import { translationHelpers } from '../../../helpers/translation-helper';

import {
    Row,
    Col,
    Card,
    CardBody,
    Button,
    Label,
    Input,
    CardHeader,
    CardTitle,
    InputGroup,
    Table,
    CardFooter
} from "reactstrap"
import SweetAlert from "react-bootstrap-sweetalert"
import { Link, useLocation } from "react-router-dom"
import ModalNewDebt from './ModalNewDebt';
import ModalDeudasCortoPlazo from '../../../pages/CommercialCredit/4_InformeGestionReforzado/FormularioIGR/Secciones/ModalDeudasCortoPlazo';
import ModalDeudasLargoPlazo from '../../../pages/CommercialCredit/4_InformeGestionReforzado/FormularioIGR/Secciones/ModalDeudasLargoPlazo';
import { BackendServices, CoreServices } from '../../../services';
import { AvForm, AvField } from "availity-reactstrap-validation"
import "flatpickr/dist/themes/material_blue.css";
import LoadingOverlay from "react-loading-overlay";
import HeaderSections from "../../Common/HeaderSections";
import moment from "moment";

const estiloTotales = { backgroundColor: "lightgrey", color: "black" };

const DebtSL = (props) => {
    const api = new CoreServices()

    /**
     * All use state
     */
    const [isActiveLoading, setIsActiveLoading] = useState(false);
    const [termsDebts, setTermsDebts] = useState(null);
    const [termsDebtsTotal, setTermsDebtsTotal] = useState(null);
    const [toggleModalNewDebt, setToggleModalNewDebt] = useState(false);
    /**
     * End All use state
     */

    const [t, c, tr] = translationHelpers('commercial_credit', 'common', 'translation');

    React.useEffect(() => {
        getTermsDebt({ typeDebt: props.typeDebt, customerNumberT24: props.customerNumberT24 });
    }, []);

    function getTermsDebt({ typeDebt, customerNumberT24 }) {
        setIsActiveLoading(true);

        typeDebt === 'Short' && api.getShortLongTermDebts(customerNumberT24).then(resp => {
            setTermsDebtsTotal(setDebtTotal(resp[resp.length - 1]));
            resp.length = resp.length - 1;
            setTermsDebts(setDebt(resp));
            setIsActiveLoading(false);
        }).catch(err => {
            setIsActiveLoading(false);
        });

        typeDebt === 'Long' && api.getLongTermDebts(customerNumberT24).then(resp => {
            setTermsDebtsTotal(setDebtTotal(resp[resp.length - 1]));
            resp.length = resp.length - 1;
            setTermsDebts(setDebt(resp));
            setIsActiveLoading(false);
        }).catch(err => {
            setIsActiveLoading(false);
        });
    }

    function setDebt(debt) {
        return debt?.map((debt, key) => (
            <tr key={key}>
                <td>
                    {debt.name}
                </td>
                <td>
                    {debt.approvalRisk}
                </td>
                <td>
                    {debt.proposalRisk}
                </td>
                <td>
                    {debt.variationRisk}
                </td>
                <td>
                    {formatDate(debt.date, 'dd-mmm-yyyy')}
                </td>
                {/* <td style={{ textAlign: "right" }}>
                    <Button type="button" color="link" onClick={(resp) => { editDebt(debt) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                    <Button type="button" color="link" onClick={(resp) => { deleteDebt(debt) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
                </td> */}
            </tr>
        ))
    }

    function setDebtTotal(debt) {
        return (
            <tr className="table-info">
                <th>{t("Total").toLocaleUpperCase()}</th>
                <th>{debt.proposalRisk}</th>
                <th>{debt.approvalRisk}</th>
                <th>{debt.variationRisk}</th>
                <th>{formatDate(debt.date, 'dd-mmm-yyyy')}</th>
            </tr>
        )
    }

    function editDebt(debt) {

    }

    function deleteDebt(debt) {

    }

    function formatDate(date) {
        return moment(date).format("DD/MM/YYYY");
    }

    return (
        <>
            <HeaderSections title={props.title} t={t}></HeaderSections>
            {/* <p className="card-title-desc"></p> */}

            <Col md="12">
                <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>
                    <Table className="table mt-1" responsive>
                        <thead className="table-light">
                            <tr>
                                <th>{tr("Name")}</th>
                                <th>{tr("ProposedRisk")}</th>
                                <th>{tr("ApprovedRisk")}</th>
                                <th>{tr("Variation")}</th>
                                <th>{tr("Date")}</th>
                                <th style={{ textAlign: "right" }}></th>
                                <th>
                                    {props.editable && (<Link to="#" className="float-end" onClick={() => { toggleModalNewDebt() }}>
                                        <i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i>{t("")}
                                    </Link>)}
                                </th>
                            </tr>
                        </thead>
                        <tbody>
                            {termsDebts}
                            {termsDebts?.length > 0 && termsDebtsTotal}
                        </tbody>
                    </Table>
                </LoadingOverlay >

            </Col>
        </>
    );
};

DebtSL.propTypes = {
    title: PropTypes.string,
    // items: PropTypes.array.isRequired,
    editable: PropTypes.bool,
    addDebt: PropTypes.func
};

DebtSL.defaultProps = {
    editable: false
};


export default DebtSL;
