import { useState } from 'react'
import { useTranslation } from 'react-i18next'
import {
    Row,
    Col,
    Form,
    Container,
} from "react-bootstrap";
import Select from 'react-select'
import { AvField, AvForm } from 'availity-reactstrap-validation';
import { Label } from 'reactstrap';

const FormCommonFields = (props) => {
    // props
    const { onChange, form } = props

    // hooks
    const { t, i18n } = useTranslation();
    const [typePerson, setTypePerson] = useState({
        value: '1', label: 'Natural'
    })
    const [personTypeSelected, setPersonTypeSelected] = useState("")

    const options = [
        { value: '1', label: 'Natural' },
        { value: '2', label: 'Jurídica' }
    ]
    const optionsIdType = [
        { value: '1', label: 'RUC' },
        { value: '2', label: 'RIF' },
        { value: '3', label: 'Cédula' },
    ]

    const handleChangeTypePerson = (personSelected) => {
        setPersonTypeSelected(personSelected.value)
        console.log("Handle type person", personSelected.value);
    }

    return (
        <Container>
            <AvForm>
                <Row>
                    <Col md={6} lg={4}>
                        <div className="mb-3">
                            <Label htmlFor="personType">{t("PersonType")}</Label>
                            <Select
                                defaultValue={typePerson}
                                options={options}
                                name="personType"
                                onChange={handleChangeTypePerson}
                            />
                        </div>
                    </Col>
                    <Col md={6} lg={4}>
                        <div className="mb-3">
                            <Label htmlFor="idType">{t("IdType")}</Label>
                            <Select
                                id="idType"
                                name="idType"
                                value={form.name}
                                options={optionsIdType}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                    <Col md={12} lg={4}>
                        <div className="mb-3">
                            <Label htmlFor="idnumber">{t("IdNumber")}</Label>
                            <AvField
                                id="idnumber"
                                name="idnumber"
                                value={form.name}
                                validate={{ required: { value: true } }}
                                errorMessage={t("Required Field")}
                                options={optionsIdType}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                </Row>
            </AvForm>
        </Container>
    )
}

export default FormCommonFields
