import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';

import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
  InputGroup,
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"


const ModalCapex1 = (props) => {
  const { t, i18n } = useTranslation();
  // Submitimos formulario para busqueda de clientes
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    // {
    //   "transactId": 0,
    //   "description": "",
    //   "useInMiles": 0,
    //   "shareholder": "",
    //   "bank": ""
    // }
    if(props.tipo=="ECAPEX1"){
      values.capexId=props.dataCapex1.capexId;
      props.actualizarDatos(values,props.tipo);
    }else{
      props.guardarDatos(values,props.tipo);
    }
  }


  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}>
      <div className="modal-header">
        <h5 className="modal-title mt-0">{t("Capex")}</h5>
        <button
          type="button"
          onClick={props.toggle}
          data-dismiss="modal"
          className="close"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>

        <Row>
          <Col xl="12">
            <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
              <Card>
                <CardBody>
                  <Row>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="description">{t("Description")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="description"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                          }}
                          value={props.dataCapex1.description}
                          id="description"
                        />
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="usageThousands">{t("UsageThousands")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="useInMiles"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          value={props.dataCapex1.useInMiles}
                          id="useInMiles"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="shareholder">{t("Shareholder")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="shareholder"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                          }}
                          value={props.dataCapex1.shareholder}
                          id="shareholder"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="bank">{t("Bank")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="bank"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                          }}
                          value={props.dataCapex1.bank}
                          id="bank"
                        />
                      </div>
                    </Col>
                  </Row>

                </CardBody>
                <CardFooter style={{ textAlign: "right" }}>

                  <Button id="btnNew" color="dark" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                    <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                  </Button>
                  {props.botones ?
                    <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}><i className="mdi mdi-content-save mdi-12px"></i>
                      {" "} {props.tipo == "CAPEX1" ? t("Save") : t("Edit")}
                    </Button> : null}
                </CardFooter>
              </Card>


            </AvForm>


          </Col>
        </Row>


      </div>
    </Modal>
  );
};

ModalCapex1.propTypes = {
  isOpen: PropTypes.bool,
  toggle: PropTypes.func
};

export default ModalCapex1;
