                console.log("getCorporateExhibitionByClients", resp2);
                for (let j = 0; j < resp2.length; j++) {
                  if (resp2 != undefined) {
                    for (let i = 0; i < resp2[j].data.length; i++) {
                      let datoSent = {
                        "transactId": Number(dataSession.transactionId),
                        "accountNumber": "",
                        "t24ClientId": resp2[j].Cliente,
                        "description": resp2[j].data[i].name,
                        "approved": resp2[j].data[i].approved,
                        "currentBalance": resp2[j].data[i].balance,
