                await backendServices.getExposicionCorporativaClienteBD(dataSession.transactionId).then(async exposision => {
                  if (exposision.length > 0 && exposision !== undefined) {
                    let datosGenerales = []
                    let datosLista = groupBy(exposision, "t24ClientId");
                    for (let i = 0; i < datosLista.length; i++) {
                      let dataTotal = []
                      dataTotal.push(datosLista[i].find(x => x.description == "Facilidades Corto Plazo") ?? {
