  async function handleSubmit(event, errors, values) {
    event.preventDefault();
    // console.log(Object.entries(ErrorValidate).map(([key, value]) => value).every(Boolean), values);
    // if (!Object.entries(ErrorValidate).map(([key, value]) => value).every(Boolean)) {
    //   return;
    // }

    if (errors.length > 0) {
      return;
    }
    console.log(product);

    values.instructiveType = instructiveType;
    values.facilityId = facilityId;
    values.product = product;
    values.subProduct = subProduct;
    values.loanType = loanType;
    values.indexedRate = indexedRate;
    values.feci = feci;
    values.branch = branch;
    values.monthlyLetterFeci = monthlyLetterFeci;
    values.interestPaymentCycle = interestPaymentCycle;
    values.capitalPaymentCycle = capitalPaymentCycle;
    values.destinyCountry = destinyCountry;
    values.province = province;
    values.activityType = activityType;
    values.cinuActivity = cinuActivity;
    values.dutyFree = dutyFree;
    values.saleChannel = saleChannel;
    values.toDifferIop = toDifferIop;
    values.pawnIop = pawnIop;
    values.creditAccount = creditAccount;
    values.checker = checker;
    values.transfer = transfer;
    values.others = others;


    console.log(values)

    // setIsActiveLoading(true);
    // onSaveProcess(4, values);

    //onSubmit(values);
  }


  function LoadDataDataGeneral() {
    return new Promise((resolve, reject) => {
      apiServiceBackend.consultarDatosGeneralesDesembolso(dataLocation.transactionId).then(data => {
        if (data != null || data != undefined) {
          setbranch(data.branch);
          setsubProduct(data.subproduct);
          setDataGeneral(data)
        };
        resolve();
      });
    })
  }


  function handleSelect(e) {
    let data = Facility.at(e);
    setinstructiveType(data.debtor)
    setfacilityId(data.facilityId)
  }


  async function onSaveProcess(option, values = null) {
    setIsActiveLoading(true);
    switch (option) {
      case 1: {

        break;
      }
      case 2: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 3: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 4: {

        await apiServiceBackend.saveDisbursementInstructiveByFacility(values);



        let descripcion = 0;
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        await api.abortProcess(descripcion).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        setIsActiveLoading(false);
        break;
      }
    }
    setIsActiveLoading(false);

  }

