        <Col md="12">
          <h4 className="card-title mb-3">{t("")}</h4>
          <h4 className="card-title">{datosUsuario != null ? (datosUsuario.typePerson === "1" ? (datosUsuario.name + " " + datosUsuario.name2 + " " + datosUsuario.lastName + " " + datosUsuario.lastName2) : (datosUsuario.name)) + "       " : ""}           #{dataLocation.transactionId}</h4>
          <h4 className="card-title">{t("DisbursementInstructions")}</h4>
          <p className="card-title-desc">{t("This form is official and must be filled out correctly")}</p>
        </Col>

      </Row>
      <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit} autoComplete="off">

        <Tabs defaultActiveKey="0" id="uncontrolled-tab-example" className="mb-3" onSelect={(e) => { handleSelect(e) }}>
          {tabsFacility}
        </Tabs>



        <CardFooter style={{ textAlign: "right" }}>

          <Button color="dark" type="button" style={{ margin: '5px' }} onClick={() => { onSaveProcess(2); }}><i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i> {t("Exit")}</Button>

          <Button color="negative" type="button" onClick={() => { onSaveProcess(3); }} style={{ margin: '5px' }}>
            <i className="mdi mdi-block-helper mdi-12px"></i>{" "}{t("Refuse")}
          </Button>

          <Button color="success" type="submit" style={{ margin: '5px' }}>
            <i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i>{" "} {t("Advance")}
          </Button>
          {/* <Button color="success" type="button" onClick={() => { onSaveProcess(1); }} style={{ margin: '5px' }}>
              <i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i>{" "} {t("GenerateDocument")}
            </Button> */}
        </CardFooter>
      </AvForm>
    </React.Fragment >


  );

}

export default (withTranslation()(DatosGenerales))
