                        </SweetAlert>
                    ) : null}
                </div>
                {loading && (
                    <LoadingIndicator message={t("Please Wait")} />
                )}
                {showSuccess && (
                    <SuccessMessage title={t("Operation Complete")} message={t("Credit Proposal Saved")} />
                )}
            </CreditProposalProvider>
        </React.Fragment >
    );

}

export default DesembolsoLista;
