import React, { useState, useEffect } from "react"
import {useTranslation, withTranslation } from "react-i18next"
import {
  Row,
  Col,
  Button,
  Card,CardHeader,
  CardBody,
  Table,
  CardFooter
} from "reactstrap"

//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb"

import FormFilter from "./firmaContrato/FormFilter"
import SearchResults from "./firmaContrato/SearchResults"
import ApiServicesBandejaFirmaContrato from "../../../services/FirmaContrato/ApiServicesBandejaFirmaContrato"
import { translationHelpers } from '../../../helpers';
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"


const InboxPage = (props) => {

  const { t, i18n } = useTranslation();
  
  const [showResults, setShowResults] = useState(false);
  const [dataList, setDataList] = useState([]);

  const [c] = translationHelpers('common');


  const [locationData, setLocationData] = useState(null);
  //Servicios
  const [backendServices, setbackendServices] = useState(new BackendServices());
  const [bpmServices, setbpmServices] = useState(new BpmServices());
  //const [coreServices, setcoreServices] = useState(new CoreServices());

  const [mainDebtor, setmainDebtor] = useState(null);

  //On Mounting (componentDidMount)
  React.useEffect(() => {
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        fetchData(location.data);
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        fetchData(result);
      }
    }
  }, []);

  //Caraga Inicial de datos
  function fetchData(locationData) {

    //chequear si la tarea no ha sido iniciada
    bpmServices.checkAndStartTask(locationData)
      .then((iniresult) => {
        if (iniresult === false) {
          history.push(url.URL_DASHBOARD);
        }
      })

    loadUserProspect(locationData.transactionId)
    // Read Api Service data
    searchData(null);
  }

  function loadUserProspect(transactionId) {
    // consultarDeudorPrincipal
    backendServices.consultPrincipalDebtor(transactionId)
      .then((data) => {
        if (data !== undefined) {
          setmainDebtor(data);
        }
      });
  }
  
  //buscamos las Propuestas de Credito
  function searchData(values) {
    console.log(values);
    const api = new ApiServicesBandejaFirmaContrato()
    api.getResultadosBandejaFirmaContrato(values)
      .then((data) => {

        console.log(data);
        setDataList(data);
        setShowResults(true);

      })
      .catch((error) => {

        console.error('Error fetching documents', error);
      });


  }


  /*
  // Camino Feliz Firma Contrato... 
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"si"
  };
    
  //Requiere No Firma Contrato .. Termina Proceso
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"no"
  };
  */



  return (
    <React.Fragment>
      <AvForm id="frmSearch" className="needs-validation" >
        <div className="page-content">

          <Breadcrumbs title={t("CommercialCredit")} breadcrumbItem={t("SignContract")} />

          <Card>

          <CardHeader>
              <Row>
                <Col>
                  <h4 className="card-title">{t("SignContract")}</h4>
                  <p className="card-title-desc">{t("This form is official and must be filled out correctly")}</p>
                </Col>
              </Row> 
              <Row>
                <Col sm={6}>
                  <h5 className="card-title">{mainDebtor != null ? (mainDebtor.typePerson === "1" ? (mainDebtor.name + " " + mainDebtor.name2 + " " + mainDebtor.lastName + " " + mainDebtor.lastName2) : (mainDebtor.name)): ""} </h5>
                </Col>
                <Col sm={6}>
                  <h5 className="card-title" style={{ textAlign: "right" }}>{t("Tramit")}:{" "}#{locationData?.transactionId}</h5>
                </Col>
              </Row>                           
            </CardHeader>


            <CardBody>

              <Table className="table mb-0">
                <thead className="table-light">
                  <tr>
                    <td colSpan={2}> <h4 className="card-title">{t("Documentation")}</h4>
                    </td>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <th style={{ width: '40px' }} scope="row"><input type="checkbox" checked={true} onChange={(e) => { }} /></th>
                    <td>Docuemnto 1</td>
                  </tr>
                  <tr>
                    <th scope="row"><input type="checkbox" checked={true} onChange={(e) => { }} /></th>
                    <td>Docuemnto 2</td>
                  </tr>
                  <tr>
                    <th scope="row"><input type="checkbox" checked={true} onChange={(e) => { }} /></th>
                    <td>Docuemnto 3</td>
                  </tr>
                </tbody>
              </Table>

              <div className="mt-4">
                <div className="pb-2">{t("AttachSignedDocument")}</div>
                <AvField
                  className="form-control"
                  name="file"
                  type="file"
                  id="file" />
              </div>

            </CardBody>
            <CardFooter style={{ textAlign: "right" }}>
              <Button id="btnSearch" color="negative" type="button" style={{ margin: '5px' }} onClick={() => { }}><i className="mdi mdi-call-missed mid-12px"></i>
                {" "} {props.t("Refuse")}</Button>
              <Button color="success" type="button" style={{ margin: '5px' }} onClick={() => { }}><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i> {props.t("Continue")}</Button>
            </CardFooter>
          </Card>

        </div>
      </AvForm>
    </React.Fragment>
  );

}


export default (withTranslation()(InboxPage));
