import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"

import ModalSegurosActualesEmpresa from "./ModalSegurosActualesEmpresa"
import { BackendServices } from "../../../../../services"
import SweetAlert from "react-bootstrap-sweetalert"
const dataReciprocity = [
  { id: 1, insuranceCompany: "120", insuranceType: "Incendio", amount: '200', expiration: '' },
  { id: 2, insuranceCompany: "120", insuranceType: "Incendio", amount: '200', expiration: '' },
  { id: 3, insuranceCompany: "120", insuranceType: "Incendio", amount: '200', expiration: '' },
  { id: 4, insuranceCompany: "120", insuranceType: "Incendio", amount: '200', expiration: '' },
];
const SegurosActualesEmpresa = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const location = useLocation()
  /* ---------------------------------------------------------------------------------------------- */
  /*                        Variables de estados para los mensajes de alerta                        */
  /* ---------------------------------------------------------------------------------------------- */
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")
  const [dataDelete, setDataDelete] = useState([]);
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [dataSeguros, setdataSeguros] = useState({
    "transactId": 0,
    "company": "",
    "insuranceType": {
      "code": "",
      "name": ""
    },
    "amount": 0,
    "dueDate": ""
  });
  const [tipo, settipo] = useState("");

  const [formValid, setFormValid] = useState(false);
  const [dataReciprocityRows, setdataReciprocityRows] = useState(null);
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmProveedores');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }
  }));
  const [showModalModalSegurosActualesEmpresa, setShowModalModalSegurosActualesEmpresa] = useState(false);
  React.useEffect(() => {
    initializeData();
  }, []);
  function initializeData() {
    const api = new BackendServices();
    api.consultarSegurosEmpresaIGR(dataLocation.transactionId).then(resp => {
      if (resp!=undefined) {
        setdataReciprocityRows(resp.map((data) => (
          data.status?
          <tr key={data.insuranceId}>
            <td>{data.company}</td>
            <td>{data.insuranceType.code}</td>
            <td>{data.amount}</td>
            <td>{data.dueDate}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { actualizarDatosSeguros(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { EliminarDatosSeguros(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>:null)
        ));
      } else {
        setdataReciprocityRows(
          <tr key={1}>
            <td colSpan="7" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
  }
  function verDatosSeguros(data) {
    setdataSeguros(data)
    setbotonValidation(false);
    toggleShowModalSegurosActualesEmpresa()
  }
  function actualizarDatosSeguros(data) {
    settipo("editar")
    setdataSeguros(data)
    setbotonValidation(true);
    toggleShowModalSegurosActualesEmpresa()
  }
  function gestionDatos(data, tipo) {
    console.log(data);
    const api = new BackendServices()
    data.transactId = dataLocation.transactionId;
    if (tipo == "guardar") {
      api.nuevoSegurosEmpresaIGR(data).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModalSegurosActualesEmpresa();
        } else {
          toggleShowModalSegurosActualesEmpresa();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    } else {
      api.actualizarSegurosEmpresaIGR(data).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModalSegurosActualesEmpresa();
        } else {
          toggleShowModalSegurosActualesEmpresa();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    }
  }
  function EliminarDatosSeguros(data) {
    setDataDelete(data)
    setconfirm_alert(true);
  }
  //abrimos modal para adjunar archivos
  function toggleShowModalSegurosActualesEmpresa() {
    setShowModalModalSegurosActualesEmpresa(!showModalModalSegurosActualesEmpresa);
    removeBodyCss()
  }

  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }

  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    //props.onSubmit(values);
  }


  return (
    <React.Fragment>
      <h5>
        {t("CurrentCompanyInsurance")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12">
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("InsuranceCompany")}</th>
                  <th>{t("InsuranceType")}</th>
                  <th>{t("Amount")}</th>
                  <th>{t("Expiration")}</th>
                  <th><Link className="float-end" onClick={() => {
                    settipo("guardar");
                    setbotonValidation(true); setdataSeguros({
                      "transactId": 0,
                      "company": "",
                      "insuranceType": {
                        "code": "",
                        "name": ""
                      },
                      "amount": 0,
                      "dueDate": ""
                    }); toggleShowModalSegurosActualesEmpresa();
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link>
                  </th>
                </tr>
              </thead>
              <tbody>
                {dataReciprocityRows}
              </tbody>
            </Table>
          </Col>
        </Row>
      </AvForm>
      <ModalSegurosActualesEmpresa dataSeguros={dataSeguros} gestionDatos={gestionDatos} tipo={tipo} botones={botonValidation} isOpen={showModalModalSegurosActualesEmpresa} toggle={() => { toggleShowModalSegurosActualesEmpresa() }} />
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            initializeData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            initializeData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            apiBack.eliminarSegurosEmpresaIGR({ transactId: dataLocation.transactionId, InsuranceId: dataDelete.insuranceId }).then(resp => {
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                setsuccessSave_dlg(true)
              }
            }).catch(error => {
              setconfirm_alert(false)
              seterror_dlg(true)
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
    </React.Fragment>
  );

});

/*
SegurosActualesEmpresa.propTypes = {
  onSubmit: PropTypes.func.isRequired,
}
*/

export default SegurosActualesEmpresa;
