import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"

import ModalEmpresasRelacionadas from "./ModalEmpresasRelacionadas"
import { BackendServices } from "../../../../../services"
import SweetAlert from "react-bootstrap-sweetalert"

const EmpresasRelacionadas = React.forwardRef((props, ref) => {

  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmEmpresaRelacionada');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn2
  }));
  const location = useLocation()
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [showModelAttachment, setShowModelAttachment] = useState(false);
  const [dataAccionistasDataRows, setdataAccionistasDataRows] = useState([]);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [tipo, settipo] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [formValid, setFormValid] = useState(false);
  const [dataReturn2, setDataReturn2] = useState(props.dataEmpresaRelacionada);
  const [jsonEmpresaRelacionada, setjsonEmpresaRelacionada] = useState({
    trasactId: 0,
    name: null,
    activity: null,
    sectorExperience: null,
    relationship: null
  });
  const [dataDelete, setDataDelete] = useState([]);
  const [dataReturn, setDataReturn] = useState({
    trasactId: 0,
    name: null,
    activity: null,
    sectorExperience: null,
    relationship: null
  });
  const { t, i18n } = useTranslation();
  React.useEffect(() => {
    // Read Api Service data   
    initializeData();
  }, []);
  function initializeData() {
    const api = new BackendServices()
    api.consultarDatosEmpresaRelacionada(dataLocation.transactionId).then(resp => {
      if (resp.relatedCompanies.length > 0) {
        dataReturn2.dataTableEmpresaRelacionada=resp.relatedCompanies;
        setDataReturn2(dataReturn2)
        setdataAccionistasDataRows(resp.relatedCompanies.map((data) => (
          <tr key={data.companyId}>
            <td>{data.name}</td>
            <td>{data.activity}</td>
            <td>{data.sectorExperience}</td>
            <td>{data.relationship}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { verDatosEmpresa(data) }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { editarDatosEmpresa(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { EliminarEmpresa(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ))
      } else {
        setdataAccionistasDataRows(
          <tr key={1}>
            <td colSpan="5" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });

  }
  function verDatosEmpresa(data) {
    setjsonEmpresaRelacionada(data)
    setbotonValidation(false);
    toggleShowModelAttachment()
  }
  function editarDatosEmpresa(data) {
    settipo("editar")
    setjsonEmpresaRelacionada(data)
    setbotonValidation(true);
    toggleShowModelAttachment()
  }
  function EliminarEmpresa(data) {
    setDataDelete(data)
    setconfirm_alert(true);
  }

  //abrimos modal para adjunar archivos
  function toggleShowModelAttachment() {
    setShowModelAttachment(!showModelAttachment);
    removeBodyCss()
  }

  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }

  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    dataReturn2.informacionAccionistaDetails=values.informacionAccionistaDetails;
    setDataReturn2(dataReturn2)
    setFormValid(true)
    //props.onSubmit(values);
  }
  function addEmpresaRelacionada(values,tipo0) {
    dataReturn.name = values.name;
    dataReturn.activity = values.activity;
    dataReturn.sectorExperience = Number(values.sectorExperience);
    dataReturn.relationship = values.relationship;
    dataReturn.trasactId = Number(dataLocation.transactionId);
    setDataReturn(dataReturn);
    const apiBack = new BackendServices();
    if(tipo0=="guardar"){
      apiBack.nuevoEmpresaRelacionada(dataReturn).then(resp => {
        setsuccessSave_dlg(true)
        toggleShowModelAttachment();
      })
      
    }else{
      dataReturn.companyId =values.companyId;
      apiBack.actualizarEmpresaRelacionada(dataReturn).then(resp => {
        setsuccessSave_dlg(true)
        toggleShowModelAttachment();
      })

    }
  }

  return (
    <React.Fragment>
      <h5>
        {t("RelatedCompanies")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmEmpresaRelacionada" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12">

            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{t("Name")}</th>
                  <th>{t("Activity")}</th>
                  <th>{t("YearsInTheSector")}</th>
                  <th>{t("Relation")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setbotonValidation(true); setjsonEmpresaRelacionada({
                      trasactId: 0,
                      name: null,
                      activit: null,
                      sectorExperience: null,
                      relationship: null
                    }); settipo("guardar"); toggleShowModelAttachment();
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link>
                  </th>
                </tr>
              </thead>
              <tbody>
                {dataAccionistasDataRows}
              </tbody>
            </Table>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <div className="mb-3">
              <Label htmlFor="companyHistoryDescription">{t("Description")}</Label>              
              <AvField onChange={(e) => { dataReturn2.description=e.target.value; dataReturn2.observations=e.target.value; setDataReturn2(dataReturn2);}}
                className="form-control"
                name="informacionAccionistaDetails"
                id="informacionAccionistaDetails"
                type="textarea"
                value={props.dataEmpresaRelacionada.observations}
                rows="7"
                validate={{
                  required: { value: true, errorMessage: t("Required Field") }
                }}
              />
            </div>
          </Col>
        </Row>

      </AvForm>
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            initializeData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            initializeData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            apiBack.eliminarEmpresaRelacionada({ trasactId: dataDelete.trasactId, companyId: dataDelete.companyId }).then(resp => {
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                setsuccessSave_dlg(true)
              }
            }).catch(error => {
              setconfirm_alert(false)
              seterror_dlg(true)
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
      <ModalEmpresasRelacionadas tipo={tipo} jsonEmpresaRelacionada={jsonEmpresaRelacionada} botones={botonValidation} addEmpresaRelacionada={addEmpresaRelacionada} isOpen={showModelAttachment} toggle={() => { toggleShowModelAttachment() }} />
    </React.Fragment>
  );

});

