import React, { useState, useImperativeHandle, forwardRef, useContext, useEffect } from 'react';

import PropTypes from 'prop-types';
import { translationHelpers } from '../../../../helpers';
import RiesgoAmbientalContext from '../RiesgoAmbientalContext';

import {
  Card,
  CardBody,
  Row,
  Col,
  Button,
  Label
} from "reactstrap"
import Switch from "react-switch";

import { AvForm, AvField, AvGroup, AvInput } from "availity-reactstrap-validation"
import ServicioRiesgoAmbiental from '../../../../services/RiesgoAmbiental/RiesgoAmbiental';
import { useTranslation } from 'react-i18next'
const Offsymbol = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        fontSize: 12,
        color: "#fff",
        paddingRight: 2,
      }}
    >
      {" "}
      No
    </div>
  );
};

const OnSymbol = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        fontSize: 12,
        color: "#fff",
        paddingRight: 2,
      }}
    >
      {" "}
      Si
    </div>
  );
};
const FormDetalles = forwardRef((props, ref) => {

  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmDetalleAmbiental');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }))
  const { t, i18n } = useTranslation();

  const [displayCovenantDetails, setDisplayCovenantDetails] = useState(true);
  const [formValid, setFormValid] = useState(false);
  const [isUpdatingRecord, setUpdatingRecord] = useState(false);
  const [environmentCovenant, setEnvironemntCovenant] = useState(false);
  const [detail, setDetail] = useState('');
  const [compliance, setCompliance] = useState('');
  const [detected, setDetected] = useState('');
  const [term, setTerm] = useState('');
  const [recommendations, setRecommendations] = useState('');
  const [conclusions, setConclusions] = useState('');
  const [dataReturn, setdataReturn] = useState('');

  // services
  const context = useContext(RiesgoAmbientalContext);
  const servicioRiesgoAmbiental = new ServicioRiesgoAmbiental();

  useEffect(() => {
    // load data
    if (!context.transactionId) {
      return;
    }
    console.log('fetch info riesgo ambiental', context);
    const { transactionId } = context;
    servicioRiesgoAmbiental.consultarInformacionRiesgoAmbiental(transactionId)
      .then((info) => {
        console.log('info riesgo ambiental', info);
        if (info.transactId) {
          setUpdatingRecord(true);
          setEnvironemntCovenant(info.environmentCovenant);
          setDetail(info.detail);
          setCompliance(info.compliance);
          setDetected(info.detected);
          setTerm(info.term);
          setRecommendations(info.recommendations);
          setConclusions(info.conclusions);
        }
      })
      .catch((error) => {
        console.log('error fetch detalles', error);
      })
  }, [context]);
  function handleCovenantChange(event) {
    setDisplayCovenantDetails(event.target.value === 'Si'?true:false);
  }

  function handleSubmit(event, errors, values) {
    values.environmentCovenant=environmentCovenant;
    event.preventDefault();
    if (errors.length > 0) {
      setFormValid(false);
      return;
    }
    setdataReturn(values)
    event.preventDefault();
    const isValid = errors.length === 0;
    setFormValid(isValid);
    // if( isValid ) {
    //   console.log('submit', values, errors);
    //   const { transactionId } = context;
    //   servicioRiesgoAmbiental.guardarInformacionRiesgoAmbiental(transactionId, values, isUpdatingRecord)
    //     .then((result) => {
    //       console.log('result', result);
    //     })
    //     .catch((error) => {
    //       console.error('Error guardando', error);
    //     })
    // }
  }

  return (
    <React.Fragment>
      <h4 className="card-title">{t("OtherDetails")}</h4>
      <p className="card-title-desc"></p>

      <AvForm id="frmDetalleAmbiental" className="needs-validation" onSubmit={handleSubmit}>
        <Card>
          <CardBody>

            <Row className="my-3">
              <Col md="12">
                <Label className="form-check-label" htmlFor="isProjectLocatedProtectedNaturalArea"> {t("Covenants Ambientales")}</Label>
              <AvGroup check className="mb-3">
                <Switch name="isProjectLocatedProtectedNaturalArea"
                  uncheckedIcon={<Offsymbol />}
                  checkedIcon={<OnSymbol />}
                  onColor="#007943"
                  id="check1"
                  className="form-label"
                  onChange={() => {
                    setEnvironemntCovenant(!environmentCovenant);
                  }}
                  checked={environmentCovenant}
                />
                {'   '}
              </AvGroup>
                {/* <AvGroup>
                  <Label htmlFor="environmentCovenant">{t("Covenants Ambientales")}</Label>
                  <AvField
                    className="form-control"
                    name="environmentCovenant"
                    type="select"
                    id="environmentCovenant"
                    value={environmentCovenant}
                    onChange={handleCovenantChange}>
                    <option value={true}>{t("Yes")}</option>
                    <option value={false}>{t("No")}</option>
                  </AvField>
                </AvGroup> */}
              </Col>
            </Row>

            {environmentCovenant && (
              <>
                <Row className="my-3">
                  <Col md="4">
                    <AvGroup>
                      <Label htmlFor="detail">{t("Detalle")}</Label>
                      <AvField className="form-control"
                        name="detail"
                        type="text"
                        id="detail"
                        value={detail} />
                    </AvGroup>
                  </Col>
                  <Col md="4">
                    <AvGroup>
                      <Label htmlFor="compliance">{t("Cumplimiento")}</Label>
                      <AvField className="form-control"
                        name="compliance"
                        type="text"
                        id="compliance"
                        value={compliance} />
                    </AvGroup>
                  </Col>
                  <Col md="4">
                    <AvGroup>
                      <Label htmlFor="term">{t("Plazo")}</Label>
                      <AvField className="form-control"
                        name="term"
                        type="text"
                        id="term"
                        value={term} />
                    </AvGroup>
                  </Col>
                </Row>

                <Row className="my-3">
                  <Col md="12">
                    <AvGroup>
                      <Label htmlFor="detected">{t("Situacion Detectada")}</Label>
                      <AvField className="form-control"
                        name="detected"
                        type="textarea"
                        id="detected"
                        value={detected}
                        rows="7" />
                    </AvGroup>
                  </Col>
                </Row>
              </>
            )}

            <Row className="my-3">
              <Col md="12">
                <AvGroup>
                  <Label htmlFor="recommendations">{t("Recomendaciones")}</Label>
                  <AvField className="form-control"
                    name="recommendations"
                    type="textarea"
                    id="recommendations"
                    value={recommendations}
                    rows="7" />
                </AvGroup>
              </Col>
            </Row>

            <Row className="my-3">
              <Col md="12">
                <AvGroup>
                  <Label htmlFor="conclusions">{t("Conclusiones")}</Label>
                  <AvField className="form-control"
                    name="conclusions"
                    type="textarea"
                    id="conclusions"
                    value={conclusions}
                    rows="7" />
                </AvGroup>
              </Col>
            </Row>

          </CardBody>

        </Card>
      </AvForm>

    </React.Fragment>
  );
});


FormDetalles.propTypes = {
  onSubmit: PropTypes.func.isRequired,
};

export default FormDetalles;
