    //Deudas a Corto y Largo Plazo... se le pasa el Numero de Cliente de T24
    async getShortLongTermDebts(partyId) {

        if(partyId.length <= 0){
            return undefined;
        }

        let params = {
            PartyId: partyId
        }

        let data = qs.stringify(params);

        var longTermresult = [] //{name:"Facilidades Corto Plazo",approvalRisk:0,proposalRisk:0,variationRisk:0};
        var longTermTotal = { name: "Total", approvalRisk: 0, proposalRisk: 0, variationRisk: 0, date: moment().format("YYYY-MM-DD") };

        var shortTermresult = [] //{name:"Facilidades Corto Plazo",approvalRisk:0,proposalRisk:0,variationRisk:0};
        var shortTermTotal = { name: "Total", approvalRisk: 0, proposalRisk: 0, variationRisk: 0, date: moment().format("YYYY-MM-DD") };

        try {            
            //servicio retorna CP y LP (Corto y Largo Plazo)
            var result = await this.get(url.URL_CORE_FACILIDADES + "?" + data);

            console.log("getCorporateExhibition result", result);

            //Armamos la data a mostrar en pantalla
            result.AcctLoans.AcctLoan.map(function (item, i) {
                var openYear = parseInt(String(item.AcctOpeningInfo.OpenDt).split('-')[0]);
                var dueYear = parseInt(String(item.CreditAcctData.DueDt).split('-')[0]);
                if (dueYear - openYear > 1) { //Largo Plazo
                    var longTerm = { name: "Facilidades Largo Plazo", approvalRisk: 0, proposalRisk: 0, variationRisk: 0 }
                    longTerm.approvalRisk = Number(item.AcctOpeningInfo.InitialAmt.Amt);
                    longTerm.proposalRisk = Number(item.AcctBal[0].CurAmt.Amt);
                    longTerm.variationRisk = longTerm.approvalRisk - longTerm.proposalRisk;
                    longTerm.name = item.ProductDesc
                    longTerm.date = item.AcctOpeningInfo.OpenDt

                    longTermTotal.approvalRisk += longTerm.approvalRisk;
                    longTermTotal.proposalRisk += longTerm.proposalRisk;

                    longTermresult.push(longTerm);
                }
                else { //Corto Pazo  
                    var shortTerm = { name: item.ProductDesc, approvalRisk: 0, proposalRisk: 0, variationRisk: 0 }
                    shortTerm.approvalRisk = Number(item.AcctOpeningInfo.InitialAmt.Amt);
                    shortTerm.proposalRisk = Number(item.AcctBal[0].CurAmt.Amt);
                    shortTerm.variationRisk = shortTerm.approvalRisk - shortTerm.proposalRisk;
                    shortTerm.name = item.ProductDesc
                    shortTerm.date = item.AcctOpeningInfo.OpenDt

                    shortTermTotal.approvalRisk += shortTerm.approvalRisk;
                    shortTermTotal.proposalRisk += shortTerm.proposalRisk;

                    shortTermresult.push(shortTerm);
                }
            })
            longTermTotal.variationRisk = longTermTotal.approvalRisk - longTermTotal.proposalRisk;
            longTermresult.push(longTermTotal);

            shortTermTotal.variationRisk = shortTermTotal.approvalRisk - shortTermTotal.proposalRisk;
            shortTermresult.push(shortTermTotal);
        }
        catch (err) {
            console.error("URL_CORE_FACILIDADES", err);
        }

        return { longTermresult, shortTermresult };
    }
    
