import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';
import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
} from "reactstrap"
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import { BackendServices } from "../../../../../services/";
import Switch from "react-switch";
import Select from "react-select";

const Offsymbol = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        fontSize: 12,
        color: "#fff",
        paddingRight: 2,
      }}
    >
      {" "}
      No
    </div>
  );
};
const OnSymbol = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        fontSize: 12,
        color: "#fff",
        paddingRight: 2,
      }}
    >
      {" "}
      Si
    </div>
  );
};
const ModalGarantes = (props) => {
  const { t, i18n } = useTranslation();
  const [identificationType, setidentificationType] = useState("RUC");
  const [identificationList, setIdentificationList] = useState([]);
  const [campoRequeridoIdentificacion, setcampoRequeridoIdentificacion] = useState(false);
  const [switch1, setswitch1] = useState(false);
  const [tipoIdentiSelect, settipoIdentiSelect] = useState(undefined);

  //On Mounting (componentDidMount)
  React.useEffect(() => {
    initializeData();
    setswitch1(props.jsonGarante.isGuarantor)
    var defaultVal = null;
    settipoIdentiSelect(undefined)
    try {
      if (identificationList.length > 0 && props.jsonGarante.documentType !== null && tipoIdentiSelect === undefined) {
        defaultVal = identificationList.find(x => x.value === props.jsonGarante.documentType);
        if (defaultVal !== undefined) {
          settipoIdentiSelect(defaultVal);
        }
      }
    }
    catch (err) { }
  }, [props]);
  // Submitimos formulario para busqueda de clientes
  function handleSubmit(event, errors, values) {
    if (identificationType == null) {
      setcampoRequeridoIdentificacion(true);
      return;
    } else {
      setcampoRequeridoIdentificacion(false);
    }
    values.guantorDebtor = switch1
    values.identificationType = identificationType
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    if (props.tipo == "guardar") {
      props.addGarante(values, props.tipo);
    } else {
      values.guarantorId = props.jsonGarante.guarantorId;
      props.addGarante(values, props.tipo);
    }
  }
  //Caraga Inicial
  function initializeData() {

    const apiServiceBackend = new BackendServices();

    apiServiceBackend.consultarCatalogoTipoIdentificacion()
      .then((data) => {
        if (data !== null && data !== undefined) {
          let json = [];
          for (let i = 0; i < data.length; i++) {
            json.push({ label: t(data[i]["description"]), value: data[i]["id"] })
          }
          const optionGroup1 = [
            {
              label: t("ID Type"),
              options: json,
            },

          ];
          setIdentificationList(json);
        }
      })
      .catch((error) => {
        console.error('api error: ', error);
      });
  }

  //On change Inputs
  function handleChangeInputfrmSearch(e) {
    //selectedData[e.target.name] = e.target.value;
    //props.update DataModel(selectedData);    

    switch (e.target.name) {
      case "identificationType": {
        setidentificationType(e.target.value);
        break;
      }
    }
  }

  function getSearchForm() {
    //segun tipo de identiicación
    switch (identificationType) {
      case "RUC": { //RUC
        return (
          <Row>
            <Col md="8">
              <div className="mb-3">
                <Label htmlFor="name">{t("SocialReason")}</Label>
                <AvField
                  className="form-control"
                  name="firstName"
                  value={props.jsonGarante.name}
                  id="firstName"
                  type="text" onChange={handleChangeInputfrmSearch}
                  errorMessage={t("Required Field")}
                  validate={{ required: { value: true } }} />
              </div>
            </Col>
          </Row>
        )
      }
      default: { //Cedula o Pasaporte 
        return (
          <Row>
            <Col md="4">
              <div className="mb-3">
                <Label htmlFor="firstName">{t("First Name")}</Label>
                <AvField
                  className="form-control"
                  name="firstName"
                  value={props.jsonGarante.name}
                  id="firstName"
                  type="text" onChange={handleChangeInputfrmSearch}
                  errorMessage={t("Required Field")}
                  validate={{ required: { value: true } }} />
              </div>
            </Col>
            <Col md="4">
              <div className="mb-3">
                <Label htmlFor="secondName">{t("SecondName")}</Label>
                <AvField
                  name="secondName"
                  value={props.jsonGarante.secondName}
                  id="secondName"
                  type="text" onChange={handleChangeInputfrmSearch}
                  className="form-control" />
              </div>
            </Col>
            <Col md="4">
              <div className="mb-3">
                <Label htmlFor="firstLastName">{t("FirstLastName")}</Label>
                <AvField
                  name="firstLastName"
                  value={props.jsonGarante.lastName}
                  id="firstLastName"
                  type="text" onChange={handleChangeInputfrmSearch}
                  className="form-control"
                  errorMessage={t("Required Field")}
                  validate={{ required: { value: true } }} />
              </div>
            </Col>
            <Col md="4">
              <div className="mb-3">
                <Label htmlFor="secondLastName">{t("SecondLastName")}</Label>
                <AvField
                  name="secondLastName"
                  value={props.jsonGarante.secondLastName}
                  id="secondLastName"
                  type="text" onChange={handleChangeInputfrmSearch}
                  className="form-control" />
              </div>
            </Col>
          </Row>
        )
      }
    }
  }
  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}>
      <div className="modal-header">
        <h5 className="modal-title mt-0">{t("Guarantor")}</h5>
        <button
          type="button"
          onClick={props.toggle}
          data-dismiss="modal"
          className="close"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>
        <AvForm id="frnGarante" className="needs-validation" onSubmit={handleSubmit}>
          <Card>
            <CardBody>
              <Row>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="guantorDebtor">{t("The Debtor is the Guarantor of the property to be assigned as Guarantee")}</Label>
                    {'   '}
                    <Switch name="guantorDebtor"
                      id="guantorDebtor"
                      uncheckedIcon={<Offsymbol />}
                      checkedIcon={<OnSymbol />}
                      onColor="#007943"
                      className="form-label"
                      onChange={() => {
                        setswitch1(!switch1);
                      }}
                      checked={switch1}
                    />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="identificationType">{t("ID Type")}</Label>
                    <Select
                      onChange={(e) => { settipoIdentiSelect(identificationList.find(x => x.value === e.value)); setidentificationType(e.value) }}
                      options={identificationList}
                      id="sustainableCustomer"
                      classNamePrefix="select2-selection"
                      placeholder={t("toselect")}
                      value={tipoIdentiSelect}
                    />
                    {campoRequeridoIdentificacion ?
                      <p className="message-error-parrafo">{t("Required Field")}</p>
                      : null}
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="customerDocumentId">{t("ID Number")}</Label>
                    <AvField
                      className="form-control"
                      name="customerDocumentId"
                      value={props.jsonGarante.guarantorDocument}
                      id="customerDocumentId"
                      type="text" onChange={handleChangeInputfrmSearch}
                      errorMessage={t("Required Field")}
                      validate={{ required: { value: true } }}
                    />
                  </div>
                </Col>
              </Row>
              {getSearchForm()}
              <Row>
                <Col md="6">
                  <div className="mb-3">
                    <Label htmlFor="guarantorRelationship">{t("Direct relationship between the debtor and the guarantor")}</Label>
                    <AvField
                      type="textarea"
                      name="guarantorRelationship"
                      value={props.jsonGarante.guarantorRelationship}
                      id="guarantorRelationship"
                      maxLength="1000"
                      rows="7"
                      errorMessage={t("Required Field")}
                      validate={{ required: { value: true } }}
                    />
                  </div>
                </Col>
                <Col md="6">
                  <div className="mb-3">
                    <Label htmlFor="propertyOrigin">{t("How and with what funds was the asset to be sold as collateral acquired")}</Label>
                    <AvField
                      type="textarea"
                      name="propertyOrigin"
                      value={props.jsonGarante.origin}
                      id="propertyOrigin"
                      maxLength="1000"
                      rows="7"
                      errorMessage={t("Required Field")}
                      validate={{ required: { value: true } }}
                    />
                  </div>
                </Col>
              </Row>
              <Row>
                <Col md="6">
                  <div className="mb-3">
                    <Label htmlFor="guarantorCommitment">{t("Does the debtor have any commitment or credit obligation with the guarantor")}</Label>
                    <AvField
                      type="textarea"
                      name="guarantorCommitment"
                      value={props.jsonGarante.guarantorCommitment}
                      id="guarantorCommitment"
                      maxLength="1000"
                      rows="7"
                      errorMessage={t("Required Field")}
                      validate={{ required: { value: true } }}
                    />
                  </div>
                </Col>
                <Col md="6">
                  <div className="mb-3">
                    <Label htmlFor="guaranteeReason">{t("Why do you pledge your property as collateral")}</Label>
                    <AvField
                      type="textarea"
                      name="guaranteeReason"
                      value={props.jsonGarante.warrantyReason}
                      id="guaranteeReason"
                      maxLength="1000"
                      rows="7"
                      errorMessage={t("Required Field")}
                      validate={{ required: { value: true } }}
                    />
                  </div>
                </Col>
              </Row>
            </CardBody>
            <CardFooter style={{ textAlign: "right" }}>
              <Button id="btnNew" color="dark" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                {t("Cancel")}
              </Button>
              {props.botones ?
                <Button id="btnSearch" type="submit" color="success" style={{ margin: '5px' }}>
                  <i className="mdi mdi-content-save mdi-12px"></i>
                  {" "} {props.tipo == "guardar" ? t("Save") : t("Edit")}
                </Button>
                : null}
            </CardFooter>
          </Card>
        </AvForm>
      </div>
    </Modal>
  );
};

ModalGarantes.propTypes = {
  isOpen: PropTypes.bool,
  toggle: PropTypes.func,
};

export default (withTranslation()(ModalGarantes));
