  function loadProduct() {
    return new Promise((resolve, reject) => {
      apiServiceCore.getProductCatalog().then(data => {
        resolve(data?.records)
      }).catch((error) => {
        resolve()
      });
    });
  }
  function loadSubProduct() {
    return new Promise((resolve, reject) => {
      apiServiceCore.getSubProductCatalog().then(data => {
        resolve(data?.Records)
      }).catch((error) => {
        resolve()
      });
    });
  }
