import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';

import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
  InputGroup,
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import Select from "react-select";


const ModalReciprocidad = (props) => {
  const { t, i18n } = useTranslation();
  const [campoRequeridoAno, setcampoRequeridoAno] = useState(false);
  const [campoRequeridoMes, setcampoRequeridoMes] = useState(false);
  const [ano, setano] = useState(null);
  const [mes, setmes] = useState(null);
  const [mesSelect, setmesSelect] = useState(undefined);
  const meses = [{ label: t("January"), value: t("January") }
    , { label: t("February"), value: t("February") },
  { label: t("March"), value: t("March") },
  { label: t("April"), value: t("April") },
  { label: t("May"), value: t("May") },
  { label: t("June"), value: t("June") },
  { label: t("July"), value: t("July") },
  { label: t("August"), value: t("August") },
  { label: t("September"), value: t("September") },
  { label: t("October"), value: t("October") },
  { label: t("November"), value: t("November") },
  { label: t("December"), value: t("December") }];
  React.useEffect(() => {
    // Read Api Service data
    var defaultVal = null;
    setmesSelect(undefined)
    try {
      if (meses.length > 0 && props.dataRecipro.month !== null && mesSelect === undefined) {
        defaultVal = meses.find(x => x.value === props.dataRecipro.month);
        if (defaultVal !== undefined) {
          setmesSelect(defaultVal);
        }
      }
    }
    catch (err) { }

  }, [props]);
  // Submitimos formulario para busqueda de clientes
  function handleSubmit(event, errors, values) {
    if (ano == null) {
      setcampoRequeridoAno(true);
      return;
    } else {
      setcampoRequeridoAno(false);
    }
    if (mes == null) {
      setcampoRequeridoMes(true);
      return;
    } else {
      setcampoRequeridoMes(false);
    }
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    values.year = ano;
    values.month = mes;
    if (props.tipo == "guardar") {
      props.gestionDatos(values, props.tipo);
    } else {
      values.reciprocity_id = props.dataRecipro.reciprocity_id;
      props.gestionDatos(values, props.tipo);
    }
    // props.onSubmit(values);
  }
  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}>
      <div className="modal-header">
        <h5 className="modal-title mt-0">{t("Reciprocity")}</h5>
        <button
          type="button"
          onClick={props.toggle}
          data-dismiss="modal"
          className="close"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>

        <Row>
          <Col xl="12">
            <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
              <Card>
                <CardBody>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="idYear">{t("Year")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="year"
                          id="year"
                          onChange={(e) => { setano(e.target.value); }}
                          value={props.dataRecipro.year}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="idMonth">{t("Month")}</Label>
                        <Select
                          onChange={(e) => { setmesSelect(meses.find(x => x.value === e.value)); setmes(e.value); }}
                          options={meses}
                          id="sustainableCustomer"
                          classNamePrefix="select2-selection"
                          placeholder={t("toselect")}
                          value={mesSelect}
                        // value={props.dataRecipro.mes}
                        />
                        {campoRequeridoMes ?
                          <p className="message-error-parrafo">{t("Required Field")}</p>
                          : null}
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="sales">{t("Sales")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="sales"
                          id="sales"
                          value={props.dataRecipro.sales}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="deposits">{t("Deposits")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="deposits"
                          value={props.dataRecipro.deposits}
                          id="deposits"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="averagebalance">{t("AverageBalance")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="averageBalance"
                          value={props.dataRecipro.averageBalance}
                          id="averageBalance"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="reciprocity">{t("Reciprocity")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="reciprocity"
                          value={props.dataRecipro.reciprocity}
                          id="reciprocity"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="sow">{t("Sow")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="sow"
                          value={props.dataRecipro.sow}
                          id="sow"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                  </Row>
                </CardBody>
                <CardFooter style={{ textAlign: "right" }}>
                  <Button id="btnNew" color="dark" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                    <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                  </Button>
                  {props.botones ?
                    <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}><i className="mdi mdi-content-save mdi-12px"></i>
                      {" "} {props.tipo == "guardar" ? t("Save") : t("Edit")}
                    </Button> : null}
                </CardFooter>
              </Card>
            </AvForm>
          </Col>
        </Row>
      </div>
    </Modal>
  );
};
ModalReciprocidad.propTypes = {
  isOpen: PropTypes.bool,
  toggle: PropTypes.func
};

export default ModalReciprocidad;
