            <div className="mb-4">
              <Label>{t("Instrucciones especiales de la Banca")}</Label>
              <AvField
                className="form-control"
                name="instruccionesEspeciales"
                type="textarea"
                rows="4"
                id="instruccionesEspeciales" />

            </div>
          </Col>
        </Row>
        <HeaderSections title={t("Administración")} t={t}></HeaderSections>
        <Row>
