import React, { useState } from "react"
import PropTypes from 'prop-types';
import { Link } from "react-router-dom"
import { useTranslation } from "react-i18next";
import { formatDate } from '../../../../helpers';
import {
  Table,
} from "reactstrap"

import ModalNewAttachment from "./ModalNewCheckListItem";

const CheckList = (props) => {
  const { t, i18n } = useTranslation();

  const [displayModal, setDisplayModal] = useState(false);

  if (!props.items) {
    return null;
  }
  function toggleModal() {
    setDisplayModal(!displayModal);
  }
  const dataRows = props.items.map((item, index) => (
    <tr key={'row-checklist-' + index}>
      <th scope="row">{item.requerido ? t("Yes") : t("No")}</th>
      <td>{item.descripcion}</td>
      <td>{item.exception ? t("Yes") : t("No")}</td>
      <td>{item.fechaExpiracion ? formatDate(item.fechaExpiracion, 'dd-mmm-yyyy') : ''}</td>
      <td>{item.comentarios}</td>
      <td className="text-end">
      </td>
    </tr>)
  );
  return (
    <React.Fragment>
      <h5>{props.title}</h5>
      <p className="card-title-desc">
      </p>

      <div className="table-responsive mt-2">
        <Table className="table mb-0">
          <thead className="table-light">
            <tr>
              <th>{t("Required")}</th>
              <th>{t("Description")}</th>
              <th>{t("Exception")}</th>
              <th>{t("Expiration")}</th>
              <th>{t("Comments")}</th>
              <th className="text-center">{t("Attachment")}</th>
            </tr>
          </thead>
          <tbody>
            {dataRows}
          </tbody>
        </Table>
      </div>
      <ModalNewAttachment isOpen={displayModal} toggle={toggleModal} onSave={props.onSaveItem} />
    </React.Fragment>
  );
};
CheckList.propTypes = {
  items: PropTypes.array.isRequired,
  onSaveItem: PropTypes.func
};
export default CheckList;
