/*ReinforcedManagementReport = Lista de Informe Reforzado*/
import React, { useState } from "react"
import PropTypes from 'prop-types';

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
} from "reactstrap"

import { AvForm, AvField } from "availity-reactstrap-validation"

//i18n
import { withTranslation } from "react-i18next"
import { BackendServices, CoreServices, BpmServices, } from "../../../../../services";
import { useLocation, useHistory } from 'react-router-dom'
import Select from "react-select";
import { useTranslation } from "react-i18next"


const DatosGenerales = (props) => {

  const apiServiceBackend = new BackendServices();
  const apiServiceCore = new CoreServices();
  const location = useLocation()

  const { t, i18n } = useTranslation();


  const [dataLocation, setData] = useState(location.data);

  const [campoRequeridoSubcursal, setcampoRequeridoSubcursal] = useState(false);
  const [campoRequeridoSubproducto, setcampoRequeridoSubproducto] = useState(false);
  const [campoRequeridoProducto, setcampoRequeridoProducto] = useState(false);
  const [campoRequeridoComercial, setcampoRequeridoComercial] = useState(false);
  const [campoRequeridoPais, setcampoRequeridoPais] = useState(false);
  const [campoRequeridoProvincia, setcampoRequeridoProvincia] = useState(false);
  const [campoRequeridoPrestamo, setcampoRequeridoPrestamo] = useState(false);
  const [campoRequeridoPagoInteres, setcampoRequeridoPagoInteres] = useState(false);
  const [campoRequeridoCicloPagoInteres, setcampoRequeridoCicloPagoInteres] = useState(false);

  const [PaisesCatalogoSelecte, setPaisesCatalogoSelecte] = useState(undefined);
  const [PaisesCatalogo, setPaisesCatalogo] = useState(null);

  const [ProvinceCatalogoSelecte, setProvinceCatalogoSelecte] = useState(undefined);
  const [ProvinceCatalogo, setProvinceCatalogo] = useState(null);

  const [ProductosCatalogoSelecte, setProductosCatalogoSelecte] = useState(undefined);
  const [ProductosCatalogo, setProductosCatalogo] = useState(null);

  const [actividadEmpresa, setActividadEmpresa] = useState([]);
  const [actividadEmpresaSelected, setActividadEmpresaSelected] = useState(undefined);

  const [subactividadEmpresa, setSubActividadEmpresa] = useState([]);
  const [subactividadEmpresaSelected, setSubActividadEmpresaSelected] = useState(undefined);

  const [SubcursalCatalogoSelecte, setSubcursalCatalogoSelecte] = useState(undefined);
  const [SubcursalCatalogo, setSubcursalCatalogo] = useState(null);

  const [PrestamoCatalogoSelecte, setPrestamoCatalogoSelecte] = useState(undefined);
  const [PrestamoCatalogo, setPrestamoCatalogo] = useState(null);

  const [PagoInteresCatalogoSelecte, setPagoInteresCatalogoSelecte] = useState(undefined);
  const [PagoInteresCatalogo, setPagoInteresCatalogo] = useState(null);

  const [CicloPagoInteresCatalogoSelecte, setCicloPagoInteresCatalogoSelecte] = useState(undefined);
  const [CicloPagoInteresCatalogo, setCicloPagoInteresCatalogo] = useState(null);

  const [cambio, setCambio] = useState(2);

  const [campoRequeridoIdentificacion, setcampoRequeridoIdentificacion] = useState(false);

  const [campoRequeridoActividad, setcampoRequeridoActividad] = useState(false);
  const [campoRequeridoSubActividad, setcampoRequeridoSubActividad] = useState(false);



  React.useEffect(() => {
    LoadProducto();
    LoadDataPais();
    LoadSubcursales();
    LoadPrestamos();
    LoadPagoInteres();
    LoadCicloPagoInteres();
    cargarActividadEconomica();

    props.setErrorValidate({
      campoRequeridoSubcursal,
      campoRequeridoSubproducto,
      campoRequeridoPais,
      campoRequeridoProvincia,
      campoRequeridoPrestamo,
      campoRequeridoPagoInteres,
      campoRequeridoCicloPagoInteres,
    });
  }, []);

  // React.useEffect(() => {
  //   // Read Api Service data
  //   try {
  //     var defaultVal = null;
  //     setProvinceCatalogoSelecte(undefined)
  //     if (ProvinceCatalogo?.length > 0 && ProvinceCatalogoSelecte === undefined) {
  //       defaultVal = ProvinceCatalogo.find(x => x.value === "EC04");
  //       if (defaultVal !== undefined) {
  //         setProvinceCatalogoSelecte(defaultVal);
  //       }
  //     }
  //   }
  //   catch (err) { console.log(err); }
  // }, [PaisesCatalogo]);

  // React.useEffect(() => {
  //   // Read Api Service data
  //   try {
  //     var defaultVal = null;
  //     setProductosCatalogoSelecte(undefined)
  //     if (ProductosCatalogo?.length > 0 && ProductosCatalogoSelecte === undefined) {
  //       defaultVal = ProductosCatalogo.find(x => x.value === "001");
  //       if (defaultVal !== undefined) {
  //         setProductosCatalogoSelecte(defaultVal);
  //       }
  //     }
  //   }
  //   catch (err) { console.log(err); }
  // }, [ProductosCatalogo]);

  // React.useEffect(() => {
  //   // Read Api Service data
  //   try {
  //     var defaultVal = null;
  //     setSubcursalCatalogoSelecte(undefined)
  //     if (SubcursalCatalogo?.length > 0 && SubcursalCatalogoSelecte === undefined) {
  //       defaultVal = SubcursalCatalogo.find(x => x.value === "001");
  //       if (defaultVal !== undefined) {
  //         setSubcursalCatalogoSelecte(defaultVal);
  //       }
  //     }
  //   }
  //   catch (err) { console.log(err); }
  // }, [SubcursalCatalogo]);


  // React.useEffect(() => {
  //   // Read Api Service data
  //   var defaultVal = null;
  //   try {

  //     if (actividadEmpresa?.length > 0 && props.economicActivity !== null && actividadEmpresaSelected === undefined) {
  //       defaultVal = actividadEmpresa.find(x => x.value === "A");
  //       if (defaultVal !== undefined) {
  //         setActividadEmpresaSelected(defaultVal);
  //       }
  //     }

  //   }
  //   catch (err) { }
  // }, [cambio]);


  function cargarActividadEconomica() {
    cargarSubActividadEconomica("A");
    const api = new CoreServices();
    api.getActividadEconomicaCatalogo().then(data => {

      let Setter = {
        SetData: "A",
        data: data.Records,
        SetGlobalCat: setActividadEmpresa,
        SetUseSelected: setActividadEmpresaSelected,
        useSelected: actividadEmpresaSelected,
        structure: { description: "Description", id: "Code" }
      }

      SetterUseStateSelect(Setter)

      setCambio(null);
      setCambio(5);
    })
  }
  function cargarSubActividadEconomica(codActividad) {
    const api = new CoreServices();
    api.getSubActividadEconomicaCatalogo(codActividad).then(data => {

      let Setter = {
        SetData: "3001",
        data: data.Records,
        SetGlobalCat: setSubActividadEmpresa,
        SetUseSelected: setSubActividadEmpresaSelected,
        useSelected: subactividadEmpresaSelected,
        structure: { description: "Description", id: "Code" }
      }

      SetterUseStateSelect(Setter)



      setCambio(null);
      setCambio(5);
    })
  }

  function changeAll(e, tipo) {

    if (tipo == "actividadEconomica") {
      // props.economicActivity.code = e.value;
      // props.economicActivity.name = e.label;

      props.seteconomicActivity(e.label);

      props.setsubeconomicActivity(null);
      cargarSubActividadEconomica(e.value)
    }
    if (tipo == "subactividadEconomica") {
      // props.subeconomicActivity.code = e.value;
      // props.subeconomicActivity.name = e.label;

      props.setsubeconomicActivity(e.label);
    }

    props.setErrorValidate({
      campoRequeridoSubcursal,
      campoRequeridoSubproducto,
      campoRequeridoPais,
      campoRequeridoProvincia,
      campoRequeridoPrestamo,
      campoRequeridoPagoInteres,
      campoRequeridoCicloPagoInteres,
    });

  }

  async function handleSubmit(event, errors, values) {
    event.preventDefaulprops.t();
    if (errors.length > 0) {
      return;
    }

    //props.onSubmiprops.t(values);
  }


  function LoadDataPais() {
    LoadProvince("EC");
    apiServiceCore.getPaisesCatalogo(dataLocation.transactionId).then(data => {

      let Setter = {
        SetData: "EC",
        data: data.Records,
        SetGlobalCat: setPaisesCatalogo,
        SetUseSelected: setPaisesCatalogoSelecte,
        useSelected: PaisesCatalogoSelecte,
        structure: { description: "Description", id: "Code" }
      }

      props.setPaisesCatalogo(SetterUseStateSelect(Setter).label);

    }).catch((error) => {
      console.error('api error: ', error);
    });
  }

  function LoadProvince(code) {
    apiServiceCore.getProvinciasCatalogo(code).then(data => {

      let Setter = {
        SetData: "EC04",
        data: data.Records,
        SetGlobalCat: setProvinceCatalogo,
        SetUseSelected: setProvinceCatalogoSelecte,
        useSelected: ProvinceCatalogoSelecte,
        structure: { description: "Description", id: "Code" }
      }

      props.setProvinceCatalogo(SetterUseStateSelect(Setter).label);

    }).catch((error) => {
      console.error('api error: ', error);
    });
  }

  function LoadProducto() {
    apiServiceCore.getSubProductosCatalogo().then(data => {

      let Setter = {
        SetData: "1000",
        data: data.Records,
        SetGlobalCat: setProductosCatalogo,
        SetUseSelected: setProductosCatalogoSelecte,
        useSelected: ProductosCatalogoSelecte,
        structure: { description: "Description", id: "Code" }
      }

      props.setProductosCatalogo(SetterUseStateSelect(Setter).label);

    }).catch((error) => {
      console.error('api error: ', error);
    });
  }

  function LoadSubcursales() {
    apiServiceCore.getSucursalesCatalogo().then(data => {

      let Setter = {
        SetData: "001",
        data: data.Records,
        SetGlobalCat: setSubcursalCatalogo,
        SetUseSelected: setSubcursalCatalogoSelecte,
        useSelected: SubcursalCatalogoSelecte,
        structure: { description: "Description", id: "Code" }
      }
      props.setSubcursalCatalogo(SetterUseStateSelect(Setter).label);

    }).catch((error) => {
      console.error('api error: ', error);
    });
  }


  function LoadPrestamos() {
    apiServiceBackend.retrieveLoanType().then(data => {

      let Setter = {
        SetData: "PRC",
        data: data,
        SetGlobalCat: setPrestamoCatalogo,
        SetUseSelected: setPrestamoCatalogoSelecte,
        useSelected: PrestamoCatalogoSelecte,
        structure: { description: "description", id: "id" }
      }

      props.setPrestamoCatalogo(SetterUseStateSelect(Setter).label);

    }).catch((error) => {
      console.error('api error: ', error);
    });
  }

  function LoadPagoInteres() {
    apiServiceBackend.retrieveInterestPaymentCycle().then(data => {
      let Setter = {
        SetData: "TR",
        data: data,
        SetGlobalCat: setPagoInteresCatalogo,
        SetUseSelected: setPagoInteresCatalogoSelecte,
        useSelected: PagoInteresCatalogoSelecte,
        structure: { description: "description", id: "id" }
      }

      props.setPagoInteresCatalogo(SetterUseStateSelect(Setter).label);

    }).catch((error) => {
      console.error('api error: ', error);
    });
  }

  function LoadCicloPagoInteres() {
    apiServiceBackend.retrieveCapitalPaymentCycle().then(data => {
      let Setter = {
        SetData: "SE",
        data: data,
        SetGlobalCat: setCicloPagoInteresCatalogo,
        SetUseSelected: setCicloPagoInteresCatalogoSelecte,
        useSelected: CicloPagoInteresCatalogoSelecte,
        structure: { description: "description", id: "id" }
      }

      props.setCicloPagoInteresCatalogo(SetterUseStateSelect(Setter).label);

    }).catch((error) => {
      console.error('api error: ', error);
    });
  }

  function SetterUseStateSelect({ SetData, data, SetGlobalCat, SetUseSelected, useSelected, structure }) {
    let json = data?.map($$ => ({ label: t($$[structure.description]), value: $$[structure.id] }));
    SetGlobalCat(json)
    try {
      var defaultVal = null;
      SetUseSelected(undefined)
      if (json?.length > 0 && useSelected === undefined) {
        defaultVal = json.find(x => x.value === SetData);
        if (defaultVal !== undefined) {
          SetUseSelected(defaultVal);
        }
        return defaultVal;
      }
    }
    catch (err) { console.log(err); }
  }


  return (

    <React.Fragment>

      <Row>
        <Col md="12">
          <h4 className="card-title">{props.t("GeneralData")}</h4>


          <Row>
            <Col md="3">
              <div className="mb-3">
                <Label htmlFor="branch">{props.t("branchoffice")}</Label>

                <Select
                  onChange={(e) => {
                    setSubcursalCatalogoSelecte(SubcursalCatalogo.find(x => x.label === e.label));
                    props.setSubcursalCatalogo(e.label);
                    setcampoRequeridoSubcursal(true);
                  }}
                  options={SubcursalCatalogo}
                  placeholder={t("SelectType")}
                  value={SubcursalCatalogoSelecte}
                />
                {campoRequeridoSubcursal ?
                  <p className="message-error-parrafo">{t("Required Field")}</p>
                  : null}
              </div>
            </Col>
            <Col md="3">
              <div className="mb-3">
                <Label htmlFor="loan">{props.t("Loannumber")}</Label>
                <AvField
                  className="form-control"
                  name="loan"
                  type="text"
                  id="loan"
                  validation={{ required: true }}
                  value={props.DataGeneral?.loan ?? ''} />
              </div>
            </Col>
          </Row>
          <Row>
            <Col md="3">
              <div className="mb-3">
                <Label htmlFor="product">{props.t("product")}</Label>
                <AvField
                  className="form-control"
                  name="product"
                  type="select"
                  id="product"
                  value={props.DataGeneral?.product ?? ''}
                  validation={{ required: true }}>
                  <option value="3200">{props.t("CommercialType")}</option>

                </AvField>
              </div>
            </Col>
            <Col md="3">
              <div className="mb-3">
                <Label htmlFor="subproduct">{props.t("subproduct")}</Label>

                <Select
                  onChange={(e) => {
                    setProductosCatalogoSelecte(ProductosCatalogo.find(x => x.label === e.label));
                    props.setProductosCatalogo(e.label);
                    setcampoRequeridoSubproducto(true)
                  }}
                  options={ProductosCatalogo}
                  placeholder={t("SelectType")}
                  value={ProductosCatalogoSelecte}
                />
                {campoRequeridoSubproducto ?
                  <p className="message-error-parrafo">{t("Required Field")}</p>
                  : null}

              </div>
            </Col>
            <Col md="3">
              <div className="mb-3">
                <Label htmlFor="field-5">{props.t("LoanType")}</Label>                 {/**NoItem*/}
                <Select
                  onChange={(e) => {
                    setPrestamoCatalogoSelecte(PrestamoCatalogo.find(x => x.label === e.label));
                    props.setPrestamoCatalogo(e.label);
                    setcampoRequeridoPrestamo(true)
                  }}
                  options={PrestamoCatalogo}
                  placeholder={t("SelectType")}
                  value={PrestamoCatalogoSelecte}
                />
                {campoRequeridoPrestamo ?
                  <p className="message-error-parrafo">{t("Required Field")}</p>
                  : null}
              </div>
            </Col>

          </Row>
          <Row>
            <Col md="3">
              <div className="mb-3">
                <Label htmlFor="line">{props.t("linenumber")}</Label>
                <AvField
                  className="form-control"
                  name="line"
                  type="text"
                  id="line"
                  validation={{ required: true }}
                  value={props.DataGeneral?.line ?? ''} />
              </div>
            </Col>
            <Col md="3">
              <div className="mb-3">
                <Label htmlFor="masterLine">{props.t("MasterLineNumber")}</Label>
                <AvField
                  className="form-control"
                  name="masterLine"
                  type="text"
                  id="masterLine"
                  validation={{ required: true }}
                  value={props.DataGeneral?.masterLine ?? ''} />
              </div>
            </Col>
            <Col md="3">
              <div className="mb-3">
                <Label htmlFor="customerLine">{props.t("MasterClient")}</Label>
                <AvField
                  className="form-control"
                  name="customerLine"
                  type="text"
                  id="customerLine"
