              </div>
            </Col>

          </Row>
          <Row>
            <Col md="12">
              <div className="mb-3">
                <Label htmlFor="field50">{props.t("Specialinstructions")}</Label>
                <AvField
                  className="form-control"
                  name="field50"
                  validation={{ required: true }}
                  type="textarea" rows={7}
                  id="field50" value="" />
              </div>
            </Col>
          </Row>


        </Col>
      </Row >


    </React.Fragment >


  );

}

export default (withTranslation()(DatosGenerales))
