import React, { useState, useRef, useEffect } from "react"
import { translationHelpers } from "../../../helpers";
import { useLocation, useHistory } from 'react-router-dom'
import * as OPTs from "../../../helpers/options_helper"
import * as url from "../../../helpers/url_helper"
import moment from "moment";
import classnames from "classnames"
import { CreditProposalProvider } from '../../../components/PropuestaCreditoComercial/CreditProposalContext';
import {
  Row,
  Col,
  Button,
  Card,
  CardBody,
  CardFooter,
  TabPane,
  Nav,
  NavLink,
  TabContent
} from "reactstrap"

import {
  CorporateExposure, CorporateExposureClient,
  FacilityList, FormCreditProposal,
  Refinancing
} from "../../../components/PropuestaCreditoComercial";
import LoadingOverlay from "react-loading-overlay";


// UI Components
import Breadcrumbs from "../../../components/Common/Breadcrumb"
import LogProcess from "../../../components/LogProcess";
import LoadingIndicator from "../../../components/UI/LoadingIndicator";
import SuccessMessage from "../../../components/UI/SuccessMessage";
import ModalCloseOptions from "../../../components/UI/ModalCloseOptions";
import WizardSidebar from "../../../components/UI/WizardSidebar";

// Services
import { BackendServices, CoreServices, BpmServices, } from "../../../services";
import SweetAlert from "react-bootstrap-sweetalert";
import {
  ServicioExposicionCorporativa,
  ServicioResumenCambios,
  ServicioFacilidades,
  ServicioRefinanciamiento,
  ServicioChecklist,
  ServicioPropuestaCredito,
} from "../../../services/PropuestaCredito";
import DatosGeneralesPropuesta from "./Secciones/DatosGenerales";
import { useTranslation } from "react-i18next/";
import ListaFacilidad from "./Secciones/FacilidadesSeccion";
import ExposicionCorportativa from "./Secciones/ExposicionCorporativa";
import ExposicionCorporativaClientes from "./Secciones/ExposicionCorporativaCliente";
import CheckList from "./Secciones/CheckList";
import ModalPrevisualizarPropCred from "./previsualizarPropCred";
import ChangeSummary from "./Secciones/ResumenCambios";

const CreditProposalPage = (props) => {

  const location = useLocation();
  const history = useHistory();
  // modelo.datosGenerales.transactId=location.data.transactionId;

  //Data que recibimos en el location
  const [locationData, setLocationData] = useState(location.data);
  const [usuarioProspecto, setusuarioProspecto] = useState(null);
  const [dataGlobal, setdataGlobal] = useState({
    requestId: null,
  });

  const [loading, setLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [changeSummaryItems, setChangeSummaryItems] = useState([]);
  const [facilityListItems, setFacilityListItems] = useState([]);
  const [identificationList, setIdentificationList] = useState([]);
  const [refinancing, setRefinancing] = useState([]);
  const [documentAuthors, setDocumentAuthors] = useState(null);
  const [checkListItems, setCheckListItems] = useState([]);
  const [currentTab, setCurrentTab] = useState(0);
  const [displayModalCloseOptions, setDisplayModalCloseOptions] = useState(false);
  const [displayModalEndProcess, setDisplayModalEndProcess] = useState(false);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [context, setContext] = useState({});
  const [datosGenerales, setDatosGenerales] = useState({});
  const [activeTab, setactiveTab] = useState(1);
  const [datosUsuario, setdatosUsuario] = useState(null);
  const [isActiveLoading, setIsActiveLoading] = useState(false); //loading de la pagina
  const [validarDatos, setvalidarDatos] = useState(false); //loading de la pagina
  // Services
  const [ModalPrevisualizardata, settoogleModalPrevisualizar] = useState(false);

  const coreServices = new CoreServices();
  const backendServices = new BackendServices();
  const bpmServices = new BpmServices();
  
  const servicioPropuestaCredito = new ServicioPropuestaCredito();
  const servicioExposicionCorporativa = new ServicioExposicionCorporativa();
  const servicioResumenCambios = new ServicioResumenCambios();
  const servicioRefinanciamiento = new ServicioRefinanciamiento();
  const servicioChecklist = new ServicioChecklist();

  // Component Refs
  const wizardRef = useRef();
  const creditProposalRef = useRef();
  const FacilidadesRef = useRef();
  const ResumenCambiosRef = useRef();

  const { t, i18n } = useTranslation();

  // const sections = [
  //   t('General Data'),
  //   t('Corporate Exposure'),
  //   t('Change Summary'),
  //   t('Facilities'),
  //   t('Checklist')
  // ];

  //On Mounting (componentDidMount)
  useEffect(() => {
    setLocationData(location.data);//guardamos los datos que se pasan como parametros
    initializeData(location.data);
    // update context
    setContext({
      transactionId: location.data.transactionId,
      customerId: location.data.customerId,
      instanceId: location.data.instanceId,
      requestId: location.data.requestId, // debe venir del resultado de guardar datos generales
    });
  }, []);
  useEffect(() => {
    if(dataGlobal.requestId!=null){ 
      setvalidarDatos(true)
    }else{
      setvalidarDatos(false)
    }
  }, [props,dataGlobal]);

  //Caraga Inicial
  function initializeData(params) {
    const { transactionId, requestId, customerId } = params;
    console.log(params);
    cargarUsuarioProspecto(params.transactionId);
    cargarDatosGenerales(params);
    cargarIdentificaciones();
    cargarListaFacilidades(customerId);
    fetchReport(transactionId);
  }
  function cargarUsuarioProspecto(transactionId) {
    backendServices.consultarUsuarioProspecto(transactionId)
      .then((data) => {
        if (data !== undefined) {
          setusuarioProspecto(data);
        }
      });
  }
  function cargarDatosGenerales(params) {
    backendServices.consultarDatosGeneralesPropCred(params.transactionId)
      .then((data) => {
        if (data !== undefined) {
          var datosgenerales = data.find(record => record.requestId === params.requestId)
          if (datosgenerales === undefined || datosgenerales !== null) {
            datosgenerales = data[data.length - 1];
          }
          setdataGlobal({})
          dataGlobal.requestId = datosgenerales.requestId
          setdataGlobal(dataGlobal)
          setDatosGenerales(datosgenerales);
        }
        else {
          nuevosDatosGenerales(params);
        }
      });
  }
  function nuevosDatosGenerales(params) {
    var data = {
      "transactId": params.transactionId,
      "customerNumber": usuarioProspecto?.customerNumberT24 ? usuarioProspecto?.customerNumberT24 : "1",
      "revision": "",
      "preapproval": true,
      "economicGroupNumber": "",
      "economicGroupName": "",
      "country": "",
      "activityCompany": "",
      "riskClassification": "",
      "minimumProvisionSIB": 0,
      "relatedPart": true,
      "approvalLevel": "",
      "accountExecutive": "",
      "responsibleUnit": "",
      "countryRisk": "",
      "requestDate": moment().format("YYYY-MM-DD"),
      "lastRequestDate": moment().format("YYYY-MM-DD"),
      "nextRevisionDate": moment().format("YYYY-MM-DD"),
      "proposedExpirationDate": moment().format("YYYY-MM-DD"),
      "flow": ""
    };
    backendServices.nuevoDatosGeneralesPropCred(data).then((data) => {
      if (data !== undefined) {

        //Actualizar variables del Proceso
        var values = {
          "processId": OPTs.PROCESS_PROPUESTACREDITO.toString(),
          "activityId": OPTs.ACT_DATOSGENERALES.toString(),
          "transactionId": locationData.transactId.toString(),
          "customerId": locationData.customerId.toString(),
          "requestId": data.requestId,
          //"decision":"no"          
        };
        bpmServices.updatevariables(locationData.instanceId, values)
          .then((data) => {});




        setdataGlobal({})
        //setDatosGenerales(data);
        //Setear en variables de BPMN el requestId
        dataGlobal.requestId = data.requestId;
        setdataGlobal(dataGlobal)
        console.log("nuevoDatosGeneralesPropCred", data);
        cargarDatosGenerales(data.requestId);
      }
    });
  }
  function cargarListaFacilidades(idcliente) {
    coreServices.getFacilidades("600133774")
      .then(resp => {
        setFacilityListItems(resp);
      })
      .catch(err => {
        console.log(err);
      });
  }
  function cargarIdentificaciones() {
    backendServices.consultarCatalogoTipoIdentificacion()
      .then((data) => {
        if (data !== null && data !== undefined) {
          setIdentificationList(data);
        }
      })
      .catch((error) => {
        console.error('api error: ', error);
      });
  }
  function fetchReport(transactionId) {

    servicioResumenCambios.consultarResumenCambios(transactionId)
      .then((results) => {
        setChangeSummaryItems(results);
      });
    servicioRefinanciamiento.consultarBitacoraRefinanciamiento(transactionId)
      .then((results) => {
        setRefinancing(results);
      });
    servicioChecklist.consultarListaCheckList(transactionId)
      .then((results) => {
        setCheckListItems(results);
      });
    setDocumentAuthors({
      owner: { name: 'Carolina Delgado' },
      ceo: { name: 'Carolina Delgado' }
    });
  }
  function handleSaveFacility(newItem) {
    const newFacilities = [...facilityListItems, newItem];
    setFacilityListItems(newFacilities);
  }
  function toogleModalPrevisualizar() {
    settoogleModalPrevisualizar(!ModalPrevisualizardata);
    removeBodyCss()
  }
  function handleSaveCheckListItem(newItem) {
    const newCheckListItems = [...checkListItems, newItem];
    setCheckListItems(newCheckListItems);
  }
  //Modal Opciones al Salir
  function toggleModalCloseOptions() {
    setDisplayModalCloseOptions(!displayModalCloseOptions);
    removeBodyCss();
  }
  //Salvando el Proceso y le pasamos una opcion: 1-Finalizar Proceso, 2- Salvarlo, 3- A Cumplimmineto, 4- Avanzar a IGR
  function onSaveProcess(option) {
    switch (option) {
      case OPTs.OPT_FINALIZARPROCESO: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                     Procedemos a llamar al servicio de finalizar el proceso                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        console.log(location.data.instanceId);
        let data = location.data.instanceId;
        api.abortProcess(data).then((resp) => {
          if (resp !== undefined) {
            setsuccessSave_dlg(true);
          }
          else {
            //Mensaje ERROR
            seterror_msg(t("TheProcessCouldNotFinish"));
            seterror_dlg(true);
          }

        });
        toggleModalEndProcess();
        // navigate to dashboard
        history.push(url.URL_DASHBOARD);
        return;
      }
      case OPTs.OPT_SALVAPARCIAL: {
        submitData();
        wizardRef.current.next();
        break;
      }
      case OPTs.OPT_SALVAFULL: {
       var result = submitData();
       console.log("onSaveProcess",result);
        break;
      }
      default: {
        // do nothing
        return;
      }
    }
  }

  function saveJBPMProcess(option) {
    console.log("saveJBPMProcess", option, locationData);
    const apiBpmServices = new BpmServices();
    switch (option) {
      case OPTs.OPT_FINALIZARPROCESO: {
        apiBpmServices.abortProcess(locationData.instanceId)
          .then((data) => {
            if (data !== undefined) {
              history.push(url.URL_DASHBOARD);
            }
            else {
              //Mensaje ERROR
              seterror_msg(props.t("TheProcessCouldNotFinish"))
              seterror_dlg(true)
            }
          });
        break;
      }
      case OPTs.OPT_SALVAPARCIAL: {
        var values = {
          "processId": OPTs.PROCESS_PROPUESTACREDITO.toString(),
          "activityId": OPTs.ACT_DATOSGENERALES.toString(),
          "transactionId": locationData.transactId.toString(),
          "customerId": locationData.customerId.toString(),
          "applicationNumber": "",
          "procedureNumber": "",
          //"decision":"no"          
        };
        apiBpmServices.updatevariables(locationData.instanceId, values)
          .then((data) => {
            if (data !== undefined) {
              setsuccessSave_dlg(true);
            }
            else {
              //Mensaje ERROR
              seterror_msg(props.t("ErrorSaveMessage"))
              seterror_dlg(true)
            }
          });
        break;
      }
      case OPTs.OPT_ENVIARPROPUESTACREDITO: {

        var values = {
          "processId": OPTs.PROCESS_PROPUESTACREDITO.toString(),
          "activityId": OPTs.ACT_NONE.toString(),
          "transactionId": locationData.transactionId,
          "customerId": locationData.customerId,
          "applicationNumber": "",
          "procedureNumber": "",
          "decision": "si"
        };
        console.log("saveJBPMProcess value", values, locationData);
        apiBpmServices.updatevariables(locationData.instanceId, values)
          .then((data) => {
            if (data !== undefined) {
              setsuccessSave_dlg(true);
              history.push(url.URL_DASHBOARD);
            }
            else {
              //Mensaje ERROR
              seterror_msg(props.t("ErrorSaveMessage"))
              seterror_dlg(true)
            }
          });

        break;
      }
    }
  }

  //Modal Para mostrar Comentario de Fin de Proceso
  function toggleModalEndProcess() {
    setDisplayModalEndProcess(!displayModalEndProcess);
  }
  function guardarDatos() {
    const apiBack = new BackendServices();
    if (activeTab == 1) {
      if (!creditProposalRef.current.getFormValidation()) {
        //mandamos a validar el formulario
        creditProposalRef.current.validateForm();
        //nos movemos al tab para llenar los campos
        setIsActiveLoading(false);
        return false;
      }
      apiBack.guardarSeccionDatosGeneralesPropCred(creditProposalRef.current.dataReturn).then((resp) => {
        if (resp != undefined) {
          toggleTab(activeTab + 1);
        } else {
          seterror_dlg(true);
        }
        setIsActiveLoading(false);
      })
    } else if (activeTab == 2) {
      setIsActiveLoading(false);
      toggleTab(activeTab + 1);
    } else if (activeTab == 3) {
      if (!ResumenCambiosRef.current.getFormValidation()) {
        //mandamos a validar el formulario
        ResumenCambiosRef.current.validateForm();
        //nos movemos al tab para llenar los campos
        setIsActiveLoading(false);
        return false;
      }
      apiBack.guardarResumenCambios(ResumenCambiosRef.current.dataReturn).then((resp) => {
        if (resp != undefined) {
          toggleTab(activeTab + 1);
        } else {
          seterror_dlg(true);
        }
        setIsActiveLoading(false);
      })
    } else if (activeTab == 4) {
      apiBack.consultarFacilidades(dataGlobal.requestId).then(resp => {
        if (resp.length > 0) {
          toggleTab(activeTab + 1);
        }
        setIsActiveLoading(false);
      })

    } else {
      setIsActiveLoading(false);
      toggleTab(activeTab + 1);
    }
  }
  function submitData(validate = false) {
    console.log("1");
    if (!creditProposalRef.current.getFormValidation()) {
      console.log("2");
      //mandamos a validar el formulario
      creditProposalRef.current.validateForm();
      //nos movemos al tab para llenar los campos
      setIsActiveLoading(false);
      toggleTab(1);
      return false;
    }
    const apiBack = new BackendServices();    
    apiBack.consultarFacilidades(dataGlobal.requestId).then(resp => {
      console.log("4");
      console.log(resp);
      if (resp.length == 0) {
        toggleTab(4);
        setIsActiveLoading(false);
        return false;
      }
    })
    // Aqui pasamos a actualizar la variable
    
    return true;
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }
  function toggleTab(tab) {
    if (activeTab !== tab) {
      if (tab >= 1 && tab <= 5) {
        setactiveTab(tab)
        window.scrollTo(0, 0)
      }
    }
  }
  return (
    <React.Fragment>
      <CreditProposalProvider value={context}>
        <div className="page-content">
          <Breadcrumbs title={t("Commercial Credit")} breadcrumbItem={t("Credit Proposal")} />
          <Card>
            <CardBody>

              <h4 className="card-title">{usuarioProspecto != null ? usuarioProspecto.firstName + " " + usuarioProspecto.secondName + " " + usuarioProspecto.firstLastName + " " + usuarioProspecto.secondLastName : ""}    #{locationData.transactionId}</h4>

              <h4 className="card-title">{t("Credit Proposal")}</h4>
              <p className="card-title-desc">{t("This form is official and must be filled out correctly")}</p>
              <Row>
                <Col md={3}>
                  <Nav pills className="flex-column">
                    <NavLink className={classnames({ active: activeTab === 1 })}
                      onClick={() => {
                        toggleTab(1)
                      }}>
                      {t("General Data")}
                    </NavLink>
                    <NavLink className={classnames({ active: activeTab === 2 })}
                      onClick={() => {
                        toggleTab(2)
                      }}>
                      {t("CorporateExhibition")}
                    </NavLink>
                    <NavLink className={classnames({ active: activeTab === 3 })}
                      onClick={() => {
                        toggleTab(3)
                      }}>
                      {t("Change Summary")}
                    </NavLink>
                    <NavLink className={classnames({ active: activeTab === 4 })}
                      onClick={() => {
                        toggleTab(4)
                      }}>
                      {t("FacilitiesList")}
                    </NavLink>
                    <NavLink className={classnames({ active: activeTab === 5 })}
                      onClick={() => {
                        toggleTab(5)
                      }}>
                      {t("Check List")}
                    </NavLink>
                  </Nav>
                </Col>
                <Col md={9}>
                  <LoadingOverlay active={isActiveLoading} spinner text={t("Processinginformation")}>
                    <TabContent activeTab={activeTab} className="body">
                      <TabPane tabId={1}>
                        <DatosGeneralesPropuesta dataGlobal={dataGlobal} ref={creditProposalRef}
                          title={t("General Data")}
                          values={datosGenerales} />
                      </TabPane>
                      <TabPane tabId={2}>
                        <ExposicionCorportativa title={t("CorporateExhibition")} customerNumberT24={"600133774"} />
                        <ExposicionCorporativaClientes title={t("CorporateExhibition")} customerNumberT24={"600133774"} />
                      </TabPane>
                      <TabPane tabId={3}>
                        <ChangeSummary dataGlobal={dataGlobal} ref={ResumenCambiosRef} title={t("Change Summary")} items={changeSummaryItems} />
                      </TabPane>
                      <TabPane tabId={4}>
                        {validarDatos?
                          <ListaFacilidad title={t("Facility List")}
                            items={facilityListItems}
                            editMode={true}
                            ref={FacilidadesRef}
                            dataGlobal={dataGlobal}
                            onSaveFacility={handleSaveFacility}
                            identificationList={identificationList} /> : null}
                      </TabPane>
                      <TabPane tabId={5}>
                        <CheckList title={t("Check List")} items={checkListItems}
                          onSaveItem={handleSaveCheckListItem} />
                      </TabPane>
                    </TabContent>
                  </LoadingOverlay>
                </Col>
              </Row>
            </CardBody>
            <CardFooter className="text-end">
              <Button color="info" type="button" style={{ margin: '5px' }} onClick={() => { toogleModalPrevisualizar() }}><i className="mdi mdi-eye mdi-12px"></i> Previsualizar</Button>

              <Button color="dark" type="button" style={{ margin: '5px' }}
                onClick={() => { toggleModalCloseOptions() }}>
                <i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i>&nbsp;
                {t("Exit")}
              </Button>
              {activeTab > 1 && (
                <Button color="success" type="button" style={{ margin: '5px' }} onClick={() => { wizardRef.current.prev() }}>
                  <i className="mdi mdi-arrow-left-bold mid-12px"></i>&nbsp;
                  {t("Previous")}
                </Button>)
              }
              {activeTab < 5 && (
                <Button color="success" type="button" style={{ margin: '5px' }} onClick={() => {
                  guardarDatos(); setIsActiveLoading(true); setTimeout(() => {
                    guardarDatos();
                  }, 1000);
                }}>
                  {t("Next")}&nbsp;
                  <i className="mdi mdi-arrow-right-bold mid-12px"></i>
                </Button>)
              }
              <Button color="success" type="button" style={{ marginLeft: '5px' }} onClick={() => { onSaveProcess(OPTs.OPT_SALVAFULL);setIsActiveLoading(true);  setTimeout(() => {
                onSaveProcess(OPTs.OPT_SALVAFULL);
              }, 1000); }}>
                <i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i>&nbsp;
                {t("Continue")}
              </Button>
            </CardFooter>
          </Card>
          <LogProcess />
          {successSave_dlg ? (
            <SweetAlert
              success
              title={t("SuccessDialog")}
              onConfirm={() => {
                setsuccessSave_dlg(false)
              }}
            >
              {t("SuccessSaveMessage")}
            </SweetAlert>
          ) : null}
          {error_dlg ? (
            <SweetAlert
              error
              title={t("ErrorDialog")}
              onConfirm={() => {
                seterror_dlg(false)
              }}
            >
              {error_msg}
            </SweetAlert>
          ) : null}
        </div>
        {loading && (
          <LoadingIndicator message={t("Please Wait")} />
        )}
        {showSuccess && (
          <SuccessMessage title={t("Operation Complete")} message={t("Credit Proposal Saved")} />
        )}
        <ModalPrevisualizarPropCred isOpen={ModalPrevisualizardata} toggle={() => { toogleModalPrevisualizar() }} />

        <ModalCloseOptions onSaveProcess={onSaveProcess} isOpen={displayModalCloseOptions} toggle={() => { toggleModalCloseOptions() }} />
      </CreditProposalProvider>
    </React.Fragment >
  );

}

export default CreditProposalPage;
