import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import AvInput from 'availity-reactstrap-validation/lib/AvInput'

import ModalGarantes from "./ModalGarantes"
import SweetAlert from "react-bootstrap-sweetalert"
import { BackendServices } from "../../../../../services"


const InformacionGarante = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const location = useLocation()
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [formValid, setFormValid] = useState(false);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dataDelete, setDataDelete] = useState([]);
  const [dataFixedGarantesRows, setdataFixedGarantesRows] = useState(null)
  const [tipo, settipo] = useState("")
  const [jsonGarante, setjsonGarante] = useState({
    "transactId": 0,
    "id": 0,
    "type": false,
    "typeId": "",
    "docIdCustomer": "",
    "customerNumberT24": "",
    "firstName": "",
    "secondName": "",
    "firstLastName": "",
    "secondLastName": "",
    "nationality": "",
    "birthDate": "2022-03-01",
    "relationship": null,
    "fundsAssets": null,
    "debtorCommitment": null,
    "guaranteeReason": null
  });
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmProveedores');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }
  }));
  const [showModalGarantes, setShowModalGarantes] = useState(false);

  const dataFixedGarantes = [
    { id: 1, idtype: 'RUC', idnumber: '321321', fullname: 'Empresas Carbone, S.A' },
    { id: 2, idtype: 'RUC', idnumber: '654654', fullname: 'Empresas Carbone, S.A' },
    { id: 3, idtype: 'Cedula', idnumber: '987654321', fullname: 'Pedro Rojas Fuentes' },
    { id: 4, idtype: 'Pasaporte', idnumber: '321654987', fullname: 'Aroldis Chapman' },
  ];
  React.useEffect(() => {
    // Read Api Servi
    initializeData();
  }, []);
  function initializeData() {
    const api = new BackendServices();
    api.consultarGaranteBD(dataLocation.transactionId).then(resp => {
      if (resp.length > 0) {
        setdataFixedGarantesRows(resp.map((data) => (
          <tr key={data.id}>
            <td>{data.typeId}</td>
            <td>{data.docIdCustomer}</td>
            <td>{data.firstName + " " + data.secondName + " " + data.firstLastName + " " + data.secondLastName}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { actualizarGarante(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              {/* <Button type="button" color="link" onClick={(resp) => { EliminarGarante(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button> */}
            </td>
          </tr>)
        ));
      } else {
        setdataFixedGarantesRows(
          <tr key={1}>
            <td colSpan="7" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
  }
  function verGarante(data) {
    setjsonGarante(data)
    setbotonValidation(false);
    toggleShowModalGarantes()
  }
  function actualizarGarante(data) {
    settipo("editar");
    setjsonGarante(data)
    setbotonValidation(true);
    toggleShowModalGarantes()
  }
  function EliminarGarante(data) {
    setDataDelete(data)
    setconfirm_alert(true);
  }
  function addGarante(data, tipo2) {
    let datos = jsonGarante;
    datos.transactId = Number(dataLocation.transactionId);
    datos.type = data.type;
    datos.documentType = data.type;
    datos.relationship = data.relationship;
    datos.fundsAssets = data.fundsAssets;
    datos.debtorCommitment = data.debtorCommitment;
    datos.guaranteeReason = data.guaranteeReason;
    const dataSet = {
      "transactId": Number(dataLocation.transactionId),
      "id": jsonGarante.id,
      "relationship": data.relationship,
      "fundsAssets": data.fundsAssets,
      "debtorCommitment": data.debtorCommitment,
      "guaranteeReason": data.guaranteeReason,
      "isGuarantor": data.isGuarantor,
      "status": true
    }

    if (tipo2 == "guardar") {
      const apiBack = new BackendServices();
      apiBack.nuevoGarante(datos).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModalGarantes();
        } else {
          toggleShowModalGarantes();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    } else {
      const apiBack = new BackendServices();
      datos.guarantorId = jsonGarante.id;
      apiBack.actualizarGaranteBD(dataSet).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModalGarantes();
        } else {
          toggleShowModalGarantes();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    }
  }
  //abrimos modal para adjunar archivos
  function toggleShowModalGarantes() {
    setShowModalGarantes(!showModalGarantes);
    removeBodyCss()
  }

  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }

  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    //props.onSubmit(values);
  }


  return (
    <React.Fragment>
      <h5>
        {t("GuarantorInformation")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>

        <Row>
          <Col lg="12">
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("ID Type")}</th>
                  <th>{t("ID Number")}</th>
                  <th>{t("FullName")}</th>
                  <th></th>
                  {/* <th><Link className="float-end" onClick={() => {
                    setbotonValidation(true); setjsonGarante({
                      "transactId": 0,
                      "id": 0,
                      "type": false,
                      "typeId": "",
                      "docIdCustomer": "",
                      "customerNumberT24": "",
                      "firstName": "",
                      "secondName": "",
                      "firstLastName": "",
                      "secondLastName": "",
                      "nationality": "",
                      "birthDate": "2022-03-01",
                      "relationship": null,
                      "fundsAssets": null,
                      "debtorCommitment": null,
                      "guaranteeReason": null
                    }); settipo("guardar"); toggleShowModalGarantes();
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link>
                  </th> */}
                </tr>
              </thead>
              <tbody>
                {dataFixedGarantesRows}
              </tbody>
            </Table>

          </Col>
        </Row>
      </AvForm>
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            initializeData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            initializeData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            apiBack.eliminarGarante({ transactId: dataLocation.transactionId, guarantorId: dataDelete.guarantorId }).then(resp => {
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                setsuccessSave_dlg(true)
              }
            }).catch(error => {
              setconfirm_alert(false)
              seterror_dlg(true)
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
      <ModalGarantes jsonGarante={jsonGarante} tipo={tipo} botones={botonValidation} addGarante={addGarante} isOpen={showModalGarantes} toggle={() => { toggleShowModalGarantes() }} />
    </React.Fragment>
  );

});

/*
FacilidadActivosFijos.propTypes = {
  onSubmit: PropTypes.func.isRequired,
}
*/

export default InformacionGarante;
