import PropTypes from 'prop-types';
// import Select from "react-select";

import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
  InputGroup,
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import { AsyncPaginate } from "react-select-async-paginate";
//Import Flatepicker
import "flatpickr/dist/themes/material_blue.css";
import Flatpickr from "react-flatpickr";
import { CoreServices } from "../../../../../services";
import 'antd/dist/antd.css';
// import './index.css';
import { Select } from 'antd';

const ModalDeudasCortoPlazo = (props) => {
  const { Option } = Select;
  const { t, i18n } = useTranslation();
  const [options, setBancos] = useState([]);
  const [codigoBanco, setCodigoBanco] = useState(null);
  const [date, setdate] = useState(null);
  const [expirationDate, setexpirationDate] = useState(null);
  const [campoRequeridoBanco, setcampoRequeridoBanco] = useState(false);

  React.useEffect(() => {
    // Read Api Service data
    cargarBanco()
    if (props.tipo == "ECP") {
      setCodigoBanco(props.jsonSow.bank)
      setdate(props.jsonSow.date)
      setexpirationDate(props.jsonSow.expirationDate)
    }
    setcampoRequeridoBanco(false);
  }, []);
  function changeAll(e, tipo) {
    if (tipo == "bank") {
      setCodigoBanco(e);
    }
    if (tipo == "date") {
      setdate(e.target.value);
    }
    if (tipo == "expirationDate") {
      setexpirationDate(e.target.value);
    }
  }
  // Submitimos formulario para busqueda de clientes
  function handleSubmit(event, errors, values) {
    if (codigoBanco == null) {
      setcampoRequeridoBanco(true);
      return;
    } else {
      setcampoRequeridoBanco(false);
    }
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    values.bank = codigoBanco;
    values.date = date;
    values.expirationDate = expirationDate;
    if (props.tipo == "CP") {
      props.guardarDatos(values, props.tipo);
    }
    if (props.tipo == "ECP") {
      values.debtId=props.jsonSow.debtId;
      values.status=true;
      props.actualizarDatos(values, props.tipo);
    }
  }
  function cargarBanco() {
    const api = new CoreServices();
    api.getBancosCatalogo()
      .then(response => {

        if(response===null){ return;}

        let json = [];
        for (let i = 0; i < response.Records.length; i++) {
          json.push({ label: response.Records[i]["Description"], value: response.Records[i]["Code"] })
        }
        const optionGroup1 = [
          {
            label: t("Bank"),
            options: json,
          },

        ];
        setBancos(json);
      });
  }


  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}>
      <div className="modal-header">
        <h5 className="modal-title mt-0">{t("ShortTermDebts")}</h5>
        <button
          type="button"
          onClick={props.toggle}
          data-dismiss="modal"
          className="close"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>
        <Row>
          <Col xl="12">
            <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
              <Card>
                <CardBody>
                  <Row>
                    <Col md="6">
                      <div className="mb-12">
                        <Label htmlFor="idBank">{t("Bank")}</Label>
                        <Select
                          showSearch
                          style={{ width: "100%" }}
                          placeholder={t("SearchtoSelect")}
                          optionFilterProp="children"
                          defaultValue={props.jsonSow.bank}
                          onChange={(e) => { changeAll(e, "bank") }}
                          filterOption={(input, option) =>
                            option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                          }
                        >
                          {options.length > 0 ?

                            options.map((item) => (
                              <Option value={item.value}>{item.label}</Option>
                            ))

                            : null}
                        </Select>
                      </div>
                    </Col>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="facilityType">{t("FacilityType")}</Label>
                        <AvField
                          className="form-control"
                          type="select"
                          name="facilityType"
                          value={props.jsonSow.facilityType}
                          id="facilityType"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                          }}
                        >
                          <option value="1" selected >LCR</option>
                          <option value="2" >LCP</option>
                        </AvField>
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="amount">{t("Amount")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="amount"
                          value={props.jsonSow.amount}
                          id="amount"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="date">{t("GrantDate")}</Label>
                        {/* <InputGroup>
                          <Flatpickr
                            className="form-control d-block"
                            placeholder="dd/MM/yyyy"
                            options={{
                              dateFormat: "d/m/Y",
                            }}
                            name="date"
                            value={props.jsonSow.date}
                            id="date"
                            onChange={(e) => { changeAll(e, "date") }}
                          />
                        </InputGroup> */}
                        <AvField
                          className="form-control"
                          type="date"
                          name="date"
                          id="date"
                          value={props.jsonSow.date}
                          onChange={(e) => { changeAll(e, "date") }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="expirationDate">{t("DueDate")}</Label>
                        {/* <InputGroup>
                          <Flatpickr
                            className="form-control d-block"
                            placeholder="dd/MM/yyyy"
                            options={{
                              dateFormat: "d/m/Y",
                            }}
                            name="expirationDate"
                            value={props.jsonSow.expirationDate}
                            id="expirationDate"
                            onChange={(e) => { changeAll(e, "expirationDate") }}
                          />
                        </InputGroup> */}
                        <AvField
                          className="form-control"
                          type="date"
                          name="expirationDate"
                          id="expirationDate"
                          value={props.jsonSow.expirationDate}
                          onChange={(e) => { changeAll(e, "expirationDate") }}
                        />
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="debitBalance1">{t("DebitBalance")} 1</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="debitBalance1"
                          value={props.jsonSow.debitBalance1}
                          id="debitBalance1"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="debitBalance2">{t("DebitBalance")} 2</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="debitBalance2"
                          value={props.jsonSow.debitBalance2}
                          id="debitBalance2"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="debitBalance3">{t("DebitBalance")} 3</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="debitBalance3"
                          value={props.jsonSow.debitBalance3}
                          id="debitBalance3"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>

                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="rate">{t("Rate")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="rate"
                          value={props.jsonSow.rate}
                          id="rate"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="fee">{t("Term")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="fee"
                          value={props.jsonSow.fee}
                          id="fee"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="bail">{t("Bond")}</Label>
                        <AvField
                          className="form-control"
                          type="select"
                          name="bail"
                          value={props.jsonSow.bail}
                          id="bail"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                          }}
                        >
                          <option value="1" selected>Fianza de Accionista</option>
                          <option value="2" selected>Fianza de Accionista 2</option>
                        </AvField>

                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="fundDestiny">{t("DestinationOfFunds")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="fundDestiny"
                          value={props.jsonSow.fundDestiny}
                          id="fundDestiny"
                        />
                      </div>
                    </Col>
                  </Row>
                </CardBody>
                <CardFooter style={{ textAlign: "right" }}>
                  <Button id="btnNew" color="dark" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                    <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                  </Button>
                  {props.botones?
                  <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}><i className="mdi mdi-content-save mdi-12px"></i>
                    {" "} {props.tipo == "CP" ? t("Save") : t("Edit")}
                  </Button>:null}
                </CardFooter>
              </Card>
            </AvForm>
          </Col>
        </Row>
      </div>
    </Modal >
  );
};

ModalDeudasCortoPlazo.propTypes = {
  isOpen: PropTypes.bool,
  toggle: PropTypes.func
};

export default ModalDeudasCortoPlazo;
