import React, { useEffect, useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import { formatCurrency, translationHelpers } from '../../../helpers';
import * as url from "../../../helpers/url_helper"

import PropTypes from 'prop-types';
import {
    Row,
    Col,
    Button,
    Label,
    Input,
    Modal,
    Card,
    CardBody,
    CardFooter,
    InputGroup,
    CardHeader,
    Table,
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import { BackendServices, CoreServices } from "../../../services";
import { useHistory, useLocation } from "react-router-dom";
import ExposicionCorportativa from "./Secciones/ExposicionCorporativa";
import ExposicionCorporativaClientes from "./Secciones/ExposicionCorporativaCliente";
import LoadingOverlay from "react-loading-overlay";
import Currency from "../../../helpers/currency";

const PrevisualizarPropCred = (props) => {
    const currencyData = new Currency();

    const { t, i18n } = useTranslation();
    const location = useLocation()
    const [dataRows, setdataRows] = React.useState(null);
    const [ExposicionCorportativa, setExposicionCorportativa] = useState(null);
    const [dataLocation, setData2] = useState(location.data);
    const [Facilidades, setFacilidades] = useState(null);
    const [dataRows1, setdataRows1] = React.useState(null);
    const [isActiveLoading1, setisActiveLoading1] = React.useState(false);
    const [isActiveLoading2, setisActiveLoading2] = React.useState(false);
    const [CorporateExposure, setCorporateExposure] = useState(null);
    const [data, setData] = useState({
        datosGenerales: {
            customerNumber: "",
            revision: "",
            preapproval: "",
            economicGroupNumber: "",
            economicGroupName: "",
            country: "",
            activityCompany: "",
            riskClassification: "",
            minimumProvisionSIB: "",
            relatedPart: "",
            approvalLevel: "",
            accountExecutive: "",
            responsibleUnit: "",
            countryRisk: "",
            requestDate: "",
            lastRequestDate: "",
            nextRevisionDate: "",
            proposedExpirationDate: "",
            flow: "",
            status: "",
            date: "",
        },
        resumenCambios: {
            "requestId": "",
            "increase": "",
            "rate": "",
            "guarantee": "",
            "desbtorsGuarantors": "",
            "terms": "",
            "fees": "",
            "covenats": "",
            "others": "",
            "status": true
        },
        Facilidades: {}
    })
    const [datosUsuario, setdatosUsuario] = useState(null)
    const api = new BackendServices();
    const coreServices = new CoreServices();
    const history = useHistory();
    const [locationData, setLocationData] = useState(null);


    useEffect(() => {
        let dataSession;
        if (location.data !== null && location.data !== undefined) {
            if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
                //location.data.transactionId = 0;
                //checkAndCreateProcedure(location.data);
                history.push(url.URL_DASHBOARD);
            } else {
                sessionStorage.setItem('locationData', JSON.stringify(location.data));
                setLocationData(location.data);
                dataSession = location.data
            }
        } else {
            //chequeamos si tenemos guardado en session
            var result = sessionStorage.getItem('locationData');
            if (result !== undefined && result !== null) {
                result = JSON.parse(result);
                setLocationData(result);
                dataSession = result;
            }
        }
        initializeData(dataSession);
    }, []);
    function initializeData(dataSession) {

        Promise.allSettled([
            api.consultGeneralDataPropCred(dataSession?.transactionId),// consultarResumenCambios
            api.retrieveProposalType(),
            api.retrieveFacilityType(),
            api.retrieveSubproposalType(),
            api.consultPrincipalDebtor(dataSession?.transactionId)
        ]).then(allSettled => {
            const [
                { value: GeneralDataPropCred },
                { value: propuesta },
                { value: facilidad },
                { value: proposalType },
                { value: PrincipalDebtor },

            ] = allSettled;


            if (GeneralDataPropCred != undefined && GeneralDataPropCred.length > 0) {
                let requestId = GeneralDataPropCred[0].requestId;

                data.datosGenerales.requestId = requestId;
                data.datosGenerales.customerNumber = GeneralDataPropCred[0].customerNumber;
                data.datosGenerales.revision = GeneralDataPropCred[0].revision;
                data.datosGenerales.preapproval = GeneralDataPropCred[0].preapproval;
                data.datosGenerales.economicGroupNumber = GeneralDataPropCred[0].economicGroupNumber;
                data.datosGenerales.economicGroupName = GeneralDataPropCred[0].economicGroupName;
                data.datosGenerales.country = GeneralDataPropCred[0].country;
                data.datosGenerales.activityCompany = GeneralDataPropCred[0].activityCompany;
                data.datosGenerales.riskClassification = GeneralDataPropCred[0].riskClassification;
                data.datosGenerales.minimumProvisionSIB = GeneralDataPropCred[0].minimumProvisionSIB;
                data.datosGenerales.relatedPart = GeneralDataPropCred[0].relatedPart;
                data.datosGenerales.approvalLevel = GeneralDataPropCred[0].approvalLevel;
                data.datosGenerales.accountExecutive = GeneralDataPropCred[0].accountExecutive;
                data.datosGenerales.GeneralDataPropCredonsibleUnit = GeneralDataPropCred[0].GeneralDataPropCredonsibleUnit;
                data.datosGenerales.countryRisk = GeneralDataPropCred[0].countryRisk;
                data.datosGenerales.requestDate = GeneralDataPropCred[0].requestDate;
                data.datosGenerales.lastRequestDate = GeneralDataPropCred[0].lastRequestDate;
                data.datosGenerales.nextRevisionDate = GeneralDataPropCred[0].nextRevisionDate;
                data.datosGenerales.proposedExpirationDate = GeneralDataPropCred[0].proposedExpirationDate;
                data.datosGenerales.flow = GeneralDataPropCred[0].flow;
                data.datosGenerales.status = GeneralDataPropCred[0].status;
                data.datosGenerales.date = GeneralDataPropCred[0].date;
                setData(data);
                api.consultarResumenCambios(requestId).then(ResumenCambios => {
                    if (ResumenCambios != undefined) {
                        data.resumenCambios = ResumenCambios
                        setData(data);
                    }
                }).catch(err => { });

                api.consultarFacilidades(requestId).then(resp => {
                    if (resp?.filter(data => data.debtor != "  ").length > 0) {
                        console.log(resp);
                        setFacilidades(resp.map((data) => (
                            data.debtor != "  " ?
                                <tr key={data.facilityId}>
                                    <td>{data.facilityId}</td>
                                    <td>{data.debtor}</td>
                                    <td>{facilidad.find(x => x.id === (data?.facilityType ?? data?.facilityTypeId))?.description}</td>
                                    <td>{propuesta.find(x => x.id === (data?.proposedType ?? data?.proposalTypeId))?.description}</td>
                                    <td>{proposalType.find(x => x.id === data?.proposalTypeName)?.description}</td>
                                    <td>{data.amount}</td>
                                    <td>{data.proposalRate}</td>
                                    <td>{data.balance}</td>
                                    <td>{data.termType}</td>
                                    <td>{data.sublimits}</td>
                                    <td>{data.otherConditions}</td>
                                    <td>{data.creditRiskOpinion}</td>
                                    <td>{data.provision}</td>
                                </tr> : null)
                        ));
                    } else {
                        setFacilidades(
                            <tr key={1}>
                                <td colSpan="7" style={{ textAlign: 'center' }}></td>
                            </tr>);
                    }
                }).catch(err => { });
            }

            if (PrincipalDebtor != undefined) {
                data.datosGenerales.codigoTipoPersona = PrincipalDebtor.typePerson
                data.datosGenerales.codigoTipoIdentificacion = PrincipalDebtor.idType
                data.datosGenerales.numeroCliente = PrincipalDebtor.customerNumberT24
                data.datosGenerales.primerNombre = PrincipalDebtor.name
                data.datosGenerales.segundoNombre = PrincipalDebtor.name2
                data.datosGenerales.primerApellido = PrincipalDebtor.lastName
                data.datosGenerales.segundoApellido = PrincipalDebtor.lastName2
                data.datosGenerales.numeroIdentificacion = PrincipalDebtor.clientDocId
                data.datosGenerales.customerNumber = PrincipalDebtor.clientDocId
                data.datosGenerales.nuevoDato = ""
                setData(data);
                setdatosUsuario(PrincipalDebtor);


                setisActiveLoading1(true)
                setisActiveLoading2(true)
                coreServices.getCorporateExhibition(PrincipalDebtor?.customerNumberT24 ?? 0).then(resp => {
                    setisActiveLoading1(false)
                    if (resp === undefined) {
                        return;
                    }
                    setExposicionCorportativa(resp.length > 0 ? resp : null);
                    setdataRows(resp.map((item, index) => (
                        <tr key={'row-' + index}>
                            <td data-label={t("type")} scope="row" width="30%">{item.name}</td>
                            <td data-label={t("ApprovedRisk")} scope="row" className="table-right">{formatCurrency('$', item.approvalRisk, 0)}</td>
                            <td data-label={t("Balance")} scope="row" className="table-right">{formatCurrency('$', item.approvalRisk, 0)}</td>
                            <td data-label={t("Variation")} scope="row" className="table-right">{formatCurrency('$', item.variationRisk, 0)}</td>
                            <td data-label={t("ProposedRisk")} scope="row" className="table-right">{formatCurrency('$', item.proposalRisk, 0)}</td>
                        </tr>
                    )
                    ));
                });
                coreServices.getCorporateExhibitionByClients(PrincipalDebtor?.customerNumberT24 ?? 0).then(resp => {
                    setisActiveLoading2(false)
                    if (resp === undefined) {
                        return;
                    }
                    setCorporateExposure(resp.length > 0 ? resp : null);
                    setdataRows1(resp.map((item, index) => (
                        <>
                            <tr key={'row-' + index}>
                                <th scope="row" width="30%">{item.clinteId}</th>
                                <th scope="row" className="text-end"></th>
                                <th scope="row" className="text-end"></th>
                                <th scope="row" className="text-end"></th>
                            </tr>
                            {item.data.map((item2, index2) => (
                                <tr key={'subrow-' + index2}>
                                    <td scope="row" width="30%"></td>
                                    <td scope="row" className="text-start">{formatCurrency('$', item2.approvalRisk, 0)}</td>
                                    <td scope="row" className="text-start">{formatCurrency('$', item2.variationRisk, 0)}</td>
                                    <td scope="row" className="text-start">{formatCurrency('$', item2.proposalRisk, 0)}</td>
                                </tr>
                            ))}
                        </>
                    )
                    ));
                });
            }

        }).catch(err => { });

    }
