/*ReinforcedManagementReport = Lista de Informe Reforzado*/
import React, { useState } from "react"
import PropTypes from 'prop-types';
import { useLocation, useHistory } from "react-router-dom";
import { Link } from "react-router-dom"

import {
  Card,
  CardBody,
  Col,
  Button,
  CardFooter,
  Row,
} from "reactstrap"

//Import Breadcrumb
import Breadcrumbs from "../../../../components/Common/Breadcrumb"

//i18n
import { withTranslation } from "react-i18next"

import DatosGenerales from "./DatosGenerales"
import ResultadosBusqueda from "./ResultadosBusqueda"
import ApiServicesDetalleDocumentacion from "../../../../services/DocumentacionLegal/ApiServicesDetalleDocumentacion"
import ModalCloseOptions from "./ModalCloseOptions";
import ModalAdvanceOptions from "./ModalAdvanceOptions";
import * as url from "../../../../helpers/url_helper";
import SweetAlert from "react-bootstrap-sweetalert";
import { BpmServices, BackendServices } from "../../../../services";

const PantallaBusqueda = props => {

  const history = useHistory();
  const location = useLocation()
  const { selectedData } = location;
  const [dataList, setDataList] = useState([]);
  const [displayModalCloseOptions, setDisplayModalCloseOptions] = useState(false);
  const [displayModalAdvanceOptions, setDisplayModalAdvanceOptions] = useState(false);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");

  const apiServiceBackend = new BackendServices();


  React.useEffect(() => {
    // Read Api Service data
    searchData(null)
  }, []);




  function searchData(values) {
    const api = new ApiServicesDetalleDocumentacion()
    api.getResultadosDetalleDocumentacionModel(values)
      .then((data) => {
        setDataList(data);

      })
      .catch((error) => {

        console.error('Error fetching documents', error);
      });
  }


  //Modal Opciones al Salir
  function toggleModalCloseOptions() {
    setDisplayModalCloseOptions(!displayModalCloseOptions);
  }

  //Modal Opciones al Salir
  function toggleModalModalAdvanceOptions() {
    setDisplayModalAdvanceOptions(!displayModalAdvanceOptions);
  }

  function validarCheck() {
    ValidateChecked().then(resp => {
      resp && toggleModalModalAdvanceOptions();
    });
  }

  //Salvando el Proceso y le pasamos una opcion: 1-Finalizar Proceso, 2- Salvarlo, 3- A Cumplimmineto, 4- Avanzar a IGR
  function onSaveProcess(option, values = null) {
    switch (option) {
      case 1: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 2: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 3: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 4: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        let valores = values.frmSearch;
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
    }

    // history.push("/dashboard");
  }

  const jsonCond = {
    rotary: () => ('rotary'),
    commercialLoan: () => apiServiceBackend.ConsultarDatosComparecenciaJuridicaPrestAPlazo(location.data.transactionId),
    multipleRotary: () => ('multipleRotary'),
    rotaryNumber: () => ('rotaryNumber'),
    overdraft: () => apiServiceBackend.ConsultarDatosComparecenciaJuridicaLineaSobregiro(location.data.transactionId),
    transfer: () => ('transfer'),
    bail: () => apiServiceBackend.ConsultarDatosFianzaSolidariaIlimitadaPJuri(location.data.transactionId),
    trust: () => ('trust'),
    agroPawn: () => ('agroPawn'),
    naturalBail: () => apiServiceBackend.ConsultarDatosFianzaSolidariaIlimitadaPNat(location.data.transactionId),
    legalBail: () => ('legalBail'),
    crossedLegalBail: () => apiServiceBackend.ConsultarDatosFianzaSolidariaLimitadaPJuri(location.data.transactionId),
    crossedNaturalBail: () => ('crossedNaturalBail'),
    promiseLetter: () => ('promiseLetter'),
    privateRecord: () => apiServiceBackend.ConsultarDatosContratosPrivados(location.data.transactionId),
    trustRecord: () => apiServiceBackend.ConsultarDatosActaFideicomiso(location.data.transactionId),
    loanGuarantees: () => ('loanGuarantees'),
    fiduciaryLine: () => ('fiduciaryLine'),
    prefinance: () => ('prefinance'),
  }

  function ValidateChecked() {
    return new Promise((resolve, reject) => {
      let validation = false;
      apiServiceBackend.consultarDocumentacionLegal(location.data.transactionId).then(data => {
        delete data.status;
        delete data.legalDocumentationId;
        delete data.transactId;

        let json = Object.entries(data ?? {})?.filter(([key, value]) => value)?.map(([key, value]) => key);
        for (const iterator of json) {
          console.log(jsonCond[iterator]());
        }

        validation = Object.entries(data ?? {})?.map(([key, value]) => value)?.some(Boolean) ?? false;
        seterror_dlg(!validation);
        resolve(validation);
      })
    })
  }

/*
  // Camino Feliz ... 
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"no"
  };
    
  //Requiere Ajustes 
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"si"
  };
  */


  return (
    <React.Fragment>

      <div className="page-content">

        <Breadcrumbs title={props.t("CommercialCredit")} breadcrumbItem={props.t("GenerateDocumentation")} />

        <Card>
          <CardBody>
            <CardBody>
              <Row>
                <Col md="6">
                  <h4 className="card-title">{props.t("LegalDocumentation")}</h4>
                </Col>
                <Col md="6" style={{ textAlign: "right" }}>
                  <Button id="btnSave" color="success" type="button" onClick={() => { ValidateChecked() }} style={{ margin: '5px' }}>
                    {props.t("generate")}
                  </Button>
                </Col>
              </Row>

            </CardBody>
            {/* <DatosGenerales /> */}

            <ResultadosBusqueda results={dataList} JSONCompare={Object.entries(jsonCond)?.map(([key, value]) => key)} />

          </CardBody>
          <CardFooter style={{ textAlign: "right" }}>
            <Button color="dark" type="button" style={{ margin: '5px' }} onClick={() => { onSaveProcess(2); }}><i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i> {props.t("Exit")}</Button>
            <Button color="success" type="button" style={{ margin: '5px' }} onClick={() => { validarCheck() }}><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i> {props.t("Continue")}</Button>
            {/*
          <Link className="btn btn-dark" style={{ margin: '5px' }} to={ { pathname: "/creditocomercial/documentacionlegal", selectedData: undefined } }>{props.t("Close")}</Link>

            <Button color="negative" type="button" style={{ margin: '5px' }}>
              { props.t("Refuse") }
            </Button>
            <Button color="success" type="button" style={{ margin: '5px' }}>
              { props.t("Advance") }
            </Button>
            */}
          </CardFooter>
        </Card>
        <ModalAdvanceOptions onSaveProcess={onSaveProcess} isOpen={displayModalAdvanceOptions} toggle={() => { toggleModalModalAdvanceOptions() }} />
        {successSave_dlg ? (
          <SweetAlert
            success
            title={props.t("SuccessDialog")}
            onConfirm={() => {
              setsuccessSave_dlg(false)

              history.push(url.URL_DASHBOARD);
            }}
          >
            {props.t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null}

        {error_dlg ? (
          <SweetAlert
            error
            title={props.t("ErrorDialog")}
            onConfirm={() => {
              seterror_dlg(false)
            }}
          >
            {error_msg}
          </SweetAlert>
        ) : null}
      </div>

    </React.Fragment>
  )
}

PantallaBusqueda.propTypes = {
  // onSelectIdPropuesta: PropTypes.func.isRequired,
  //selectedIdPropuesta:PropTypes.any
  selectedId: PropTypes.any,
  // onClose: PropTypes.func.isRequired
}

export default (withTranslation()(PantallaBusqueda))
