                    <th>{t("Date")}</th>
                    <th>{t("Type")}</th>
                    <th>{t("Quantity")}</th>
                    <th>{t("Day")}</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>
                      <Flatpickr
                        id="freqDate"
                        name="freqDate"
                        className="form-control d-block"
                        placeholder={OPTs.FORMAT_DATE_SHOW}
                        options={{
                          dateFormat: OPTs.FORMAT_DATE,
                          //maxDate: new Date().setFullYear(new Date().getFullYear() - 18),
                          defaultDate: moment().format("DD-MM-YYYY")
                        }}
                        onChange={(selectedDates, dateStr, instance) => { setfechaSet6(moment(dateStr, "DD/MM/YYYY").format("YYYY-MM-DD")) }}
                      />
                    </td>
                    <td>
                      <Select noOptionsMessage={() => ""}
                        showSearch
                        style={{ width: "100%" }}
                        placeholder={t("Search")}
                        optionFilterProp="children"
                        // defaultValue={props.dataCapex1.back}
                        onChange={(e) => { setTipoFrecuencia(e) }}
                        filterOption={(input, option) =>
                          option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                      >
                        <Option value={"A"}>A</Option>
                        <Option value={"M"}>M</Option>
                        <Option value={"D"}>D</Option>
                      </Select>
                    </td>
                    <td>
                      <AvField
                        className="form-control"
                        type="number"
                        name="freqAmount4"
                        id="freqAmount4"
                      />
                    </td>
                    <td>
                      <AvField
                        className="form-control"
                        type="number"
                        name="freqDay4"
                        id="freqDay4"
                      />
                    </td>
                  </tr>
                </tbody>
              </Table>
            </Col>
            <Col md="6">
              <Row>
                <Col md="6">
                  <div className="mb-2">
                    <Label>{t("Authorization type")}</Label>
                    <Select noOptionsMessage={() => ""}
                      showSearch
                      style={{ width: "100%" }}
                      placeholder={t("SearchtoSelect")}
                      optionFilterProp="children"
                      // defaultValue={props.dataCapex1.back}
                      onChange={(e) => { setTypeAuthorizationSet(e) }}
                      filterOption={(input, option) =>
                        option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                      }
                    >
                      {typeAuthorization != null ?

                        typeAuthorization.map((item, index) => (
                          <Option key={index} value={item.Code}>{item.Description}</Option>
                        ))
                        : null}
                    </Select>
                  </div>
                </Col>

                {/* <Col md="6">
                  <div className="mb-3">
                    <Label htmlFor="Autonomia">
                      {t("Autonomy")}
                    </Label>
                    <AvField
                      className="form-control"
                      type="text"
                      name="autonomy"
                      id="autonomy"
                    />
                  </div>
                </Col> */}
                <Col md="6">
                  <div className="mb-2">
                    <Label>{t("Autonomía")}</Label>
                    <Select noOptionsMessage={() => ""}
                      showSearch
                      style={{ width: "100%" }}
                      placeholder={t("SearchtoSelect")}
                      optionFilterProp="children"
                      // defaultValue={props.dataCapex1.back}
                      onChange={(e) => { setAutonomySet(JSON.parse(e)) }}
                      filterOption={(input, option) =>
                        option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                      }
                    >
                      {autonomy != null ?

                        autonomy.map((item, index) => (
                          <Option key={index} value={JSON.stringify(item)}>{item.Description}</Option>
                        ))
                        : null}
                    </Select>
                  </div>
                </Col>

                <Col md="6">
                  <div className="mb-3">
                    <Label htmlFor="usuarioAutonomia">
                      {t("Usuario de Autonomía")}
                    </Label>
                    <AvField
                      className="form-control"
                      name="usuarioAutonomia"
                      type="text"
                      id="usuarioAutonomia"
                    />
                  </div>
                </Col>

              </Row>

            </Col>
          </Row>
          <Row>
            <Col md="12">
              <div className="mb-3">
                <Label htmlFor="informacionAdicional">
                  {t("Additional Information")}
                </Label>
                <AvField
                  className="form-control"
                  type="textarea"
                  rows="3"
                  name="informacionAdicional"
                  id="informacionAdicional"
                />
              </div>
            </Col>
          </Row>
          <LineaHija dataLinea={dataLinea} dataLineaFunction={dataLineaFunction} />

          <Row>
            <Col md={12} style={{ textAlign: "right", marginTop: "10px" }}>
              <Button
                id="btnNew"
                color="danger"
                type="button"
                style={{ margin: "5px" }}
                onClick={props.toggle}
                data-dismiss="modal"
              >
                <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}
                {t("Cancel")}
              </Button>
              <Button id="btnSearch" type="submit" color="success" style={{ margin: '5px' }}>
                <i className="mdi mdi-content-save mdi-12px"></i>
                {" "} {typeGarantia == "nuevo" ? t("Save") : t("Edit")}</Button>
            </Col>
          </Row>
        </LoadingOverlay>
      </AvForm>

      {
        successSave_dlg2 ? (
          <SweetAlert
            success
            title={t("SuccessDialog")}
            confirmButtonText={t("Confirm")}
            cancelButtonText={t("Cancel")}
            onConfirm={() => {
              setsuccessSave_dlg2(false)
              props.toggle();
              // window.location.reload();
            }}
          >
            {t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null
      }
      {
        error_dlg ? (
          <SweetAlert
            error
            title={t("ErrorDialog")}
            confirmButtonText={t("Confirm")}
            cancelButtonText={t("Cancel")}
            onConfirm={() => {
              seterror_dlg(false)
            }}
          >
            {error_msg}
          </SweetAlert>
        ) : null
      }
      {
        error_dlgT24 ? (
          <SweetAlert
            error
            title={t("ErrorDialog")}
            confirmButtonText={t("Confirm")}
            cancelButtonText={t("Cancel")}
            onConfirm={() => {
              seterror_dlgT24(false)
            }}
          >
            {"La Línea no pudo ser creada en T24"}
          </SweetAlert>
        ) : null
      }
    </React.Fragment >
  );
};

export default withTranslation()(PantallaBusqueda);
