                            name="purpose"
                            id="purpose"
                            validate={{
                              required: { value: true, errorMessage: t("Required") }
                            }}
                            type="textarea"
                            value={dataSet.purpose}
                            rows="4" />
                        </AvGroup>
                      </Col>
                      <Col md="6">
                        <AvGroup className="mb-3">
                          <Label htmlFor="sublimits">{t("Sub Limits")}</Label>
                          <AvField
                            className="form-control"
                            name="sublimits"
                            id="sublimits"
                            value={dataSet.sublimits}
                            validate={{
                              required: { value: true, errorMessage: t("Required") }
                            }}
                            type="textarea"
                            rows="4" />
                        </AvGroup></Col>
                    </Row>
                  </Col>
                </Row>
