import React, { useEffect, useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';
import {
    Row,
    Col,
    Button,
    Label,
    Input,
    Modal,
    Card,
    CardBody,
    CardFooter,
    InputGroup,
    Table,
} from "reactstrap"
import { Select } from 'antd';
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import { Link, useHistory } from "react-router-dom";
import { CoreServices } from "../../../../../../services";
const ModalLineaHija = (props) => {
    const { Option } = Select;
    const history = useHistory();
    const [typeLine, settypeLine] = useState(null);
    const apiServiceCore = new CoreServices();
    const [datoSeleccionado, setdatoSeleccionado] = useState(null)
    const { t, i18n } = useTranslation();
    const jsonPrioridad = [
        { value: "1", label: "Muy Urgente" },
        { value: "2", label: "Urgencia Moderada" },
        { value: "3", label: "Importante" },
        { value: "4", label: "Baja Urgencia" },
        { value: "5", label: " Sin Urgencia" }]
    useEffect(() => {
        setdatoSeleccionado(jsonPrioridad[0])
        apiServiceCore.getTypeOfCreditLimitsCatalog().then(resp => {
            settypeLine(resp.Records)
        });
    }, [])
    function handleSubmit(event, errors, values) {
        event.preventDefault();
        if (errors.length > 0) {
            return;
        }
        props.actuaizarDocumentacion(values)
    }
    return (
        <Modal
            size="md"
            isOpen={props.isOpen}
            toggle={props.toggle}
            centered={true}>
            <div className="modal-header">
                <h5 className="modal-title mt-0">{"Nueva línea hija"}</h5>
                <button
                    type="button"
                    onClick={props.toggle}
                    data-dismiss="modal"
                    className="close"
                    aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>
            <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>

                <Row>
                    <Col xl="12">
                        <AvForm id="frmSearch" autocomplete="off" className="needs-validation" onSubmit={handleSubmit}>
                            <Card>
                                <CardBody>
                                    <Row>
                                        <Col md="4">
                                            <div className="mb-2">
                                                <Label>{t("Type of line")}</Label>
                                                <Select noOptionsMessage={() => ""}
                                                    showSearch
                                                    style={{ width: "100%" }}
                                                    placeholder={t("SearchtoSelect")}
                                                    optionFilterProp="children"
                                                    // onChange={(e) => { settypeLineSet(typeLine.find(x => x.Code === e)); setisActiveLoading(true); coreServices.getLineSerial(usuarioProspecto?.customerNumberT24, e).then(resp => { setisActiveLoading(false); setnumeroLinea(resp) }) }}
                                                    filterOption={(input, option) =>
                                                        option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                                    }
                                                >
                                                    {typeLine != null ?

                                                        typeLine.map((item, index) => (
                                                            <Option key={index} value={item.Code}>{item.Code}</Option>
                                                        ))
                                                        : null}
                                                </Select>

                                            </div>
                                        </Col>
                                        <Col md="4">
                                            <div className="mb-3">
                                                <Label>{t("Monto de la línea")}</Label>
                                                <AvField
                                                    className="form-control"
                                                    type="text"
                                                    name="PartyId"
                                                    validate={{ required: { value: true, errorMessage: t("Required Field") } }}
                                                />
                                            </div>
                                        </Col>
                                        <Col md="4">
                                            <div className="mb-3">
                                                <Label>{t("Número de línea")}</Label>
                                                <AvField
                                                    className="form-control"
                                                    type="text"
                                                    name="PartyId"
                                                    validate={{ required: { value: true, errorMessage: t("Required Field") } }}
                                                />
                                            </div>
                                        </Col>
                                    </Row>
                                </CardBody>
                                <CardFooter style={{ textAlign: "right" }}>
                                    <Button id="btnNew" color="danger" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                                        <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                                    </Button>
                                    <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}><i className="mdi mdi-content-save mdi-12px"></i>
                                        {t("Save")}
                                    </Button>
                                </CardFooter>
                            </Card>
                        </AvForm>
                    </Col>
                </Row>
            </div>
        </Modal>
    );
};


export default ModalLineaHija;
