//Gestionar Log de los Procesos
import React, { useState } from "react"
import PropTypes from 'prop-types';
import { Link } from "react-router-dom"
import functionshelper from "../../helpers/functions_helper"

import {
  Table,
  Card,
  CardBody,
  Alert,  
  UncontrolledAlert,
  Pagination,
  PaginationItem,
  PaginationLink,
  CardFooter,
  Button,
  Row,
  Col,
} from "reactstrap"

//i18n
import { withTranslation } from "react-i18next"
//Import SweetAlert
import SweetAlert from "react-bootstrap-sweetalert"

import ModalNewLog from "./ModalNewLog"
import {BackendServices} from "../../services/index";


const LogProcess = props => {

  //Data que recibimos en el location
  const [locationData, setLocationData] = useState(props.locationData);  
  
  const [dataList, setDataList] = useState([]);
  const [displayNewLogModal, setDisplayNewLogModal] = useState(false);
    
  const [success_dlg, setsuccess_dlg] = useState(false);  
  const [success_msg, setsuccess_msg] = useState("");
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");

  const [showErro, setshowErro] = useState(false);
  const [errorMsg, seterrorMsg] = useState("");
  
  //On Mounting (componentDidMount) if selectedData has changes
  React.useEffect(() => {
    if(props.locationData!==undefined && props.locationData!==null){
      setLocationData(props.locationData)
      searchData(props.locationData);
    }
  }, [props.locationData]);

  //nuevo log
  function toggleDisplayNewLogModal() {
    setDisplayNewLogModal(!displayNewLogModal);
    removeBodyCss()
  }

  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }

  //buscamos las Propuestas de Credito
  function searchData(data) {

    var parameters ={responsible:data.customerName,instanceNumber:data.instanceId,transactId:data.transactionId}
    const api = new BackendServices();
      api.searchLogs(parameters)
      .then(resp => {
        
        if(resp.status==200){          
          const dataRows = resp.result.map((dt,idx) => (
            <tr key={idx}>
              <th scope="row">{dt.date}</th>
              <td>{dt.observations}</td>
              <td>{ functionshelper.getProcessName(dt.processId) }</td>
              <td></td>
              <td>{dt.responsible}</td>
              <td></td>
            </tr>)
          );
          setDataList(dataRows);
        }
        else{
          console.log("searchLogs",resp)

          setshowErro(false);
          seterrorMsg(props.t('ErrorCode')+": " + resp.status + "  " +props.t('ErrorMsg')+": " + resp.error.error);
          setshowErro(true)
        }        
      }).catch(err => {
        console.log(err);});
  }

  function onSaveLogProcess(observations) {
      /* ---------------------------------------------------------------------------------------------- */
      /*              Le pasamos los valores al servicio de guardar bitacora                            */
      /* ---------------------------------------------------------------------------------------------- */
      let data = {
        "instanceId":locationData.instanceId,
        "transactId": locationData.transactionId,
        "clientId": locationData.customerId,
        "observations":observations,
        "responsible":locationData.customerName, //hay que poner el usuario login
        "processId":locationData.processId,
        "activityId":locationData.activityId,
        "status":true
    }
      const api = new BackendServices();
      api.guardarHistorico(data)
      .then(resp => {
        if(resp!==undefined){
          searchData(locationData);

          setsuccess_msg(props.t("SuccessSaveMessage"))
          setsuccess_dlg(true)
        }
        else{
          setshowErro(false);
          seterrorMsg(props.t("ErrorSaveMessage"));
          setshowErro(true);           
        }        
      }).catch(err => {
        console.log(err);
      });
  }  

  return (

    <React.Fragment>
      <Card>
        <CardBody>

        {showErro ? (
            <Row>
            <Col lg="12">
            <UncontrolledAlert color="danger" role="alert" dismissible onClose={() =>{ console.log("error"); setshowErro(false)}}>
              {errorMsg}
            </UncontrolledAlert>
            </Col>   
             </Row>
          ) : null}        

          <Row>
            <Col lg="12">
              <h4 className="card-title">{props.t("Binnacle")}</h4>
              <div className="table-responsive">
                <Table className="table mb-0">
                  <thead className="table-light">
                    <tr>
                      <th>{props.t("Date")}</th>
                      <th>{props.t("Comment")}</th>
                      <th>{props.t("Process")}</th>
                      <th>{props.t("Activity")}</th>
                      <th>{props.t("User")}</th>
                      <th>
                        <Link to="#" className="float-end" onClick={() => { toggleDisplayNewLogModal() }} title={props.t("Add")}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i>{" "}</Link>                            
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {dataList}
                  </tbody>
                </Table>

              </div>
            </Col>
          </Row>
        </CardBody>
      </Card>

      <ModalNewLog onSaveLogProcess={(observations) => { onSaveLogProcess(observations) }} isOpen={displayNewLogModal} toggle={() => { toggleDisplayNewLogModal() }} />


      {success_dlg ? (
            <SweetAlert
              success
              title={props.t("SuccessDialog")}
              onConfirm={() => {
                setsuccess_dlg(false)
                
              }}
            >
              {success_msg}
            </SweetAlert>
          ) : null}

          {error_dlg ? (
            <SweetAlert
              error
              title={props.t("ErrorDialog")}
              onConfirm={() => {
                seterror_dlg(false)
              }}
            >
              {error_msg}
            </SweetAlert>
          ) : null}


    </React.Fragment>

  );
}

LogProcess.propTypes = {
  locationData: PropTypes.any,
};

export default (withTranslation()(LogProcess))
