import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';
import { Link, useLocation, useHistory } from "react-router-dom"
import {
  Row,
  Col,
  Button,
  Label,
  Table,
} from "reactstrap"
import { AvForm, AvField } from "availity-reactstrap-validation"
import ModalMovimientoCuenta from "./ModalMovimientoCuentas";
import { BackendServices } from "../../../../../services";
import SweetAlert from "react-bootstrap-sweetalert";
import * as url from "../../../../../helpers/url_helper"
const MoviemientosCuentas = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const location = useLocation()
  const [formValid, setFormValid] = useState(false);
  const [dataReturn, setDataReturn] = useState(props.dataMoviemientosCuentas);
  const [showModelAttachment, setShowModelAttachment] = useState(false);
  const [tipo, settipo] = useState("")
  const [botonValidation, setbotonValidation] = useState(false);
  const [dataLocation, setData] = useState(location.data);
  const [accionistasJSON, setAccionistasJson] = useState(props.jsonAccionistas);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [dataMovimientos, setdataMovimientos] = useState(null);
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")
  const [dataDelete, setDataDelete] = useState([]);
  const [dataAcciones, setdataAcciones] = useState({
    trasactId: null,
    year: null,
    month: null,
    deposits: null,
    averageBalance: null,
    observations: null
  });

  const [locationData, setLocationData] = useState(null);
  const history = useHistory();


  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmMovimientoCuentas');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }));
  React.useEffect(() => {

    let dataSession;
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        dataSession = location.data;
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        dataSession = result;
      }
    }

    // Read Api Service data  
    loadInitialData(dataSession);
  }, [props.activeTab == 15]);
  const year2019 = [
    { id: 1, year: "2019", month: "enero", deposit: '73515064.00', averagebalance: '19032949.00' },
    { id: 2, year: "2019", month: "febrero", deposit: '73515064.00', averagebalance: '19032949.00' },
    { id: 3, year: "2019", month: "marzo", deposit: '73515064.00', averagebalance: '19032949.00' },
    { id: 4, year: "2019", month: "abril", deposit: '73515064.00', averagebalance: '19032949.00' }
  ];
  const api = new BackendServices()

  const year2019Rows = year2019.map((data, index) => (
    <tr key={index}>
      <td>{data.month}</td>
      <td>{data.deposit}</td>
      <td>{data.averagebalance}</td>
    </tr>)
  );
  function loadInitialData(dataLocation) {
    /* ---------------------------------------------------------------------------------------------- */
    /*            Cargamos la lista de accionistas pasandole como parametro el transactionId           */
    /* ---------------------------------------------------------------------------------------------- */
    // consultarMovimientosCuentasIGR
    api.consultMovementsAccountsIGR(dataLocation.transactionId).then(resp => {
      if (resp.length > 0) {
        setdataMovimientos(resp.map((data, index) => (
          data.status ?
            <tr key={index}>
              <td>{data.year}</td>
              <td>{data.month}</td>
              <td>{data.deposits}</td>
              <td>{data.averageBalance}</td>
              <td>{data.observations}</td>
              <td>
                <Button type="button" color="link" onClick={(resp) => { updateDataAccount(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { deleteDataAccount(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr> : null)
        ));
      } else {
        setdataMovimientos(
          <tr key={4578}>
            <td colSpan="6" style={{ textAlign: 'center' }}></td>
          </tr>);
      }
    });

  }
  const year2020 = [
    { id: 1, year: "2020", month: "enero", deposit: '73515064.00', averagebalance: '19032949.00' },
    { id: 2, year: "2020", month: "febrero", deposit: '73515064.00', averagebalance: '19032949.00' },
    { id: 3, year: "2020", month: "marzo", deposit: '73515064.00', averagebalance: '19032949.00' },
    { id: 4, year: "2020", month: "abril", deposit: '73515064.00', averagebalance: '19032949.00' }
  ];
  const year2021 = [
    { id: 1, year: "2021", month: "enero", deposit: '73515064.00', averagebalance: '19032949.00' },
    { id: 2, year: "2021", month: "febrero", deposit: '73515064.00', averagebalance: '19032949.00' },
    { id: 3, year: "2021", month: "marzo", deposit: '73515064.00', averagebalance: '19032949.00' },
    { id: 4, year: "2021", month: "abril", deposit: '73515064.00', averagebalance: '19032949.00' }
  ];
  React.useEffect(() => {
    setDataReturn(props.dataMoviemientosCuentas)
    dataReturn.dataTableMovimientoCuentas = [year2021, year2020, year2019]
    setDataReturn(dataReturn);
  }, [props.activeTab == 15]);
  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    setFormValid(true)
  }
  function toggleShowModelAttachment() {
    setShowModelAttachment(!showModelAttachment);
    removeBodyCss()
  }
  function updateDataAccount(data) {
    setdataAcciones(data)
    settipo("editar");
    setbotonValidation(true);
    abrirModal()
  }
  function deleteDataAccount(data) {
    setDataDelete(data)
    setconfirm_alert(true)

  }
  function cerrarModal() {
    setShowModelAttachment(false);
    removeBodyCss()
  }
  function abrirModal() {
    setShowModelAttachment(true);
    removeBodyCss()
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }
  function dataManagament(values, tipo) {
    values.trasactId = Number(locationData.transactionId);
    if (tipo == "guardar") {
      // nuevoMovimientosCuentasIGR
      api.newMovementsAccountsIGR(values).then(resp => {
        if (resp !== null && resp !== undefined) {
          loadInitialData(locationData);
          cerrarModal();
        } else {
          cerrarModal();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      });
    } else {
      // actualizarMovimientosCuentasIGR
      api.updateMovementsAccountsIGR(values).then(resp => {
        if (resp !== null && resp !== undefined) {
          loadInitialData(locationData);
          cerrarModal();
        } else {
          cerrarModal();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      });
    }
  }
  return (
    <React.Fragment>
      <h5>
        {t("AccountMovements")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmMovimientoCuentas" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12" style={{"overflowX":"scroll"}}>
            <Table className="table mb-0" >
              <thead className="table-light">
                <tr>
                  <th>{t("Year")}</th>
                  <th>{t("Month")}</th>
                  <th>{t("Deposits")}</th>
                  <th>{t("AverageBalance")}</th>
                  <th>{t("Observation")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setbotonValidation(true); setdataAcciones({
                      trasactId: null,
                      year: null,
                      month: null,
                      deposits: null,
                      averageBalance: null,
                      observations: null
                    }); settipo("guardar"); abrirModal()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {dataMovimientos}
              </tbody>
            </Table>
          </Col>
        </Row>
        <div className="my-3"></div>
        <Row>
          <Col md="12">
            <div className="mb-3">
              <Label htmlFor="detalleMovimientoCuenta">{t("Details")}</Label>
              <AvField
                type="textarea"
                name="observations"
                id="observations"
                maxLength="1000"
                rows="7"
                value={props.dataMoviemientosCuentas.observations}
                onChange={(e) => { dataReturn.observations = e.target.value; setDataReturn(dataReturn); }}
              />
            </div>
          </Col>
        </Row>
      </AvForm>
      <ModalMovimientoCuenta tipo={tipo} dataManagament={dataManagament} botones={botonValidation} dataAcciones={dataAcciones} isOpen={showModelAttachment} toggle={() => { cerrarModal() }} />
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            loadInitialData(locationData);
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            loadInitialData(locationData);
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            // eliminarMovimientosCuentasIGR
            apiBack.deleteMovementsAccountsIGR({ transactId: dataDelete.trasactId, movementId: dataDelete.movementId }).then(resp => {
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                loadInitialData(locationData);
              }
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
    </React.Fragment>
  );
});
MoviemientosCuentas.propTypes = {
  onSubmit: PropTypes.func.isRequired,
}
export default MoviemientosCuentas;
