import React, { useState } from 'react'
import { useTranslation } from "react-i18next";
import {
    Button,
    Card,
    Col,
    Form,
    FormControl,
    FormGroup,
    Row,
    Container
} from 'react-bootstrap'
import { Label } from "reactstrap";
import Select from "react-select";
import { useFormQuoter } from './Hooks/useFormQuoter';
import { AvField, AvForm } from 'availity-reactstrap-validation';

const intialForm = {
    amountDebt: ""
}

const validationsForm = (form) => { }

const FormDataQuoter = (props) => {
    // props
    const { onChange, onKeyPress, form } = props

    // hooks
    const { t, i18n } = useTranslation();

    // custom hooks
    const {
        form2,
        errors,
        loading,
        response,
        handleChange,
        handleBlur,
        handleSubmit,
    } = useFormQuoter(intialForm, validationsForm)
    const [amountDebt, setAmountDebt] = useState("")
    const [anualRate, setAnualRate] = useState("")

    const optionsConfirmation = [
        { value: '1', label: 'Sí' },
        { value: '2', label: 'No' }
    ]
    const optionsPayments = [
        { value: '1', label: 'Mensual' },
        { value: '2', label: 'Trimestral' },
        { value: '3', label: 'Semestral' },
        { value: '4', label: 'Anual' },
    ]
    const optionsBanking = [
        { value: '1', label: 'International Banking' },
        { value: '2', label: 'Fuerza en venta' },
    ]

    const handleChangeInput = (evt) => {
        const regexOnlyNum = new RegExp(/^[0-9]+$/)
        const regexOnlyNum2 = evt.target.value.replace(/[^0-9,.]/g, "")
        const valueAmountDebt = regexOnlyNum2;
        setAmountDebt(valueAmountDebt)
    }

    const handleChangeInput2 = (evt) => {
        const regexOnlyNum2 = evt.target.value.replace(/[^0-9,.]/g, "")
        const valueAnualRate = regexOnlyNum2;
        setAnualRate(valueAnualRate)
    }

    const handleKeyOnlyNum = (evt) => {
        const keyPress = evt.key;
        const regexOnlyNum = /^[0-9,.]+$/

        if (!regexOnlyNum.test(keyPress)) return
        // console.log('here!');
    }

    const handleTest = (bankingSelected) => {
        console.log(bankingSelected);
    }

    return (
        <Container>
            <Card.Title className='mt-5'>
                {t("QuoterData")}
            </Card.Title>
            <AvForm>
                <Row>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="amountDebt">{t("Amount Debt")}</Label>
                            <AvField
                                id="amountDebt"
                                name='amountDebt'
                                value={form.name}
                                validate={{ required: { value: true } }}
                                errorMessage={t("Required Field")}
                                // onKeyPress={(evt) => handleKeyOnlyNum(evt)}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="amountFinanced">{t("Amount to finance")}</Label>
                            <AvField
                                id="amountFinanced"
                                name="amountFinanced"
                                value={form.name}
                                validate={{ required: { value: true } }}
                                errorMessage={t("Required Field")}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="anualRate">{t("Annual rate (%)")}</Label>
                            <AvField
                                id="anualRate"
                                name="anualRate"
                                value={form.name}
                                validate={{ required: { value: true } }}
                                errorMessage={t("Required Field")}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="effectiveRate">{t("Effective rate (%)")}</Label>
                            <AvField
                                id="effectiveRate"
                                name="effectiveRate"
                                value={form.name}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                </Row>
                <Row>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="payments">{t("Payments")}</Label>
                            <Select
                                id="payments"
                                name="payments"
                                value={form.name}
                                options={optionsPayments}
                                onChange={onChange} />
                        </div>
                    </Col>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="term">{t("Term (months)")}</Label>
                            <AvField
                                id="term"
                                name="term"
                                value={form.name}
                                validate={{ required: { value: true } }}
                                errorMessage={t("Required Field")}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="monthlyBill">{t("Monthly bill")}</Label>
                            <AvField
                                id="monthlyBill"
                                name="monthlyBill"
                                value={form.name}
                                validate={{ required: { value: true } }}
                                errorMessage={t("Required Field")}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="initialCredit">{t("Initial credit")}</Label>
                            <AvField
                                id="initialCredit"
                                name="initialCredit"
                                value={form.name}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                </Row>
                <Row>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="gracePeriod">{t("Grace period")}</Label>
                            <Select
                                id="gracePeriod"
                                name="gracePeriod"
                                value={form.name}
                                options={optionsConfirmation}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="ballomFee">{t("Balloom fee")}</Label>
                            <Select
                                id="ballomFee"
                                name="ballomFee"
                                value={form.name}
                                options={optionsConfirmation}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="feciPercentage">{t("Feci")}</Label>
                            <Select
                                id="feciPercentage"
                                name="feciPercentage"
                                value={form.name}
                                options={optionsConfirmation}
                                onChange={onChange} />
                        </div>
                    </Col>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="amountFeci">{t("Feci amount")}</Label>
                            <AvField
                                id="amountFeci"
                                name="amountFeci"
                                value={form.name}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                </Row>
                <Row>
                    <Col md={12} lg={6}>
                        <div className="mb-3">
                            <Label htmlFor="banking">{t("Banking")}</Label>
                            <Select
                                id="banking"
                                name="banking"
                                value={form.name}
                                options={optionsBanking}
                                onChange={handleTest}
                            />
                        </div>
                    </Col>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="legalExpenses">{t("Legal expenses")}</Label>
                            <AvField
                                id="legalExpenses"
                                name="legalExpenses"
                                value={form.name}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="financialTrust">{t("Trust")}</Label>
                            <AvField
                                id="financialTrust"
                                name="financialTrust"
                                value={form.name}
                                onChange={onChange}
                            />
                        </div>
                    </Col>
                </Row>
            </AvForm>
        </Container>
    )
}

export default FormDataQuoter
