          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalSow();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "Propuesto") {
      api.nuevoSowPropuestoIGR(data).then(resp => {
