                id="companyHistoryDetails"
                maxLength="1000"
                rows="7"
                value={props.dataRelacionesBancarias.observations}
                validate={{
                  required: { value: true, errorMessage: t("Required Field") },
                }}
                onChange={(e) => { setdataReturn({ observations: e.target.value }) }}
              />
            </div>
          </Col>
        </Row>
      </AvForm >
      <ModalDeudasCortoPlazo actualizarDatos={actualizarDatos} botones={botonValidation} jsonSow={dataSet} guardarDatos={guardarDatos} tipo={tipo} isOpen={showModalDeudasCortoPlazo} toggle={() => { toggleShowModalDeudasCortoPlazo() }} />
      <ModalDeudasLargoPlazo actualizarDatos={actualizarDatos} botones={botonValidation} jsonSow={dataSet} guardarDatos={guardarDatos} tipo={tipo} isOpen={showModalDeudasLargoPlazo} toggle={() => { toggleShowModalDeudasLargoPlazo() }} />
      <ModalSow actualizarDatos={actualizarDatos} botones={botonValidation} jsonSow={dataSet} guardarDatos={guardarDatos} tipo={tipo} isOpen={showModalSow} toggle={() => { toggleShowModalSow() }} />
      {/* LLAMADA A LOS COMPONENTES DE ALERTA PARA MENSAJES DE CONFIRMACION */}
      {
        successSave_dlg ? (
          <SweetAlert
            success
            title={t("SuccessDialog")}
            onConfirm={() => {
              setsuccessSave_dlg(false)
              initializeData();
            }}
          >
            {t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null
      }

      {
        error_dlg ? (
          <SweetAlert
            error
            title={t("ErrorDialog")}
            onConfirm={() => {
              seterror_dlg(false)
              initializeData();
            }}
          >
            {error_msg}
          </SweetAlert>
        ) : null
      }
      {
        confirm_alert ? (
          <SweetAlert
            title={t("Areyousure")}
            warning
            showCancel
            confirmButtonText={t("Yesdeleteit")}
            confirmBtnBsStyle="success"
            cancelBtnBsStyle="danger"
            onConfirm={() => {
              const apiBack = new BackendServices();
              if (tipo == "Actual") {
                apiBack.eliminarSowActualIGR({ transactId: dataLocation.transactionId, currentSowId: dataDelete.currentSowId }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              } else if (tipo == "Propuesto") {
                apiBack.eliminarSowPropuestoIGR({ transactId: dataLocation.transactionId, currentSowId: dataDelete.currentSowId }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              } else if (tipo == "DCP") {
                apiBack.eliminarRelacionesBancariasDeudasCP({ transactId: dataLocation.transactionId, debtId: dataDelete.debtId }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              } else if (tipo == "DCL") {
                apiBack.eliminarRelacionesBancariasDeudasLP({ transactId: dataLocation.transactionId, debtId: dataDelete.debtId }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              }
            }}
            onCancel={() => setconfirm_alert(false)}
          >
            {t("Youwontbeabletorevertthis")}
          </SweetAlert>
        ) : null
      }
    </React.Fragment >
  );

});


RelacionesBancarias.propTypes = {
  onSubmit: PropTypes.func.isRequired,
}


export default RelacionesBancarias;
