/*ReinforcedManagementReport = Lista de Informe Reforzado*/
import React, { useState } from "react"
import PropTypes from 'prop-types';
import { useLocation, useHistory } from "react-router-dom";
import { Link } from "react-router-dom"
import * as url from  "../../../../helpers/url_helper"
import * as OPTs from "../../../../helpers/options_helper";

import {
  Card,CardHeader,
  CardBody,
  Col,
  Button,
  CardFooter,
  Row,
} from "reactstrap"

//Import Breadcrumb
import Breadcrumbs from "../../../../components/Common/Breadcrumb"

//i18n
import { useTranslation, withTranslation } from "react-i18next"

import DatosGenerales from "./DatosGenerales"
import ResultadosBusqueda from "./ResultadosBusqueda"
import ApiServicesDetalleDocumentacion from "../../../../services/DocumentacionLegal/ApiServicesDetalleDocumentacion"
import SweetAlert from "react-bootstrap-sweetalert";
import { BpmServices, BackendServices } from "../../../../services";
import LoadingOverlay from "react-loading-overlay";

const PantallaBusqueda = props => {
  
  const { t, i18n } = useTranslation();
  const history = useHistory();
  const location = useLocation()
  const { selectedData } = location;
  const [dataList, setDataList] = useState();
  const [displayModalCloseOptions, setDisplayModalCloseOptions] = useState(false);
  const [displayModalAdvanceOptions, setDisplayModalAdvanceOptions] = useState(false);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");

  const [isActiveLoading, setIsActiveLoading] = useState(false);

  const [locationData, setLocationData] = useState(null);
  //Servicios
  const [backendServices, setbackendServices] = useState(new BackendServices());
  const [bpmServices, setbpmServices] = useState(new BpmServices());
  //const [coreServices, setcoreServices] = useState(new CoreServices());

  const [mainDebtor, setmainDebtor] = useState(null);

  //On Mounting (componentDidMount)
  React.useEffect(() => {
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        fetchData(location.data);
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        fetchData(result);
      }
    }
  }, []);

  //Caraga Inicial de datos
  function fetchData(locationData) {

    //chequear si la tarea no ha sido iniciada
    bpmServices.checkAndStartTask(locationData)
      .then((iniresult) => {
        if (iniresult === false) {
          history.push(url.URL_DASHBOARD);
        }
      })

    loadUserProspect(locationData.transactionId)
    // Read Api Service data
    searchData(null);
  }

  function loadUserProspect(transactionId) {
    // consultarDeudorPrincipal
    backendServices.consultPrincipalDebtor(transactionId)
      .then((data) => {
        if (data !== undefined) {
          setmainDebtor(data);
        }
      });
  }

  function searchData(values) {
    setIsActiveLoading(true);
    const api = new ApiServicesDetalleDocumentacion()
    api.getResultadosDetalleDocumentacionModel(values)
      .then((data) => {
        setDataList(data);
        setIsActiveLoading(false);
      })
      .catch((error) => {
        setIsActiveLoading(false);
        console.error('Error fetching documents', error);
      });
  }


  //Modal Opciones al Salir
  function toggleModalCloseOptions() {
    setDisplayModalCloseOptions(!displayModalCloseOptions);
  }

  //Modal Opciones al Salir
  function toggleModalModalAdvanceOptions() {
    setDisplayModalAdvanceOptions(!displayModalAdvanceOptions);
  }

  function validarCheck() {
    ValidateChecked().then(resp => {
      resp && toggleModalModalAdvanceOptions();
    });
  }

  //Salvando el Proceso y le pasamos una opcion: 1-Finalizar Proceso, 2- Salvarlo, 3- A Cumplimmineto, 4- Avanzar a IGR
  function onSaveProcess(option, values = null) {
    switch (option) {
      case 1: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 2: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 3: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 4: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        let valores = values.frmSearch;
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
    }

    // history.push("/dashboard");
  }

  const jsonCond = {
    rotary: () => ('rotary'),
    commercialLoan: () => backendServices.ConsultarDatosComparecenciaJuridicaPrestAPlazo(locationData.transactionId),
    multipleRotary: () => ('multipleRotary'),
    rotaryNumber: () => ('rotaryNumber'),
    overdraft: () => backendServices.ConsultarDatosComparecenciaJuridicaLineaSobregiro(locationData.transactionId),
    transfer: () => ('transfer'),
    bail: () => backendServices.ConsultarDatosFianzaSolidariaIlimitadaPJuri(locationData.transactionId),
    trust: () => ('trust'),
    agroPawn: () => ('agroPawn'),
    naturalBail: () => backendServices.ConsultarDatosFianzaSolidariaIlimitadaPNat(locationData.transactionId),
    legalBail: () => ('legalBail'),
    crossedLegalBail: () => backendServices.ConsultarDatosFianzaSolidariaLimitadaPJuri(locationData.transactionId),
    crossedNaturalBail: () => ('crossedNaturalBail'),
    promiseLetter: () => ('promiseLetter'),
    privateRecord: () => backendServices.ConsultarDatosContratosPrivados(locationData.transactionId),
    trustRecord: () => backendServices.ConsultarDatosActaFideicomiso(locationData.transactionId),
    loanGuarantees: () => ('loanGuarantees'),
    fiduciaryLine: () => ('fiduciaryLine'),
    prefinance: () => ('prefinance'),
  }

  function ValidateChecked() {
    return new Promise((resolve, reject) => {
      let validation = false;
      backendServices.consultarDocumentacionLegal(locationData.transactionId).then(data => {
        delete data.status;
        delete data.legalDocumentationId;
        delete data.transactId;

        let json = Object.entries(data ?? {})?.filter(([key, value]) => value)?.map(([key, value]) => key);
        for (const iterator of json) {
          console.log(jsonCond[iterator]());
        }

        validation = Object.entries(data ?? {})?.map(([key, value]) => value)?.some(Boolean) ?? false;
        seterror_dlg(!validation);
        resolve(validation);
      })
    })
  }

  /*
    // Camino Feliz ... 
    var values = {
      "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
      "activityId": OPTs.ACT_NONE.toString(),
      "transactionId": locationData.transactionId,
      "requestId":locationData.requestId,
      "decision":"no"
    };
      
    //Requiere Ajustes 
    var values = {
      "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
      "activityId": OPTs.ACT_NONE.toString(),
      "transactionId": locationData.transactionId,
      "requestId":locationData.requestId,
      "decision":"si"
    };
    */


  return (
    <React.Fragment>

      <div className="page-content">

        <Breadcrumbs title={props.t("CommercialCredit")} breadcrumbItem={props.t("GenerateDocumentation")} />

        <Card>
          <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>


          <CardHeader>
              <Row>
                <Col md={6}>
                  <h4 className="card-title">{t("GenerateDocumentation")}</h4>
                  <p className="card-title-desc">{t("This form is official and must be filled out correctly")}</p>
                </Col>
                <Col md={6}>
                  <Row>
                    <Col sm={12} style={{ textAlign: "right" }}>
                      <h5 className="card-title">{mainDebtor != null ? (mainDebtor.typePerson === "1" ? (mainDebtor.name + " " + mainDebtor.name2 + " " + mainDebtor.lastName + " " + mainDebtor.lastName2) : (mainDebtor.name)) : ""} </h5>
                    </Col>
                    <Col sm={12} style={{ textAlign: "right" }}>
                      <h5 className="card-title" style={{ textAlign: "right" }}>{t("Tramit")}:{" "}#{locationData?.transactionId}</h5>
                    </Col>
                  </Row>
                </Col>
              </Row>                     
            </CardHeader>
        

            <CardBody>
              <Row>
                <Col md="6">
                  <h4 className="card-title">{props.t("LegalDocumentation")}</h4>
                </Col>
                <Col md="6" style={{ textAlign: "right" }}>
                  <Button id="btnSave" color="success" type="button" onClick={() => { ValidateChecked() }} style={{ margin: '5px' }}>
                    {props.t("generate")}
                  </Button>
                </Col>
              </Row>

              {/* <DatosGenerales /> */}

              {<ResultadosBusqueda results={dataList} JSONCompare={Object.entries(jsonCond)?.map(([key, value]) => key)} />}

            </CardBody>
          </LoadingOverlay>
          <CardFooter style={{ textAlign: "right" }}>
            <Button color="dark" type="button" style={{ margin: '5px' }} onClick={() => { onSaveProcess(2); }}><i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i> {props.t("Exit")}</Button>
            <Button color="success" type="button" style={{ margin: '5px' }} onClick={() => { validarCheck() }}><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i> {props.t("Continue")}</Button>
            
          </CardFooter>

          <CardFooter>
            <Row>
              <Col md={3} style={{ textAlign: "left", marginTop: "10px" }}>
                <Button color="danger" type="button" style={{ margin: '5px' }} onClick={() => { showModalEndProcess() }}><i className="mdi mdi-cancel mid-12px"></i> {t("Cancel")}</Button>
                
              </Col>
              <Col md={9} style={{ textAlign: "right", marginTop: "10px" }}>
                <Button color="primary" type="button" style={{ margin: '5px' }} onClick={() => { saveDataAcceptclient(); onSaveProcess(OPTs.PROCESS_ACEPTACIONCLIENTE) }}><i className="mdi mdi-content-save-outline mid-12px"></i> {t("Exit")}</Button>
                <Button color="success" type="button" style={{ margin: '5px' }}
                  onClick={() => { checkToContinue() }}><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i> {t("Continue")}</Button>
              </Col>
            </Row>
          </CardFooter>

        </Card>

        {locationData?
           <LogProcess logProcessModel={new LogProcessModel(locationData.instanceId,locationData.transactionId,0,"","",OPTs.PROCESS_ACEPTACIONCLIENTE, OPTs.ACT_NONE)}/>
          :null}


      <ModalEndProcess onSaveEndProcess={onSaveEndProcess} isOpen={displayModalEndProcess} toggle={() => { showModalEndProcess(false) }} />
      
      <ModalPrevisualizarPropCred isOpen={ShowModelCreditProposal} toggle={() => { toggleShowModelCreditProposal() }}/>
      <ModalPrevicualizarAIF isOpen={ShowModelFinancialReport} toggle={() => { toggleShowModelFinancialReport() }} />
      <ModalTermsOfConditions isOpen={ShowModelTermsOfConditions} toggle={() => { toggleShowModelTermsOfConditions() }} />
      <ModalPrevicualizarIGR isOpen={ModalPrevisualizardata} toggle={() => { toogleModalPrevisualizarIGR() }} />
     
      {
        messageDlg && messageDlg.show ? (
          <SweetAlert
            type={messageDlg.error ? "error" : "success"}
            title={messageDlg.error ? t("Error") : t("Message")}
            onConfirm={() => { showMessage(); }}>
            {messageDlg.msg}
          </SweetAlert>
        ) : null
      }

      </div>

    </React.Fragment>
  )
}

PantallaBusqueda.propTypes = {
  // onSelectIdPropuesta: PropTypes.func.isRequired,
  //selectedIdPropuesta:PropTypes.any
  selectedId: PropTypes.any,
