import PropTypes from 'prop-types';
import React, { useEffect, useState } from "react"
import { withTranslation } from "react-i18next"
import { Link } from "react-router-dom"

import {
  Table,
  Card,
  CardBody,
  Pagination,
  PaginationItem,
  PaginationLink,
  CardFooter,
  Button,
  Row,
  Col,
} from "reactstrap"
import { useLocation, useHistory } from 'react-router-dom'
import { BackendServices, CoreServices, BpmServices, } from "../../../../services";

const ResultadosBusqueda = (props) => {

  const location = useLocation()
  const [dataLocation, setData] = useState(location.data);
  const [JSONCompare, setJSONCompare] = useState(null);

  const [DataServer, setDataServer] = useState({
    rotary: true,
    commercialLoan: true,
    multipleRotary: false,
    rotaryNumber: false,
    overdraft: false,
    transfer: false,
    bail: false,
    trust: false,
    agroPawn: false,
    naturalBail: false,
    legalBail: false,
    crossedLegalBail: false,
    crossedNaturalBail: false,
    promiseLetter: false,
    privateRecord: false,
    trustRecord: false,
    loanGuarantees: false,
    fiduciaryLine: false,
    prefinanced: false,
  });



  const apiServiceBackend = new BackendServices();

  React.useEffect(() => {
    console.log(props);
    LoadDocLegal();
    setJSONCompare(props.JSONCompare);
  }, []);

  function LoadDocLegal() {

    apiServiceBackend.consultarDocumentacionLegal(dataLocation.transactionId).then(data => {
      data && setDataServer(data);
    })
  }

  async function SenData(data) {

    let Datos = {
      transactId: dataLocation.transactionId,
      ...DataServer
    };

    Datos[data.name] = data.checked;

    !DataServer && apiServiceBackend.nuevoDocumentacionLegal(Datos);

    DataServer && apiServiceBackend.actualizarDocumentacionLegal(Datos);

    setDataServer(Datos);

  }

  const dataRows = props.results.map((data, index) => (
    <tr key={data.id}>
      <th scope="row"><input type="checkbox" checked={DataServer[JSONCompare[index]]} name={JSONCompare[index]} onChange={(e) => { SenData(e.target) }} /></th>
      <td>{data.comment}</td>
    </tr>)
  );

  return (

    <React.Fragment>
      {/* <Row>
        <Col lg="12" style={{ textAlign: "right" }}>
          <Button id="btnSave" color="success" type="submit" style={{ margin: '5px' }}>
            {props.t("generate")}
          </Button>
        </Col>
      </Row> */}
      <Row>
        <Col lg="12">
          <div className="table-responsive">
            <Table className="table mb-0">
              <thead className="table-light">
                {/* <tr>

                  <th colSpan={2}> <h4 className="card-title">{props.t("LegalDocumentation")}</h4>
                  </th>


                </tr> */}
              </thead>
              <tbody>
                {dataRows}
              </tbody>
            </Table>

          </div>
        </Col>
        {/* <Col lg="12">
          <Pagination aria-label="Page navigation example"
            listClassName="justify-content-end">
            <PaginationItem disabled>
              <PaginationLink>
                <i className="mdi mdi-chevron-left" />
              </PaginationLink>
            </PaginationItem>
            <PaginationItem>
              <PaginationLink href="#">1</PaginationLink>
            </PaginationItem>
            <PaginationItem active>
              <PaginationLink>
                2<span className="sr-only">(current)</span>
              </PaginationLink>
            </PaginationItem>
            <PaginationItem>
              <PaginationLink href="#">3</PaginationLink>
            </PaginationItem>
            <PaginationItem>
              <PaginationLink href="#">
                <i className="mdi mdi-chevron-right" />
              </PaginationLink>
            </PaginationItem>
          </Pagination>
        </Col> */}
      </Row>
    </React.Fragment>

  );
}

ResultadosBusqueda.propTypes = {
  results: PropTypes.array.isRequired, //Resultado de la busqueda
}

export default (withTranslation()(ResultadosBusqueda));
