import { useState } from 'react';
import PropTypes from 'prop-types';
import { translationHelpers } from '../../../helpers/translation-helper';

import {
  Table,
  Card,
  CardBody,
  Button
} from "reactstrap"

import ModalNewHistoryDetail from './ModalNewHistoryDetail';


const History = (props) => {

  const [displayModalNewHistoryDetail, setDisplayModalNewHistoryDetail] = useState(false);

  const [t, c] = translationHelpers('commercial_credit', 'common');

  const { headers, data, editMode } = props;

  if (!data) {
    return null;
  }

  function calculateTotals(headers, data) {
    const totals = new Array(headers.length);
    totals.fill(0, 0, headers.length);
    headers.forEach((header, index) => {
      totals[index] = data.reduce((total, current) => {
        return total + current.data[index];
      }, 0);
    });
    return totals;
  }

  function calculateProportions(headers, data) {
    const cols = new Array(headers.length);
    cols.fill(0, 0, headers.length);
    return cols;
  }

  const dataHeaders = headers.map((header, index) => (
    <th key={"header-" + index} className="text-end">{header}</th>
  ));

  const dataRows = data.map((item, index) => (
    <tr key={'row-' + index}>
      <td key={index}>{item.name}</td>
      {item.data.map((data, col) => (
        <td key={"col-" + col} className="text-end">{data}</td>
      ))}
    </tr>)
  );

  // Calculate total
  const totals = calculateTotals(headers, data);
  const totalRow = (
    <tr className="table-info">
      <th>{t("Total").toLocaleUpperCase()}</th>
      {totals.map((val, index) => (
        <th key={index} className="text-end">{val}</th>
      ))}
    </tr>
  );

  // Calculate proportions
  const proportions = calculateProportions(headers, data);
  const proportionRow = (
    <tr>
      <td>{t("Proportion").toLocaleUpperCase()}</td>
      {proportions.map((val, index) => (
        <td key={index} className="text-end">{val}</td>
      ))}
    </tr>
  );

  function toggleModalNewHistoryItem() {
    setDisplayModalNewHistoryDetail(!displayModalNewHistoryDetail);
  }

  return (
    <>
      <h4 className="card-title clearfix">
        {t("History Details")}
        {editMode &&
          <Button id="btnNew" color="success" type="button" className="float-end" style={{ margin: '5px' }} onClick={toggleModalNewHistoryItem}>
            {c("New")}
          </Button>
        }
      </h4>
      <p className="card-title-desc"></p>
      <div className="table-responsive mt-2">
        <Table className="table mb-0">
          <thead className="table-light">
            <tr>
              <th>{t("Country / Client")}</th>
              {dataHeaders}
            </tr>
          </thead>
          <tbody>
            {dataRows}
            {totalRow}
            {proportionRow}
          </tbody>
        </Table>
      </div>
      <ModalNewHistoryDetail isOpen={displayModalNewHistoryDetail} toggle={toggleModalNewHistoryItem} onSave={props.addHistoryDetail} />
    </>
  );
};

History.propTypes = {
  title: PropTypes.string,
  headers: PropTypes.array.isRequired,
  data: PropTypes.array.isRequired,
  editMode: PropTypes.bool,
  addHistoryDetail: PropTypes.func
};

History.defaultProps = {
  editMode: false
};


export default History;
