import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { useTranslation } from 'react-i18next'
import { useLocation, useHistory } from 'react-router-dom'

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Table,
  Pagination,
  PaginationItem,
  PaginationLink,
  CardFooter,
  Modal,
} from "reactstrap"

import { AvForm, AvField } from "availity-reactstrap-validation"
import { Link } from "react-router-dom"
import DocumentModel from "../../models/Core/DocumentModel";
import DocumentoAnexoModel from "../../models/Backend/DocumentoAnexoModel";
import functionshelper from "../../helpers/functions_helper"
import LoadingOverlay from 'react-loading-overlay';

import { CoreServices, BackendServices } from '../../services';

const AttachmentFileCore = (props) => {

  const { t, i18n } = useTranslation();
   
  const { locationData } = props;
  
  const [showModelAttachment, setShowModelAttachment] = useState(false);
  const [attachments, setAttachments] = useState([]);
  const [isActiveLoading, setIsActiveLoading] = useState(false);
      
  //On Mounting (componentDidMount) if selectedData has changes
  React.useEffect(() => {
    console.log("attachments",attachments);
    console.log("AttachmentFileCore");
    if(locationData!==undefined && locationData!==null){      
      updateAttachments();
    }
  }, [locationData]);


      //abrimos modal para adjunar archivos
      function toggleShowModelAttachment() {
        setShowModelAttachment(!showModelAttachment);
        removeBodyCss()
      }
    
      function removeBodyCss() {
        document.body.classList.add("no_padding")
      }

      //GUARDAR ADJUNTO
      function addAttachment(filedata) {
        
        setIsActiveLoading(true);

        try{
//GUARDAR EN GESTOR DOCUMENTAL ONBASE
var data = DocumentModel.getRequestModel(filedata);//filedata.name, filedata.description, filedata.fileName, filedata.fileExtension, filedata.fileBase64
const apicore = new CoreServices()
apicore.postDocument(data).then((resultCore) => {          

  filedata['transactId'] = locationData.instanceId;
  filedata['processId'] = locationData.processId;
  filedata['activityId'] = locationData.activityId;
  filedata['documentId'] = resultCore.documentId;
  filedata['docName'] = resultCore.docName;
  
  ////GUARDAR EN BD 
  data = DocumentoAnexoModel.getSaveModel(filedata);
  const apibackend = new BackendServices();
  apibackend.guardarDocumentoAnexo(data).then((resultBackend) => {

    if(resultBackend){
      // SI TODO OK ACTUALIZAR LA LISTA DE ADJUNTOS
      updateAttachments();
    }               
  })
  .catch((error) => { console.error('api error', error);})
  .finally (() => { setIsActiveLoading(false); });
})
.catch((error) => {console.error('api error', error);})
.finally (() => {setIsActiveLoading(false);  });

        }
        catch(error) {setIsActiveLoading(false);}
      }

      //BORRAR ADJUNTO
      function removeAttachment(filedata) {        

        //BORRAR EN GESTOR DOCUMENTAL ONBASE
        //var data = DocumentModel.getDeleteModel(filedata);
        //const apicore = new CoreServices()
        //apicore.postDocument(data).then((resultCore) => {
                              
          ////BORRAR EN BD 
          var data = {transactId:filedata.transactId,onBaseIdentification:filedata.onBaseIdentification};
          const apibackend = new BackendServices();
          apibackend.eliminarDocumentoAnexo(data).then((resultBackend) => {

            if(resultBackend){
              // SI TODO OK ACTUALIZAR LA LISTA DE ADJUNTOS
              updateAttachments();
            }           

          })
          .catch((error) => { console.error('api error', error);})
          .finally (() => {  });
        //})
        //.catch((error) => {console.error('api error', error);})
        //.finally (() => {  });        
      }
    
      //VER ADJUNTO
      function viewAttachment(docId) {        
        
        const apicore = new CoreServices()
        apicore.postViewDocument(docId).then((resultCore) => {          
            if(resultCore!==null) {
              window.open(resultCore, "_blank")
            }
        }).catch((error) => {console.error('api error', error);})
          .finally (() => {setIsActiveLoading(false);  });
      }

      //ACTUALIZAR LISTA DE ADJUNTOS
      function updateAttachments() {  
        
        var params = {transactId:locationData.instanceId,processId:locationData.processId,activityId:locationData.activityId}

        const apibackend = new BackendServices();
        apibackend.consultarDocumentosAnexos(params).then((data) => {          
          setAttachments(data);          
        })
        .catch((error) => { console.error('api error', error);})
        .finally (() => {  });
      }
         
      //On change Inputs
      let fileData = {};
      // Submitimos formulario para busqueda de clientes
      function handleSubmitfrmAttachment(event, errors, values) {
        event.preventDefault();
        if (errors.length > 0) {
          return;
        }
    
        addAttachment(fileData);
        toggleShowModelAttachment();
      }    
      //On change Inputs
      async function handleChangeInputfrm(e) {
        fileData[e.target.name] = e.target.value;  
        switch(e.target.name){
          case "attachment":{
            const file = e.target.files[0]
            fileData['fileName'] = file.name;
            fileData['fileExtension']="." + file.name.split('.').pop();        
            var base64 = await functionshelper.convertBase64(file);
            //console.log("base64",base64.split(','))
            fileData['fileBase64'] = base64.split(',')[1];
            break;
          }
        }   
      }


  return (

    <React.Fragment>
      <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>
    <Row>
            <Col md="12">
            <Table className="table mt-1" responsive>
            <thead className="table-light">
            <tr>
                <th colSpan={3}>
                  <h6 className="float-start">{t("Attach File")}</h6>
                  <Link to="#" className="float-end" onClick={() => { toggleShowModelAttachment() }} title={t("Add")}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i>{" "}</Link>    
                </th>
              </tr>
            </thead>
            <tbody>              
              
          { attachments!==null && attachments!==undefined && attachments.length > 0 ? 
            (attachments.map((data) => (
                <tr key={ data.documentIdentification }>        
                  <td>{ data.fileName }</td>
                  <td>{ data.fileDescription }</td>
                  <td>
                    <Link to="#" className="float-end mx-1" title={t("Delete")} onClick={()=> removeAttachment(data)}><i className="mdi mdi-file-remove-outline mdi-24px"></i></Link>
                    <Link to="#" className="float-end mx-1" title={t("View")} onClick={()=> viewAttachment(data.documentIdentification)}><i className="mdi mdi-file-search-outline mdi-24px"></i></Link>
                </td>
              </tr>
              ))) : (
            <tr>
              <td colSpan="4">
                <div className="alert alert-info">{ t("No attachments yet") }</div>
              </td>
            </tr>
          )}
              
            </tbody>
          </Table>

              
            </Col>
          </Row>     
          
          <Modal size="xl" isOpen={ showModelAttachment } toggle={ toggleShowModelAttachment } centered={true}>
      <div className="modal-header">
        <h5 className="modal-title mt-0">{t("Attach File")}</h5>
        <button
          type="button"
          onClick={ toggleShowModelAttachment }
          className="close"
          data-dismiss="modal"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>

      <AvForm id="frmAttachment" className="needs-validation" onSubmit={handleSubmitfrmAttachment}>
      <div className="modal-body">
      <Row>
            <Col md="6">
              <div className="mb-3">
                <Label htmlFor="name">{t("Name")}</Label>
                <AvField
                  className="form-control"
                  type="text"
                  name="name" onChange={handleChangeInputfrm}
                  errorMessage={t("Required Field")}
                  validate={{ required: { value: true } }}
                />
              </div>              
            </Col>  
            <Col md="6">
              <div className="mb-3">
                <Label htmlFor="attachment">{t("File")}</Label>
                <AvField
                  className="form-control"
                  type="file"
                  name="attachment" onChange={handleChangeInputfrm}
                  errorMessage={t("Required Field")}
                  validate={{ required: { value: true } }}
                  accept=".jpg,.jpeg,.png,.doc,.docx,.xls,.xlsx,.pdf"
                />

              </div>              
            </Col>  
          </Row>
          <Row>
            <Col md="12">
              <div className="mb-3">
                <Label htmlFor="description">{t("Description")}</Label>
                <AvField
                  className="form-control"
                  type="textarea"
                  name="description"
                  maxLength="1000"
                  rows="7"
                  onChange={handleChangeInputfrm}
                />
              </div>
            </Col>
          </Row>
      </div>
      <div className="modal-footer">
      <Button color="dark" type="button" style={{ margin: '5px' }} onClick={ toggleShowModelAttachment } data-dismiss="modal">
              <i className="mdi mdi mdi-cancel mid-12px"></i> {t("Cancel")}
              </Button> 
     
              <Button color="success" type="submit" style={{ margin: '5px' }}>
              <i className="mdi mdi-content-save mid-12px"></i> {t("Save")}
              </Button>      
      </div>
        </AvForm>

    </Modal>
    </LoadingOverlay>
      </React.Fragment>

  )
}

AttachmentFileCore.propTypes = {
  locationData:PropTypes.any,
}

//export default (withTranslation()(DatosGenerales));
export default AttachmentFileCore;
