import PropTypes from 'prop-types';
import { formatCurrency, translationHelpers } from '../../../../helpers';
import {
  Table,
  Card,
  CardBody,
  Col,
} from "reactstrap"
import React, { useEffect, useState } from "react"
import { BackendServices, CoreServices, BpmServices, } from "../../../../services";
import { useTranslation } from 'react-i18next';
import { useLocation, useHistory } from 'react-router-dom';
import * as url from "../../../../helpers/url_helper"

const ExposicionCorporativaClientes = (props) => {
  const location = useLocation();
  const history = useHistory();
  const [locationData, setLocationData] = useState(null);
  const coreServices = new CoreServices();
  const { t, i18n } = useTranslation();
  const [dataRows, setdataRows] = React.useState(null);
  const [CorporateExposure, setCorporateExposure] = useState(null);
  const [customerNumberT24, setcustomerNumberT24] = React.useState(props.customerNumberT24);
  useEffect(() => {

    let dataSession;
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        dataSession = location.data;
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        dataSession = result;
      }
    }


    getCoporateExhibitionClient(dataSession);
  }, []);
  function getCoporateExhibitionClient(dataSession) {
    // getCorporateExhibitionByClients
    const backendServices = new BackendServices();
    backendServices.consultPrincipalDebtor(dataSession.transactionId)
      .then((data) => {
        if (data !== undefined) {
          coreServices.getCorporateExhibitionByClients(data.customerNumberT24).then(resp => {
            if (resp === undefined) {
              return;
            }
            setCorporateExposure(resp.length > 0 ? resp : null);
            setdataRows(resp.map((item, index) => (
              <>
                <tr key={'row-' + index}>
                  <th scope="row" width="30%">{item.clinteId}</th>
                  <th scope="row" className="text-end"></th>
                  <th scope="row" className="text-end"></th>
                  <th scope="row" className="text-end"></th>
                  <th scope="row" className="text-end"></th>
                </tr>
                {item.data.map((item2, index2) => (
                  <tr key={'subrow-' + index2}>
                    <td data-label={t("Type")} scope="row" width="30%"></td>
                    <td data-label={t("ApprovedRisk")} scope="row" className="text-start">{formatCurrency('$', item2.approved, 0)}</td>
                    <td data-label={t("ProposedRisk")} scope="row" className="text-start">{formatCurrency('$', item2.balance, 0)}</td>
                    <td data-label={t("Variation")} scope="row" className="text-start">{formatCurrency('$', item2.proposal, 0)}</td>
                    <td data-label={t("Variation")} scope="row" className="text-start">{formatCurrency('$', item2.variation, 0)}</td>
                  </tr>
                ))}
              </>
            )
            ));
          });
        }
      });
  }
  return (
    <React.Fragment>
      <Card>
        <CardBody>
          <h5 className="card-title">{props.title}</h5>
          <p className="card-title-desc">
          </p>
          <Col md="12" className="table-responsive styled-table-div">
            <Table className="table table-striped table-hover styled-table table" >
              <thead>
                <tr>
                  <th className="table-right">{t("Type")}</th>
                  <th className="table-right">{t("ApprovedRisk")}</th>
                  <th className="table-right">{t("alnce")}</th>
                  <th className="table-right">{t("ProposedRisk")}</th>
                  <th className="table-right">{t("Variation")}</th>
                </tr>
              </thead>
              <tbody>
                {dataRows}
                <tr>
                  <th scope="row" width="30%">{t("Total")}</th>
                  <th scope="row" className="table-right">{formatCurrency('$', CorporateExposure?.reduce((acu, crr) => {
                    let data1 = (acu.data?.reduce((acu2, crr2) => ((acu2.approvalRisk ?? acu2) + crr2.approvalRisk)) ?? acu);
                    let data2 = crr.data?.reduce((acu2, crr2) => ((acu2.approvalRisk ?? acu2) + crr2.approvalRisk));
                    return (data1 + data2)
                  }), 0)}</th>
                  <th scope="row" className="table-right">{formatCurrency('$', CorporateExposure?.reduce((acu, crr) => {
                    let data1 = (acu.data?.reduce((acu2, crr2) => ((acu2.variationRisk ?? acu2) + crr2.variationRisk)) ?? acu);
                    let data2 = crr.data?.reduce((acu2, crr2) => ((acu2.variationRisk ?? acu2) + crr2.variationRisk));
                    return (data1 + data2)
                  }), 0)}</th>
                  <th scope="row" className="table-right">{formatCurrency('$', CorporateExposure?.reduce((acu, crr) => {
                    let data1 = (acu.data?.reduce((acu2, crr2) => ((acu2.proposalRisk ?? acu2) + crr2.proposalRisk)) ?? acu);
                    let data2 = crr.data?.reduce((acu2, crr2) => ((acu2.proposalRisk ?? acu2) + crr2.proposalRisk));
                    return (data1 + data2)
                  }), 0)}</th>
                </tr>
              </tbody>
            </Table>
          </Col>
        </CardBody>
      </Card>
    </React.Fragment>
  );
};
ExposicionCorporativaClientes.propTypes = {
  title: PropTypes.string,
  customerNumberT24: PropTypes.string,
};
export default ExposicionCorporativaClientes;
