      ]).then(response => {
        const [{ value: Facilidades }, { value: DetalleOpinionRiesgoCredito }, { value: EnvironmentalRiskInfo }] = response;

        dataSet.covenant = EnvironmentalRiskInfo?.detail ?? "";
        dataSet.environmentRiskOpinion = EnvironmentalRiskInfo?.recommendations ?? "";

        dataSet.opinionRiskCredit = DetalleOpinionRiesgoCredito.length > 0 ? DetalleOpinionRiesgoCredito[0].opinion : "";

        dataSet.facilityIdVisible = Facilidades?.filter($$ => $$.debtor != "  ").length + 1;

        dataSet.facilityId = resp;

        setdataSet(dataSet)

        setIsActiveLoading(false);

      }).catch(err => {
        console.log(err);
        dataSet.facilityId = resp;
        setdataSet(dataSet)
        setIsActiveLoading(false);
      });



    });
