  function cargarFacilidades(dataSession) {
    api.retrieveProposalType()
      .then((propuesta) => {
        api.retrieveFacilityType()
          .then((facilidad) => {
            api.consultarFacilidades(props.dataGlobal.requestId).then(resp => {
              if (resp.length > 0) {
                initializeData(dataSession);
                let acu = 1;
                setdataRows(resp.map((data, index) => (
                  data.debtor != "  " ?
                    <tr key={acu++}>
                      <td>{acu}</td>
                      <td>{data.debtor}</td>
                      <td>{facilidad.find(x => x.id === data.facilityType)?.description}</td>
                      <td>{propuesta.find(x => x.id === data.proposedType)?.description}</td>
                      <td>{data.amount}</td>
                      <td>{data.proposedRate}</td>
                      {/* <td>{data.observations}%</td> */}
                      <td style={{ textAlign: "right" }}>
                        <Button type="button" color="link" onClick={(resp) => {
                          data.facilityIdVisible = acu - 1;
                          setdataSet(data); cargarDataSelect(data); settipo("editar"); setViewSeccion(true);
                        }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                      </td>
                    </tr> : null)
                ));
              } else {
                setdataRows(
