  function cargarDataSelect(data) {
    var defaultVal = null;
    setapplyEscrow(data?.applyEscrow);
    try {
      if (tipoPropuesta.length > 0 && data.proposedType !== null) {
        defaultVal = tipoPropuesta.find(x => x.value === data.proposedType);
        if (defaultVal !== undefined) {
          settipoPropuestaSelect(defaultVal);
        }
      }
      if (subPropuesta.length > 0 && data.subproposalType !== null) {
        defaultVal = subPropuesta.find(x => x.value === data.subproposalType);
        if (defaultVal !== undefined) {
          setsubPropuestaSelect(defaultVal);
        }
      }
      if (tipoFacilidad.length > 0 && data.facilityType !== null) {
        defaultVal = tipoFacilidad.find(x => x.value === data.facilityType);
