                    </Accordion.Body>
                  </Accordion.Item>
                </CardBody>
              </Card>
              <Card>
                <CardBody style={{ padding: "0px" }}>
                  <Accordion.Item eventKey="1">
                    <Accordion.Header>{t("Cases")}</Accordion.Header>
                    <Accordion.Body>
                      {/*
                      <Row>
                        <Col lg="12">
                          <Link
                            className="btn btn-success float-end"
                            color="success"
                            style={{ margin: "5px" }}
                            to="#" onClick={()=> NewInstance()}
                          >
                            <i className="mdi mdi-plus-thick mid-12px"></i>
                            {t("New")}
                          </Link>
                        </Col>
                      </Row>
                      */}
                      <LoadingOverlay active={isActiveLoading} spinner text={t("Processinginformation")}>
                        {dataHeader.length > 0 ?
                          <ToolkitProvider
                            keyField="id"
                            data={dataBody}
                            columns={dataHeader}
                            search
                          >
                            {
                              props => (
                                <div>

                                  <SearchBar className="custome-search-field float-end" delay={1000} placeholder={t("Search")} {...props.searchProps} />
                                  <BootstrapTable
                                    bootstrap4
                                    bordered={false}
                                    striped
                                    hover
                                    condensed
                                    style={{ cursor: "pointer" }}
                                    data={dataBody} columns={dataHeader}
                                    {...props.baseProps}
                                    pagination={paginationFactory({
                                      sizePerPage: 30,
                                      sizePerPageList: [30]
                                    })}
                                  />
                                </div>
                              )
                            }
                          </ToolkitProvider>
                          : null}
                      </LoadingOverlay>
                      {showResults && <CreditProposalPage />}
                    </Accordion.Body>
                  </Accordion.Item>
                </CardBody>
              </Card>

            </Accordion>
          </CardBody>
        </Card>
      </div>
      {processInstanceId && (<ModalWatchProces isOpen={ShowDisplayModal} toggle={() => { toggleModalWatchProcess() }} svg={svg} processInstanceId={processInstanceId} t={tr} />)}
    </React.Fragment>
  );
}

export default Dashboard
